INSERT INTO `app_travelinfo` VALUES ('691', '西岛', '4A景区', '10', '7076', '三亚', '49', '三亚天涯区肖旗港西岛海洋文化旅游区', '海上休闲娱乐运动胜地', 'https://piao.qunar.com/ticket/detail_3633440059.html', '5', '80', '55136', '西岛位于三亚湾中心位置，距离市区8海里，与东岛恰似在碧波中鼓浪而行的两只玳瑁，“波浮双玳”自古便是三亚的一道胜景。西岛以其秀美的山体，迷人的珊瑚礁，清澈的海水和松软的海滩，成为三亚旅游观光、休闲度假的胜地之一。西岛海上游乐世界是一个汇聚了汇聚海上运动、潜水观光、天然浴场、沙滩娱乐、椰林休闲的动静皆宜的立体海岛景区；牛王岭游览区是一座与西岛几近相连的袖珍小岛，可以欣赏到壮阔的海天一色、鬼斧神工的礁石景观、雄浑的日出日落以及星罗棋布的军事遗迹。', '[\"https://imgs.qunarzz.com/sight/p0/2106/e4/e48019d1735da73fa3.img.jpg_710x360_bd81b0d5.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/e6/8ef9bd749d97a627d22e6eca4ba76fc0.water.jpg_710x360_e2c83794.jpg\", \"https://imgs.qunarzz.com/sight/p0/1909/d4/d420d318b015ab4ba3.img.jpg_710x360_6b671927.jpg\", \"https://imgs.qunarzz.com/sight/p0/1909/45/4577fa60877eec33a3.img.jpg_710x360_c3e22c20.jpg\", \"https://imgs.qunarzz.com/sight/p0/1909/e7/e729dc13980c3e3ea3.img.jpg_710x360_6d39ccae.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/1f/8b641d98092fd0a40bd305c63353e9ee.water.jpg_710x360_b21431e2.jpg\"]', '[{\"author\": \"\\u7231*\\u4eba\", \"content\": \"\\u5c9b\\u4e0a\\u7535\\u52a8\\u8f6630\\u4e24\\u4e2a\\u5c0f\\u65f6\\uff0c\\u53ef\\u4ee5\\u7ed5\\u5c9b\\u597d\\u51e0\\u5708\\u4e86\\uff0c\\u5c9b\\u4e0d\\u5927\\uff0c\\u5f53\\u5730\\u98ce\\u60c5\\u7279\\u8272\\u5c0f\\u5403\\u5f88\\u597d\\u5403\\uff0c\\u8fd8\\u4e0d\\u8d35\\uff0c\\u751f\\u869d5\\u4e2a20\\uff0c\\u58a8\\u9c7c\\u997c10\\u57576\\u4e2a\\uff0c\\u867e\\u997c10\\u57574\\u4e2a\\uff0c\\u53ef\\u4ee5\\u62fc\\uff0c\\u73bb\\u7483\\u6d77\\uff0c\\u8d76\\u6d77\\u80dc\\u5730\\uff0c\\u8fd8\\u6ca1\\u843d\\u6f6e\\uff0c\\u8fb9\\u4e0a\\u7684\\u8783\\u87f9\\u90fd\\u904d\\u5730\\u722c\\uff0c\\u771f\\u540e\\u6094\\u6ca1\\u6709\\u4f4f\\u5c9b\\u4e0a\\u4e00\\u665a\\u4f53\\u9a8c\\u4e00\\u4e0b\\u8d76\\u6d77\\uff0c\\u4e0b\\u6b21\\u53bb\\u4e00\\u5b9a\\u4f4f2\\u665a\\u4e0a\\u8d762\\u665a\\u6d77\\uff0c\\u7136\\u540e\\u6e382\\u5929\", \"date\": \"2023-06-07\", \"score\": 5}, {\"author\": \"\\u5f64*\\u5f64\", \"content\": \"\\u661f\\