INSERT INTO `app_travelinfo` VALUES ('692', '亚龙湾热带天堂森林公园', '4A景区', '8.4', '5732', '三亚', '49', '三亚市亚龙湾区湾路665号亚龙湾国家旅游度假区', '电影《非诚勿扰2》取景地', 'https://piao.qunar.com/ticket/detail_2126521451.html', '5', '118', '127785', '亚龙湾热带天堂森林公园景区位于亚龙湾国家旅游度假区内，电影《非诚勿扰II》、电视剧《亲爱的，热爱的》在海南三亚的主要取景地，游客来三亚的必游地之一。园区定位于国际一流的滨海生态观光兼生态度假型森林公园，为第三代森林旅游产品的典范之作。园区内树木葱茏，能看那藤萝密布、溪水潺潺，可遇到那百年古藤、参天巨榕，能听闻虫唱鸟鸣，极尽野趣。景区内随处都可见《非诚勿扰II》、《亲爱的，热爱的》中的场景，舒淇、葛优、杨紫、李现走过素有情人桥之称的“过江龙索桥”，悬崖边的“峭壁天池”、见证浪漫爱情的《非诚勿扰II》里的试婚房、网红玻璃栈桥等，都是情侣们不可错过的打卡地。漫步天街，享受北纬18°天街慢生活，行走在全海景玻璃栈桥上将亚龙湾的美景尽收眼底，面朝大海，背靠青山，四季花开......', '[\"https://imgs.qunarzz.com/sight/p0/2109/ed/ed63269f7136c3c4a3.img.jpg_710x360_96df9528.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103r1200086yk2mb2E1A.jpg_710x360_b5f8684c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101e12000893nc3x2928.jpg_710x360_c455f6c7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106d120008fy9vqg7899.jpg_710x360_79f1420b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01073120008g4g75e227B.jpg_710x360_8eeb56e4.jpg\", \"https://imgs.qunarzz.com/sight/p0/2109/20/209b6928e1409747a3.img.jpg_710x360_214b16c1.jpg\"]', '[{\"author\": \"m*0\", \"content\": \"\\u4e70\\u4e86\\u5957\\u7968\\u8fdb\\u53bb  \\u5750\\u89c2\\u5149\\u8f66\\u4e0a\\u53bb \\u5c31\\u662f\\u5c71\\u8def28\\u5f2f \\u6655\\u8f66\\u7684\\u614e\\u5750\\uff0c\\u5f00\\u7684\\u5feb\\u4f46\\u662f\\u6280\\u672f\\u5f88\\u597d\\uff0c\\u6bcf\\u4e00\\u7ad9\\u90fd\\u53ef\\u4ee5\\u4e0b\\u8f66\\uff0c\\u7136\\u540e\\u6211\\u4eec\\u53bb\\u4e86\\u73bb\\u7483\\u6808\\u6865\\u4e0b\\u8f66 \\u4ed6\\u8981\\u8d70\\u4e00\\u6bb5\\u68ee\\u6797\\u697c\\u68af\\u4e0a\\u53bb\\u7684 \\u5982\\u679c\\u8f6e\\u6905\\u7684\\u5c31\\u4e0d\\u65b9\\u4fbf\\u4e86\\uff0c\\u7136\\u540e\\u53bb\\u770b\\u4e86\\u7535\\u5f71 \\u53bb\\u4e86\\u7d22\\u6865 \\u91cc\\u9762\\u4e5f\\u6709\\u9910\\u5385\\u5403\\u4e1c\\u897f \\u4e0d\\u7528\\u62c5\\u5fc3\\u6ca1\\u4e1c\\u897f\\u5403\\