INSERT INTO `app_travelinfo` VALUES ('693', '亚特兰蒂斯水世界', '未评价', '10', '5904', '三亚', '50', '三亚市海棠区海棠北路与风塘路交叉路口往东约200米', '挑战海神之跃滑道', 'https://piao.qunar.com/ticket/detail_3485255710.html', '5', '198', '33795', '亚特兰蒂斯是真正意义上以海洋为主题的休闲度假目的地。为宾客们提供各种各样的娱乐设施和服务，包括世界名厨主理的餐厅；缤纷多样的酒吧与酒廊；亚特兰蒂斯水世界丰富的水上游乐设施；淡水和海水海洋生物展池；露天海洋生物栖息地以及海豚互动和教育中心。在这里，来自全球的宾客还将体验到杰出的住宿服务、令人沉醉的水疗、宏伟的活动举办场地以及绵延无尽的海滩。三亚亚特兰蒂斯将各年龄层的宾客带入具创造力和吸引力的娱乐休闲水世界。', '[\"https://imgs.qunarzz.com/sight/p0/2307/c4/c468d80d935fdea3a3.water.jpg_710x360_7d056bef.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102h1200087lo8f891C8.jpg_710x360_95cc4375.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100v1200087ln7iq5E4A.jpg_710x360_ba490f86.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102d1200087lnn9vFB89.jpg_710x360_eed6757d.jpg\", \"https://imgs.qunarzz.com/sight/p0/2307/8f/8fb802537e7eb7b4a3.water.jpg_710x360_e1718050.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/f4/f4ecc654b2cc64d0a3.img.jpg_710x360_b9341ca5.jpg\"]', '[{\"author\": \"m*7\", \"content\": \"\\u7b2c\\u4e00\\u6b21\\u6765\\u4e09\\u4e9a\\u8708\\u652f\\u6d32\\u5c9b\\u73a9\\uff0c\\u5bfc\\u6e38\\u9f9a\\u9999\\u8bb2\\u89e3\\u7684\\u7279\\u522b\\u4ed4\\u7ec6\\uff0c\\u7279\\u522b\\u8f9b\\u82e6\\u4e5f\\u7279\\u522b\\u8d1f\\u8d23\\u4efb\\uff0c\\u8dd1\\u524d\\u8dd1\\u540e\\u7ed9\\u5927\\u5bb6\\u8282\\u7701\\u4e86\\u5f88\\u591a\\u65f6\\u95f4\\uff0c\\u6211\\u4eec\\u4e00\\u884c\\u4eba\\u73a9\\u5f97\\u5f88\\u5f00\\u5fc3\\uff0c\\u4f53\\u9a8c\\u611f\\u68d2\\u68d2\\u7684\\uff0c\\u4e0d\\u865a\\u6b64\\u884c \\u3002\", \"date\": \"2023-03-25\", \"score\": 5}, {\"author\": \"f*4\", \"content\": \"\\u62a5\\u56e2\\u8fd8\\u662f\\u633a\\u65b9\\u4fbf\\u7684\\uff0c\\u4e0a\\u5c9b\\u524d\\u5bfc\\u6e38\\u963f\\u71d5\\u4e5f\\u4ecb\\u7ecd\\u7684\\u5f88\\u8ba4\\u771f\\u8be6\\u7ec6\\uff0c\\u539f\\u6765\\u81ea\\u5df1\\u5b9a\\u7684\\u7535\\u74f6\\u8f66\\u6392\\u961f\\u65f6\\u95f4\\u957f\\uff0c\\u5e26\\u7740\\u5c0f\\u5b6