INSERT INTO `app_travelinfo` VALUES ('695', '分界洲岛', '5A景区', '4.2', '1712', '三亚', '50', '海南省陵水黎族自治县海南环岛东线高速牛岭出口处', '丰富的海底生态景观', 'https://piao.qunar.com/ticket/detail_888523688.html', '5', '222', '9013', '分界洲生态文化旅游度假区 位于海南省陵水县环岛高速牛岭出口处（景区码头），极具热带风情特色的海岛型旅游景区，区内天海一色、椰影婆娑，尤其是神奇的海底世界更是增添了无人居住小岛的原始神秘之感，岛上石峰林立，峭壁万仞，奇树簇拥。周围海水清澈，与沙滩相衔接，构成了画家所不及的天然画卷。岛上辟有“鬼斧神工”、“大洞天”、“刺桐花艳”等20多处景观，并有海豚潜水、珊瑚岸礁潜水、远海潜水、海底婚礼、堡礁潜水、半潜艇、海豚湾观光、海上摩托艇、海底漫步、海上拖伞、音乐吧等独具特色的海上娱乐项目，游客可以感受前所未有的新奇、刺激。岛上辟有“鬼斧神工”、“大洞天”、“刺桐花艳”等20多处景观，并有海豚潜水、珊瑚岸礁潜水、远海潜水、海底婚礼、堡礁潜水、半潜艇、海豚湾观光、海上摩托艇、海底漫步、海上拖伞、音乐吧等独具特色的海上娱乐项目，游客可以感受前所未有的新奇、刺激。', '[\"https://imgs.qunarzz.com/sight/p0/1907/46/46d1483e28be6c66a3.img.jpg_710x360_dd12ac98.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010231200087dw8k5F01F.jpg_710x360_f30aa62d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103t120008c2kpm99B82.jpg_710x360_beb1c10c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106d120008fatoh9E57F.jpg_710x360_17a05845.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103i120008fzifip7DF7.jpg_710x360_85da1b40.jpg\", \"https://imgs.qunarzz.com/sight/p0/1907/b0/b0fe45223069f4cba3.img.jpg_710x360_7ea928ca.jpg\"]', '[{\"author\": \"\\u98de*\\u83f2\", \"content\": \"\\u6ca1\\u7528\\u4e0a\\u8fd9\\u4e2a\\u8ba2\\u5355\\uff0c\\u7ea6\\u4e86\\u65e9\\u4e0a8\\u70b9\\uff0c7\\u70b959\\u6253\\u7535\\u8bdd\\u8bf4\\u8f66\\u8981\\u8d70\\u4e86\\u8ba9\\u6211\\u81ea\\u5df1\\u6253\\u8f66\\u8ffd\\u4ed6\\u4eec\\uff0c\\u6211\\u76f4\\u63a5\\u65e0\\u8bed\\u4e86\\uff0c\\u5c31\\u79bb\\u5730\\u70b9200\\u7c73\\u4e86\\u4e5f\\u4e0d\\u80fd\\u7b49\\u7b49\\u6211\\uff0c\\u5c31\\u6ca1\\u8ddf\\u56e2\\u4e86\\uff0c\\u8fd9\\u4e2a\\u7968\\u4e5f\\u4e0d\\u7ed9\\u9000\\u4e86\\uff0c\\u81ea\\u5df1\\u76f4\\u63a5\\u5728\\u666f\\u533a\\u53e6\\u4e70\\u7968\\u8fdb\\u53bb\\u6e38\\u89c8\\u7684\\uff0c\\u6211\\u771f\\u4e0d\\u559c\\u6b22\\u8ddf\\u56e2\\uff0c\\u4e5f\\u6ca1\\u5dee\\u51e0\\u5757\\u94b1\\uff0c\\u8fd8\\u6