INSERT INTO `app_travelinfo` VALUES ('696', '天涯海角', '4A景区', '10', '2265', '三亚', '48', '三亚市沿海滨西行26公里马岭山下天涯海角景区', '与三亚地标石刻合影', 'https://piao.qunar.com/ticket/detail_2036617750.html', '3', '180', '92416', '天涯海角风景区位于三亚市区约23公里的天涯镇下马岭山脚下，前海后山，风景独特。步入游览区，沙滩上那一对拔地而起的高10多米，长60多米的青灰色巨石赫然入目。两石分别刻有“天涯”和“海角”字样,意为天之边缘，海之尽头。“天涯海角”就是由此得名。奇石“天涯海角”和“南天一柱”各都流传着一个动人的故事。 这里融碧水、蓝天于一色，烟波浩瀚，帆影点点。椰林婆娑，奇石林立，如诗如画。那刻有“天涯”、“海角”、“南天一柱”、“海判南天”的巨石雄峙南海之滨，为海南一绝。', '[\"https://imgs.qunarzz.com/sight/p0/2211/97/97dea9f1c63f8cb3a3.water.jpg_710x360_98d28793.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100612000556xtguDE09.jpg_710x360_3f3325e1.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102t120008c2fgadF099.jpg_710x360_bf3a7472.jpg\", \"https://imgs.qunarzz.com/sight/p0/2211/3b/3b057e14a33d5662a3.water.jpg_710x360_b7f036e8.jpg\", \"https://imgs.qunarzz.com/sight/p0/2211/60/60b7b2766b7f0082a3.water.jpg_710x360_19d4110c.jpg\", \"https://imgs.qunarzz.com/sight/p0/2211/8c/8c7a333de5ce5aada3.water.jpg_710x360_e31c60ee.jpg\"]', '[{\"author\": \"\\u5317*\\u65b9\", \"content\": \"\\u7f51\\u4e0a\\u8ba2\\u7968\\u4e0d\\u7528\\u53d6\\u7968\\uff0c\\u8eab\\u4efd\\u8bc1\\u4e00\\u8d34\\u5c31\\u884c\\u3002\\u91cc\\u9762\\u7684\\u73af\\u5883\\u76f8\\u5f53\\u4e0d\\u9519\\uff0c\\u8fdb\\u53bb\\u4e4b\\u540e\\u6211\\u4eec\\u5148\\u5f80\\u53f3\\u62d0\\uff0c\\u6cbf\\u6c99\\u6ee9\\u4e00\\u76f4\\u8d70\\u5230\\u5929\\u6daf\\u6d77\\u89d2\\uff0c\\u8fb9\\u6b23\\u8d4f\\u6cbf\\u9014\\u666f\\u8272\\uff0c\\u8fb9\\u62cd\\u7167\\uff0c\\u8fb9\\u770b\\u6d77\\u6d6a\\u62cd\\u6253\\u5ca9\\u77f3\\uff0c\\u8fd9\\u91cc\\u7684\\u5ca9\\u77f3\\u90fd\\u5f88\\u5de8\\u5927\\uff0c\\u522b\\u5177\\u7279\\u8272\\u3002\\u8fd4\\u56de\\u7684\\u65f6\\u5019\\u8d70\\u5cb8\\u4e0a\\u8fb9\\u73a9\\u8fb9\\u5f80\\u56de\\u8d70\\uff0c\\u6700\\u540e\\u6e38\\u5a5a\\u7eb1\\u6444\\u5f71\\u7684\\u5730\\u65b9\\uff0c\\u597d\\u591a\\u65b0\\u4eba\\u6b63\\u5728\\u62cd\\u6444\\u5a5a\\u7eb1\\u7167\\u3002\\u73af\\u5883\\u5f88\\u