INSERT INTO `app_travelinfo` VALUES ('707', '广州塔', '4A景区', '9.8', '10334', '广州', '48', '广州市海珠区阅江西路222号', '打卡地标璀璨夜景', 'https://piao.qunar.com/ticket/detail_3941562390.html', '2', '148', '52054', '广州塔位于广州市中心，城市新中轴线与珠江景观轴交汇处， 与海心沙岛和广州市21世纪CBD区珠江新城隔江相望，世界第四高塔。2010年9月28日，广州市城投集团举行新闻发布会，正式公布广州新电视塔的名字为广州塔，整体高600米，而“小蛮腰”66层。从10月1日起，广州塔正式公开售票接待游客。其造型、空间和结构由两个向上旋转的椭圆形钢外壳变化生成，一个在基础平面，一个在假想的450米高的平面上，两个椭圆彼此扭转135度，两个椭圆扭转在腰部收缩变细。格子式结构底部比较疏松，向上到腰部则比较密集，腰部收紧固定了，像编织的绳索，呈现“纤纤细腰”，再向上格子式结构放开，由逐渐变细的管状结构柱支撑。', '[\"https://imgs.qunarzz.com/sight/p0/201309/06/a03726324b885c8bc8d65eac.jpg_710x360_7c9d5bb1.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100c1200087c7m5aF0D4.jpg_710x360_e25b8f19.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010641200087c7mj6414C.jpg_710x360_88d936df.jpg\", \"https://imgs.qunarzz.com/sight/p0/1911/1a/1a0416d47d2834b3a3.img.jpg_710x360_21a9a30a.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/11/a98fb72963b600b293835fbb.png_710x360_748b2126.png\", \"https://imgs.qunarzz.com/sight/p0/201405/22/c374fc136ab78f43a3deede34b92ea90.jpg_710x360_66ba4033.jpg\"]', '[{\"author\": \"j*8\", \"content\": \"\\u5c31\\u662f\\u6691\\u671f\\u4eba\\u6709\\u70b9\\u591a\\uff0c\\u8df3\\u697c\\u673a\\u5c31\\u5341\\u6765\\u79d2\\uff0c\\u5728\\u6700\\u9ad8\\u7684\\u5730\\u65b9\\u6050\\u9ad8\\u5c31\\u522b\\u53bb\\u4e86\\uff0c\\u8fd9\\u4e2a\\u7968\\u5230488\\u7c73\\u5854\\u9876\\u4e86\\uff0c\\u5305\\u542b\\u8df3\\u697c\\u673a\\u548c\\u6469\\u5929\\u8f6e\\uff0c\\u6469\\u5929\\u8f6e\\u6392\\u961f\\u4eba\\u592a\\u591a\\u6ca1\\u5750\\u6709\\u70b9\\u6d6a\\u8d39\\u4e86\", \"date\": \"2023-07-11\", \"score\": 5}, {\"author\": \"l*l\", \"content\": \"\\u666f\\u8272\\u4e0d\\u9519\\uff0c\\u6709\\u70b9\\u6050\\u9ad8\\u7684\\u6211\\uff0c\\u5c31\\u662f\\u53bb\\u666f\\u533a\\u4e0d\\u80fd\\u6709\\u73bb\\u7483\\u6808\\u9053\\u90a3\\u79cd\\u7a0b\\u5ea6\\uff0c\\u4e1c\\u65b9\\u660e\\u73e0\\u90a3\\u6bb5\\u73bb\\u7483\\u89c2\\u666f\\u53f0\\u4e0d\\u6562\\u976