INSERT INTO `app_travelinfo` VALUES ('709', '广州长隆旅游度假区', '5A景区', '10', '4104', '广州', '46', '广东省广州市番禺区汉溪大道东299号', '家庭出游畅玩多个主题园区', 'https://piao.qunar.com/ticket/detail_371131297.html', '5', '100', '74443', '广州市长隆旅游度假区是国家5A级景区。长隆旅游度假区综合了主题公园、文化演艺、科普教育、餐饮酒店、休闲度假等完善的现代服务配套设施，旅游产品结构丰富、主题特色鲜明，加上交通条件便利、游览范围集中、游客接待量大，拉动了周边相关经济发展，取得了较好的社会效益和经济效益，成为广东省旅游文化的名片，品牌效应影响全国、辐射东南亚。长隆旅游度假区包括长隆欢乐世界、长隆水上乐园、长隆国际大马戏、香江野生动物世界、长隆酒店、广州鳄鱼公园、长隆高尔夫练习中心、香江大酒店、香江海鲜酒家等9家子公司，毗邻而建，又各占鳌头。长隆旅游度假区被评为全国旅游先进单位和文化部的文化产业示范基地。', '[\"https://imgs.qunarzz.com/sight/p0/2302/8b/8bd0335627fcaaada3.water.jpg_710x360_5f3a0025.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0722cb2773cfc437.jpg_710x360_f2120fea.jpg\", \"https://imgs.qunarzz.com/sight/p0/1507/e2/d9ed877c500d6899ff72d9b075d4e936.water.jpg_710x360_69246d48.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010501200087ut12x1B1F.jpg_710x360_b6e9d74a.jpg\"]', '[{\"author\": \"c*2\", \"content\": \"\\u6bd4\\u9884\\u671f\\u7684\\u5dee\\uff0c\\u53ef\\u80fd\\u662f\\u671f\\u5f85\\u592a\\u9ad8\\u4e86\\uff0c\\u9152\\u5e97\\u623f\\u95f4\\u73af\\u5883\\u582a\\u5fe7\\u5730\\u6bef\\u4e0a\\u5899\\u4e0a\\u90fd\\u662f\\u810f\\u7684\\u5f88\\u660e\\u663e\\uff0c\\u5395\\u6240\\u5730\\u9762\\u90fd\\u662f\\u6c61\\u6e0d\\u5370\\uff0c\\u8bbe\\u65bd\\u4e00\\u822c\\uff0c\\u65e9\\u9910\\u79cd\\u7c7b\\u5f88\\u591a\\uff0c\\u9664\\u4e86\\u9762\\u5305\\u7cd5\\u70b9\\u4e4b\\u5916\\uff0c\\u5176\\u4f59\\u7684\\u5f88\\u96be\\u5403\\uff0c\\u76f8\\u5bf9\\u6765\\u8bf4\\u4ef7\\u683c\\u504f\\u8d35\\uff0c\\u6bd4\\u8f83\\u5bf9\\u4e0d\\u8d77\\u90a3\\u4e2a\\u4ef7\\u683c\", \"date\": \"2023-02-24\", \"score\": 3}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u9152\\u5e97\\u670d\\u52a1\\u5f88\\u597d\\uff0c\\u9152\\u5e97\\u8bbe\\u65bd\\u9f50\\u5168\\uff0c\\u6709\\u5065\\u8eab\\u623f\\u6709\\u513f\\u7ae5\\u4e50\\u56ed\\uff0c\\u8fd8\\u6709\\u9910\\u5385\\u548c\\u5546\\u5e97\\uff0c\\u623f\\u95f4\\u5f88\\u5927\\uff0c\\u8fd8\\u6709\\u914d\\u5907\\u5c