INSERT INTO `app_travelinfo` VALUES ('710', '广州动物园', '4A景区', '6.2', '1598', '广州', '49', '广州市先烈中路120号', '观赏珍稀动物体验游艺项目', 'https://piao.qunar.com/ticket/detail_1794402178.html', '1', '31.3', '6917', '广州动物园经40余年的发展，现已成为一个以展览动物为主，游乐、饮食服务配套的综合性游览场所。在布局上，北部的麻鹰岗顶为猩猩馆、狒狒和山魈馆等灵长类兽舍，南坡设置中小型兽笼，西南坡有熊山、狮山、虎山、河马池等。与麻鹰岗隔湖相望的大片山岗地上，17000平方米，并建有5个风光绮丽的小岛，放养丹顶鹤、白天鹅、鸳鸯等数10种涉禽和游禽。', '[\"https://imgs.qunarzz.com/sight/p0/1505/1a/1a32750c853d9a30.water.jpg_710x360_650e2f59.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102n1200087us7mi1182.jpg_710x360_d8d15054.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010321200087u4kck20E9.jpg_710x360_1dc8ee46.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/41/318b4571004e172c34673d6c0706cd52.water.jpg_710x360_02358851.jpg\", \"https://imgs.qunarzz.com/sight/p0/201306/26/54a5444db0510e11c8d65eac.jpg_710x360_3469b1dd.jpg\", \"https://imgs.qunarzz.com/sight/p0/201306/26/4d3977a25b81b05dc8d65eac.jpg_710x360_c1f44598.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u9a97\\u94b1\\u7684\\uff01\\uff01\\uff01\\u4e70\\u4e86\\u7968\\u4e00\\u76f4\\u6ca1\\u6709\\u663e\\u793a\\u5165\\u56ed\\u7684\\u4e8c\\u7ef4\\u7801\\uff0c\\u60f3\\u9000\\u6b3e\\u4e00\\u76f4\\u663e\\u793a\\u8ba2\\u5355\\u5904\\u7406\\u4e2d\\u4e0d\\u80fd\\u7533\\u8bf7\\u9000\\u6b3e\\uff0c\\u627e\\u5ba2\\u670d\\u6ca1\\u6709\\u56de\\u5e94\\uff0c\\u53ea\\u6709\\u673a\\u5668\\u4eba\\u5ba2\\u670d\\uff0c\\u6839\\u672c\\u89e3\\u51b3\\u4e0d\\u4e86\\u95ee\\u9898\\uff01\\uff01\\uff01\\u73b0\\u5728\\u53c8\\u663e\\u793a\\u8ba2\\u5355\\u5df2\\u4f7f\\u7528\\uff0c\\u660e\\u660e\\u6839\\u672c\\u5c31\\u6ca1\\u6709\\u7528\\u8fc7\\uff01\\uff01\\uff01\\u9a97\\u94b1\\u7684\\uff01\\uff01\\uff01\\u5982\\u679c\\u4e0d\\u662f\\u4e00\\u5b9a\\u8981\\u70b9\\u51fb\\u8bc4\\u661f\\u624d\\u80fd\\u53d1\\u5e03\\uff0c\\u6211\\u662f\\u4e00\\u9897\\u661f\\u90fd\\u4e0d\\u60f3\\u7ed9\\u7684\\uff01\\uff01\\uff01\\u9a97\\u94b1\\u7684\\uff01\\uff01\\uff01\", \"date\": \"2023-0