INSERT INTO `app_travelinfo` VALUES ('714', '增城白水寨风景名胜区', '4A景区', '9.3', '930', '广州', '48', '广州市增城区派潭镇白水寨风景名胜区', '近距离欣赏白水仙瀑', 'https://piao.qunar.com/ticket/detail_1210283150.html', '5', '56', '20808', '白水寨由多座大山组成，其中主峰白水寨山高828米。园区内悬崖瀑布与高山天池相依，通天峡谷与浅滩湿地相连，地形复杂多变。落差达428.5米的大瀑布从白水寨山上飞流直下，远看如白练飞虹，近观则宛若仙子下凡。相传，这道形态优美、充满灵气的瀑布乃是八仙之一何仙姑的化身，所以被后人尊称为白水仙瀑。茂密的森林、充足的日照与湍急的水流，白水寨具备了产生充沛空气负离子的三要素。据权威机构中南林学院测定，白水寨飞瀑区域空气中的负离子含量高 达11.25万个/立方厘米，位列广东参与测评的景区之首，是名副其实的天然大氧吧。白水寨园区内规划了多条登山路线，游人还可以选择乘坐观光车到上山观光。此外，在白水寨山下还建有绿道“白水绿道”、全新奇趣水谷、荔乡风味餐厅和太阳花广场等游玩及游览设施，让游客尽情放松。', '[\"https://imgs.qunarzz.com/sight/p0/1603/ee/ee3013caaa28ae7490.water.jpg_710x360_3b0551c8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102t120008amr9xv5D27.jpg_710x360_17d0d3cd.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104p120008ez5c6o1144.jpg_710x360_a5dc192b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104u120008alxuof28D2.jpg_710x360_a045b45d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102f120008akvrze7212.jpg_710x360_1bbff32d.jpg\", \"https://imgs.qunarzz.com/sight/p0/1411/eb/b7835e67c04d289b17d14824e7cb912c.water.jpg_710x360_ccd070d2.jpg\"]', '[{\"author\": \"s*3\", \"content\": \"\\u4ef7\\u683c\\u4fbf\\u5b9c  \\u5c31\\u662f\\u592a\\u504f\\u50fb\\uff0c\\u8fde\\u4e2a\\u5c0f\\u5356\\u90e8\\u90fd\\u6ca1\\u6709\\uff0c\\u524d\\u53f0\\u8bf4\\u597d\\u9001\\u4e24\\u652f\\u6c34\\uff0c\\u5230\\u9000\\u623f\\u4e86\\u90fd\\u6ca1\\u9001\\u6765\\u3002\\u623f\\u5b50\\u4e0d\\u9694\\u97f3\\uff0c\\u5b8c\\u5168\\u4e0d\\u9694\\u97f3\", \"date\": \"2023-06-11\", \"score\": 3}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u8fd9\\u4e2a\\u65f6\\u5019\\u722c\\u5c71\\u6e05\\u51c9\\u8212\\u670d\\uff0c\\u4e00\\u8def\\u4e0a\\u5c71\\u770b\\u5230\\u7011\\u5e03\\u7fa4\\uff0c\", \"date\": \"2023-02-20\", \"score\": 5}, {\"author\": \"l*8\", \"content\": \"\\u73a9\\u4e86\\u5f88\\u591a\\u5730\\u65b9\\uff0c\\u6211\\u6781\\u529b\\u63a8\