INSERT INTO `app_travelinfo` VALUES ('719', '重庆动物园', '4A景区', '9.6', '6009', '重庆', '50', '重庆市九龙坡区杨家坪西郊一村一号', '看看罕见的亚洲金猫和云豹', 'https://piao.qunar.com/ticket/detail_1130492785.html', '5', '24', '22841', '位于九龙坡区杨家坪，占地面积41万平方米，是重庆市较大的综合性公园。公园始建于1954年，原名西区公园，1958年后以展出动物为主，遂改现名。公园山丘环绕，竹木茂密，绿草如茵，繁花似锦，碧绿的桃花溪绕园流淌。辟有动物馆20多处，饲养动物150余种，1200余只，其中珍稀动物有大熊猫、金丝猴、白铁叶猴、扭角羊、角马和食火鸡等。公园还是国家华南虎繁殖中心种群基地。园中还辟一人工湖，小岛、曲桥、景亭相连，湖中广养禽鱼。公园中还建有儿童乐园、旱冰场、露天舞台、阅览室、餐馆、茶园等。', '[\"https://imgs.qunarzz.com/sight/p0/1910/83/83831f1889580ea2a3.img.jpg_710x360_52ac8c4b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100e12000858qaueF678.jpg_710x360_1ecff567.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/cb/cb2833d619c758caa3.img.jpg_710x360_03224e66.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/1c/1ce5b9339cce0c75a3.img.jpg_710x360_78911612.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/b7/d7220349e3e1bc67b12e604374ebd8fc.water.jpg_710x360_5e9574d6.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/13/1359431f70102bc2a3.img.jpg_710x360_5e9ee9ff.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"25\\u65e5\\u53c2\\u56e2\\u6e38\\u89c8\\u4e86\\u5929\\u751f\\u4e09\\u6865\\u548c\\u4ed9\\u5973\\u5c71\\u666f\\u533a\\uff0c\\u666f\\u533a\\u666f\\u8272\\u4f18\\u7f8e\\uff0c\\u8212\\u9002\\u5ea6\\u5f88\\u9ad8\\uff0c\\u9002\\u5408\\u590f\\u5b63\\u6e38\\u89c8\\u907f\\u6691\\uff0c\\u53f8\\u673a\\u5c0f\\u90ed\\u8f66\\u6280\\u624e\\u5b9e\\uff0c\\u8bb2\\u89e3\\u7ec6\\u81f4\\uff0c\\u670d\\u52a1\\u4f53\\u8d34\\uff0c\\u5b89\\u6392\\u7684\\u884c\\u7a0b\\u975e\\u5e38\\u5408\\u7406\\uff0c\\u53f8\\u673a\\u51c6\\u65f6\\u63a5\\u3001\\u670d\\u52a1\\u597d\\uff0c\\u5ba2\\u670d\\u5c0f\\u59d0\\u59d0\\u56de\\u7b54\\u4e5f\\u5f88\\u79ef\\u6781\\uff0c\\u4e0b\\u6b21\\u51fa\\u884c\\u8fd8\\u662f\\u9996\\u9009\\u8fd9\\u5bb6\\u3002\", \"date\": \"2023-07-26\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u8d85\\u68d2\\u7684\\u884c\\u7a0b\\uff0c\\u53f8\\u673a\\u5e08\\u5085\\u4e5f\\u5f88\\u597d\\