INSERT INTO `app_travelinfo` VALUES ('720', '洪崖洞', '4A景区', '4.8', '5624', '重庆', '46', '重庆市渝中区滨江路88号（嘉陵江畔）', '依山错落而筑的美丽吊脚楼', 'https://piao.qunar.com/ticket/detail_867002557.html', '5', '33', '13608', '洪崖洞民俗风貌区作为国家 级AAAA旅游风景区，是小天鹅集团投资修建的目前市中心唯 一一片展现巴渝传统民俗风貌区域。他与朝天门广场、解放碑商务区形共同形成了重庆最市民及游客追捧的游览胜地。“洪崖洞”用创新的思维把古老的巴渝吊脚楼建筑艺术注入全新的文化理念之中；与江景和城市风貌完美融合，以75米高的超常落差，顺崖而上，高低错落、依山而建。它浓缩了重庆三千年历史沉淀，融重庆独特的巴渝民俗文化、山地民居的建筑文化、码头文化于一体，成为倍受世界瞩目的，独特靓丽的城市名片。如今，热度持续升温的洪崖洞民俗风貌区，以其独特的人文内涵和璀璨的建筑特色，已然成为人流量仅次于故宫，国内外游客争相打卡、驻足、游览的重庆地标性景区。每一天，洪崖洞以其独特的魅力和不可复制的特色，吸引着来自全球各地的游客前来观光游览。', '[\"https://imgs.qunarzz.com/sight/p0/1910/a7/a7863ded4e64b6faa3.img.jpg_710x360_5c2af40f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101t1200083ivqu26130.jpg_710x360_3c2d4f29.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101j12000858i0l13386.jpg_710x360_72f9204a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/65/650dbb6a949c000aa3.img.jpg_710x360_9290eb01.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/ab/aba8a7f3c9a8839da3.img.jpg_710x360_c8b067a9.jpg\", \"https://imgs.qunarzz.com/sight/p0/1910/73/739a6aa71cea7475a3.img.jpg_710x360_b023c937.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u4e01\\u4e01\\u5e08\\u5085\\u5f88\\u4e0d\\u9519\\uff0c\\u8def\\u7ebf\\u4e5f\\u5f88\\u987a\\uff0c\\u4e00\\u5929\\u7684\\u884c\\u7a0b\\u5b89\\u6392\\u975e\\u5e38\\u4e30\\u5bcc\\uff0c\\u8fd8\\u8d34\\u5fc3\\u5730\\u4e3a\\u6211\\u4eec\\u63d0\\u524d\\u6253\\u5f00\\u7a7a\\u8c03\\u3002\\u674e\\u5b50\\u575d\\u8f7b\\u8f68\\u7a7f\\u697c\\u89c1\\u8bc6\\u4e86\\u91cd\\u5e86\\u7a7a\\u95f4\\u5229\\u7528\\u7684\\u6781\\u81f4\\uff0c\\u957f\\u6c5f\\u7d22\\u9053\\u53ef\\u4ee5\\u63d0\\u524d\\u9884\\u7ea6\\uff0c\\u8282\\u7701\\u6392\\u961f\\u65f6\\u95f4\\uff0c\\u867d\\u7136\\u65f6\\u95f4\\u77ed\\uff0c\\u4f46\\u662f\\u5f88\\u4e0d\\u9519\\u7684\\u4f53\\u9a8c\\u3002\\u7ea2\\u5ca9\\u7ea2\\u8272\\u4e4b\\u65c5\\u51e0\\u4e2a\\u5730\\u65b9\\u79bb\\u5f97\\u90fd\\u5f88\\u8fd1\\u3002\\u975e\\u5e38\\u51