INSERT INTO `app_travelinfo` VALUES ('724', '重庆长江索道', '4A景区', '1.5', '2126', '重庆', '49', '重庆市渝中区新华路151号', '跨越长江俯瞰山城美妙市景', 'https://piao.qunar.com/ticket/detail_572843179.html', '5', '3', '31167', '重庆是一座立体之城、陆、空三种旅游形式的城市，轮船是水中的、汽车是陆地的，索道是天上的。全长1166米60人，单程运行时间4.5分钟，2009。不坐索道，白来重庆。作为国家3A级景区，长江索道浓缩了“山城、江城、不夜城”的精华，是重庆必游之地和重庆特色体验地。重庆特色之美，美在于形，江中有山、山中有城。长江索道重庆空中游，“穿梭高楼大厦之间、飞渡浩瀚大江之上，行游山水都市之巅”，往返乘坐，可全方位俯看重庆“江山一体”美景，更有一种“君临天下”征服重庆的成就感。正因如此”，还成为现代影视剧在重庆拍摄必选地。自2001年起，已有巩俐、孙红雷主演的《周渔的火车》、黄渤、徐铮主演的《疯狂的石头》，学圻、范冰冰主演的《日照重庆》、陈坤、杨幂主演的《门》、林志玲主演的《北京纽约》等数十部影片在此取景拍摄。', '[\"https://imgs.qunarzz.com/sight/p0/1805/ad/ad507355a167f587a3.img.jpg_710x360_e6381251.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010691200083irfr8D3C5.jpg_710x360_fbfcc645.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101f1200085t1py26F46.jpg_710x360_088a15a8.jpg\", \"https://imgs.qunarzz.com/sight/p0/1805/cd/cdd45bb2105877fea3.img.jpg_710x360_8a1aa690.jpg\", \"https://imgs.qunarzz.com/sight/p0/1805/ba/ba56a51849aa0f7ea3.img.jpg_710x360_59c81344.jpg\", \"https://imgs.qunarzz.com/sight/p0/1805/7a/7ab2b0f943725378a3.img.jpg_710x360_6ea396a8.jpg\"]', '[{\"author\": \"b*5\", \"content\": \"\\u5e26\\u5916\\u5730\\u670b\\u53cb\\u6e38\\u73a9\\uff0c\\u5728\\u7f51\\u4e0a\\u4e70\\u4e86\\u8fd9\\u4e2a\\u5957\\u7968\\uff0c\\u5305\\u542b\\u4e86\\u5f88\\u591a\\u9879\\u76ee\\uff0c\\u5f88\\u65b9\\u4fbf\\uff0c\\u800c\\u4e14\\u89c4\\u5212\\u5f97\\u5f88\\u597d\\uff0c\\u771f\\u7684\\u662f\\u6027\\u4ef7\\u6bd4\\u8d85\\u9ad8\\uff0c\\u670b\\u53cb\\u73a9\\u5f97\\u5f88\\u6ee1\\u610f\\uff0c\\u4e00\\u5957\\u7968\\u5168\\u90e8\\u641e\\u5b9a\\uff0c\\u800c\\u4e14\\u670d\\u52a1\\u5f88\\u597d\\uff0c\\u5f3a\\u70c8\\u63a8\\u8350\\uff01\\u6027\\u4ef7\\u6bd4\\u8d85\\u9ad8\\u3002\", \"date\": \"2023-05-26\", \"score\": 5}, {\"author\": \"h*0\", \"content\": \"\\u6027\\u4ef7\\u6bd4\\u6700\\u9ad8\\uff0c\\u4f5c\\u4e3a\\u91cd\\u5e86\\u4eba\\u90fd\\u63a8\\u8350\\uff01\", \"date\": \"2023-01-21\", \"score\": 5