INSERT INTO `app_travelinfo` VALUES ('725', '黑山谷', '5A景区', '9.4', '2388', '重庆', '48', '重庆市万盛区黑山镇境内', '市郊天然大氧吧', 'https://piao.qunar.com/ticket/detail_581035896.html', '5', '80', '21158', '黑山谷风景区，是国家“AAAAA”级风景区。位于万盛经济技术开发区黑山镇境内，景区面积100平方公里，距离重庆市区110公里，是峡谷穿越、漂流观景、 攀岩探险、野营露宿、垂钓狩猎的绝佳去处。该景区自然景观十分丰富，山、水、泉、林、洞俱全，融奇、险、峻、秀、幽于一体。既有奇峰、异石、参天古树，又 有碧水、高峡、迎面飞瀑；既有古老的地质地貌，又有罕见的珍禽异兽。春天，碧水幽谷、秀峰翠鸟，令人流连忘返；夏季，森林如盖、遮天蔽日，让人心生凉意； 秋天，层林尽染，肥美的山鸡野兔奔兀其间，使人置身仙境；冬季，皑皑白雪、银妆素裹，令人赏心悦目。这里人迹罕至，古为夜郎国王夏宫，有着如诗如画的传说；这里峡高谷深，碧波如玉，穿行其间，峡中峡、谷中谷、洞中洞、瀑上瀑、令人眼花缭乱，目不暇接；这里动植物种类繁多，国家一级保护植物银杉、珙桐，国 家一级保护动物黑叶猴、云豹等分布其间，是重庆市独特的“生物基因库”，被称为“西南神农架”。', '[\"https://imgs.qunarzz.com/sight/p0/201305/08/8638c7dbd656cafac8d65eac.jpg_710x360_64dd0551.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101t120008270n4eCAF0.jpg_710x360_93f57cd3.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105f1200087edgs9C8D0.jpg_710x360_76ea580d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106d1200087e46cx9985.jpg_710x360_6a4bce87.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100m12000000s3w5u0409.jpg_710x360_2eb9a1d5.jpg\", \"https://imgs.qunarzz.com/sight/p0/2002/cf/cf48b1cc56a6cafba3.water.jpg_710x360_c1b3b8a5.jpg\"]', '[{\"author\": \"j*5\", \"content\": \"\\u970d\\u6811\\u6885\\u5bfc\\u6e38\\u5f88\\u4e0d\\u9519\\uff0c\\u5e74\\u8f7b\\u6f02\\u4eae\\u6709\\u6d3b\\u529b\\uff0c\\u666f\\u70b9\\u4ecb\\u7ecd\\u4e5f\\u5f88\\u4e0d\\u9519\\uff0c\\u5bf9\\u56e2\\u91cc\\u5e74\\u957f\\u8001\\u4eba\\u7279\\u522b\\u5173\\u5fc3\\u6709\\u52a0\\uff0c\\u975e\\u5e38\\u6709\\u7231\\u5fc3\\uff0c\\u5168\\u7a0b\\u6ca1\\u6709\\u9690\\u6027\\u7684\\u6d88\\u8d39\\u9879\\u76ee\\uff0c\\u7535\\u74f6\\u8f66\\u548c\\u89c2\\u5149\\u7684\\u7d22\\u9053\\u4e5f\\u662f\\u81ea\\u613f\\u9009\\u62e9\\u662f\\u5426\\u8d2d\\u7968\\u3002\\u9ed1\\u5c71\\u8c37\\u98ce\\u666f\\u8fd8\\u7b97\\u53ef\\u4ee5\\uff0c\\u4e3b\\u8981\\u5c31\\u662f\\u56f4\\u7ed5\\u4e00\\u6761\\u6cb3\\u4fee\\u5efa\\u7684