INSERT INTO `app_travelinfo` VALUES ('733', '毕兹卡水乐园', '未评价', '10', '70', '重庆', '50', '重庆市黔江区濯水镇秋蒲路', '毕兹卡水乐园位于全球知名旅游城市重庆黔江创建中的濯水5A级景区内，乐园南接蒲花河，西靠蒲花暗河风景区，东临濯水古镇。与世', 'https://piao.qunar.com/ticket/detail_3180910427.html', '5.0', '29.9', '336', '毕兹卡水乐园位于全球知名旅游城市重庆黔江创建中的濯水5A级景区内，乐园南接蒲花河，西靠蒲花暗河风景区，东临濯水古镇。与世界di一廊桥——风雨廊桥毗邻，总占地面积近10万平方米，毕兹卡水乐园以客为尊的服务理念为本。全国引进众多美国欢乐岛公司水上游乐设备，有获得国际旅游行业协会“金票奖”的超级大喇叭国际旅游行业协会荣誉推荐项目“互动水寨”和“急骋竞赛（眼睛蛇滑梯）”、zui受欢迎项目“四彩滑梯”和“儿童水寨”、惊险刺激的水上自由落体滑梯“高速滑梯”以及世界上zui受欢迎的海浪池及造浪池融为一体的欢乐海，惊险刺激的漂流河等11个水上娱乐设施。配套美食广场，自助快餐美食众多，口味齐全，多种特色菜肴及小吃满足消费者家庭聚餐、团队接待等各种需求。', '[\"https://imgs.qunarzz.com/sight/p0/1906/77/779f78a6e22a769ca3.img.jpg_710x360_b9f1f66b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/70/705d395468ab9554a3.img.jpg_710x360_75531a07.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/6c/6c43b4597b89fd8ca3.img.jpg_710x360_a452c92b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/28/28dbe37dc57d3e22a3.img.jpg_710x360_4352ceca.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/62/62808669d4a3ddbea3.img.jpg_710x360_2d781eab.jpg\", \"https://imgs.qunarzz.com/sight/p0/1906/20/2098d6f5935197da3.img.jpg_710x360_06bdda58.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1906/77/779f78a6e22a769ca3.img.jpg_280x200_4881775d.jpg', '2023-08-03');
