INSERT INTO `app_travelinfo` VALUES ('751', '长恨歌', '未评价', '10', '10597', '西安', '49', '西安市临潼区华清路38号（华清池景区内）', '杨贵妃与唐明皇的千古爱情', 'https://piao.qunar.com/ticket/detail_1562343914.html', '5', '29', '66926', '长恨歌陕西旅游集团公司精心推出的中国首部大型实景历史舞剧《长恨歌》以唐代诗人白居易的传世名篇《长恨歌》为主题，实现了让“真山真水真历史”为我所用和深度挖掘的突破。而借助现代科技和艺术表现手段，使这部舞剧在形象上实现了全新突破。每年4月至10月的每个夜晚，舞剧《长恨歌》都会在华清池内准时上演，以其大手笔、大制作、大阵容为陕西旅游注入活力，给中外游客带来全新体验和愉悦的享受。经过数次改版和不断锤炼的《长恨歌》已然至善至美、臻于化境，每晚双场演出座无虚席，一票难求火爆异常，《长恨歌》这一堪称旅游文化瑰宝的惊世之作必将成为陕西乃至全国旅游的一张金边名片。', '[\"https://imgs.qunarzz.com/sight/p0/1505/18/18ffcca94b1b7ca2.water.jpg_710x360_aff7e01a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/30/30b75fe5792c3e59a3.img.png_710x360_e1db8742.png\", \"https://imgs.qunarzz.com/sight/p0/1504/46/468a3f760022936.water.jpg_710x360_74553766.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/12/12537e17721ff9c0a3.img.png_710x360_447cc1aa.png\", \"https://imgs.qunarzz.com/sight/p0/1706/73/7349ea5c85af9d5ea3.img.png_710x360_3de6954b.png\", \"https://imgs.qunarzz.com/sight/p0/1706/4d/4dc144af8b342ad2a3.img.png_710x360_244e7f7e.png\"]', '[{\"author\": \"f*6\", \"content\": \"\\u897f\\u5b89\\u7684\\u5bfc\\u6e38\\u5e02\\u573a\\u73b0\\u5728\\u786e\\u5b9e\\u633a\\u89c4\\u8303\\u7684\\uff0c\\u6ca1\\u6709\\u8d2d\\u7269\\uff0c\\u81ea\\u8d39\\u9879\\u76ee\\u4e5f\\u662f\\u81ea\\u613f\\u3002\\u5728\\u51e0\\u4e2a\\u5e73\\u53f0\\u9009\\u62e9\\u4e86\\u53bb\\u54ea\\u513f\\u4e86\\uff0c\\u8fd9\\u4e2a\\u4ef7\\u683c\\u786e\\u5b9e\\u633a\\u5b9e\\u60e0\\uff0c\\u5bfc\\u6e38\\u5f20\\u65ed\\u5b81\\u662f\\u4e2a\\u5e05\\u54e5\\uff0c\\u8bb2\\u5f97\\u633a\\u4ed4\\u7ec6\\uff0c\\u7ecf\\u9a8c\\u4e30\\u5bcc\\uff0c\\u8282\\u594f\\u5408\\u7406\\u3002\\u6691\\u5047\\u9009\\u62e9\\u8fd9\\u4e9b\\u4eba\\u6587\\u666f\\u70b9\\u8fd8\\u662f\\u633a\\u7d2f\\u4eba\\u7684\\uff0c\\u56e0\\u4e3a\\u4eba\\u6bd4\\u8f83\\u591a\\uff0c\\u5c3d\\u91cf\\u4e0d\\u8981\\u9009\\u62e9\\u5468\\u672b\\uff0c\\u8ddf\\u56e2\\u6e38\\u6bd4\\u8f83\\u7701\\u5fc3\\u3002\", \"date\": \"2023-07-04\", \"score\": 5}, {\"author\": \"\\u53bb