INSERT INTO `app_travelinfo` VALUES ('753', '西安碑林博物馆', '5A景区', '0.1', '4020', '西安', '48', '西安市文昌门内三学街15号', '阅览书法名家碑刻精品', 'https://piao.qunar.com/ticket/detail_3366697776.html', '5', '0.1', '19025', '西安碑林座落于著名古城西安市三学街（因清代的长安学、府学、咸宁学均设在这里而得此名）。它于北宋二年（公元1078年）为保存《开成石经》而建立。九百多年来，经历代征集，扩大收藏，精心保护，入藏碑石近三千方。现有六个碑廊、七座碑室、八个碑亭，陈列展出了共一千零八十七方碑石。在名碑荟萃的展室里，展示了圣儒、哲人的浩瀚石经；秦汉文人的古朴遗风；魏晋北朝墓志的英华；大唐名家的绝代书法以及宋元名士的潇洒笔墨。', '[\"https://imgs.qunarzz.com/sight/p0/1411/71/85c2cb1eaf30e08b0b2f59d76a27a92a.water.jpg_710x360_9545773e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010011200082jbnbj4564.jpg_710x360_b6d10314.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/f1/f138ecfa4a69e4aba3.img.jpg_710x360_ff9500d4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/c1/c10dc38791218a4ca3.img.jpg_710x360_26bc1800.jpg\", \"https://imgs.qunarzz.com/sight/p0/1411/2c/d0a2f1c242aafd199b24395beaa2a25e.water.jpg_710x360_a6eb4f98.jpg\", \"https://imgs.qunarzz.com/sight/p0/1411/5e/f897684b8b522db10631cbf908107d67.water.jpg_710x360_73970dba.jpg\"]', '[{\"author\": \"t*8\", \"content\": \"\\u5e2e\\u5bb6\\u91cc\\u4eba\\u548c\\u5c0f\\u670b\\u53cb\\u5b9a\\u7684\\uff0c\\u4eca\\u5929\\u7684\\u8bb2\\u89e3\\u521a\\u7ed3\\u675f\\uff0c\\u53cd\\u9988\\u8bb2\\u7684\\u975e\\u5e38\\u597d\\uff0c\\u672c\\u6765\\u8ba2\\u7684\\u7891\\u6797\\u8bb2\\u89e3\\u5e94\\u8be5\\u662f2\\u5c0f\\u65f6\\u7684\\uff0c\\u4f46\\u8001\\u5e08\\u4ece9:30\\u4e00\\u76f4\\u8bb2\\u523012:30\\uff0c\\u8db3\\u8db33\\u5c0f\\u65f6\\uff0c\\u5f88\\u503c\\uff0c\\u5c0f\\u670b\\u53cb\\u4e5f\\u4ece\\u8001\\u5e08\\u7684\\u8bb2\\u89e3\\u4e2d\\u611f\\u53d7\\u5230\\u4e86\\u4e2d\\u56fd\\u4e66\\u6cd5\\u827a\\u672f\\u7684\\u9b45\\u529b\\uff0c\\u53bb\\u897f\\u5b89\\u4e4b\\u524d\\u8fd8\\u4e00\\u76f4\\u62c5\\u5fc3\\u4e0d\\u826f\\u5bfc\\u6e38\\u4f1a\\u5f71\\u54cd\\u5b69\\u5b50\\u5bf9\\u79e6\\u6587\\u534e\\u548c\\u4e2d\\u534e\\u6587\\u660e\\u7684\\u7406\\u89e3\\u548c\\u611f\\u609f\\uff0c\\u73b0\\u5728\\u770b\\u90fd\\u5f88\\u597d\\uff0c\\u7891\\u6797\\u