INSERT INTO `app_travelinfo` VALUES ('764', '西湖', '5A景区', '10', '1930', '杭州', '47', '杭州市西湖风景名胜区', '杭州之美的代言人', 'https://piao.qunar.com/ticket/detail_4125579163.html', '5', '8', '22808', '西湖古称“钱塘湖”，又名“西子湖”，古代诗人苏轼就对它评价道：“欲把西湖比西子，淡妆浓抹总相宜。”西湖形态为近于等轴的多边形，湖面被孤山及苏堤、白堤两条人工堤分割为5个子湖区，子湖区间由桥孔连通，各部分的湖水不能充分掺混，造成各湖区水质差异，大部分径流补给先进入西侧3个子湖区，再进入外西湖；湖水总面积5.593平方公里;总容积1．10亿立方米，平均水深1．97 米；西湖底质是一种有机质含量特别高的湖沼相沉积，属于粉砂质粘土或粉砂质亚粘土（黑色有机质粘土），中层泥炭层或沼泽土；入湖河流部是短小的溪涧，主要补水河流为金沙涧、龙泓涧和长桥溪泄流。西湖，是一首诗，一幅天然图画，一个美丽动人的故事，不论是多年居住在这里的人还是匆匆而过的旅人，无不为这天下无双的美景所倾倒。', '[\"https://imgs.qunarzz.com/sight/p0/1603/87/87146cf32fa8635e90.water.png_710x360_78e8d124.png\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100b12000000s6qh915B7.jpg_710x360_6cfeca7f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101t120004cs6gio3773.jpg_710x360_c74eb1b2.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100n120008aotum536DF.jpg_710x360_62a0bd37.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102y1200085ivzfa0B45.jpg_710x360_04eeb9e8.jpg\", \"https://imgs.qunarzz.com/sight/p0/1908/6f/6f7bf234e435bc2ea3.img.jpg_710x360_6f8281ff.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"4\\u6708\\u7684\\u676d\\u5dde\\uff0c\\u6c14\\u6e29\\u521a\\u521a\\u597d\\uff0c\\u65c5\\u884c\\u793e\\u884c\\u7a0b\\u5b89\\u6392\\u5408\\u7406\\uff0c\\u5bfc\\u6e38\\u5de5\\u4f5c\\u8ba4\\u771f\\u3002\\u7eaf\\u73a9\\u56e2\\u5b9e\\u5b9e\\u5728\\u5728\\u7684\\u4e00\\u65e5\\u6e38\\u3002\", \"date\": \"2023-04-07\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u6109\\u5feb\\u7684\\u4e00\\u5929\\u7ed3\\u675f\\u4e86\\uff0c\\u6c5f\\u5357\\u5c0f\\u9547\\u6211\\u4f1a\\u518d\\u6765\\u7684\\u3002\", \"date\": \"2023-04-05\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u676d\\u5dde\\uff0c\\u6211\\u6700\\u559c\\u6b22\\u7684\\u57ce\\u5e02\\u4e4b\\u4e00\\u3002\\u897f\\u6e56\\uff0c\\u6211\\u6700\\u559c\\u6b22\\u7684\\u666f\\u70b9\\u4e4