INSERT INTO `app_travelinfo` VALUES ('765', '雷峰塔', '4A景区', '0.7', '2262', '杭州', '48', '杭州市西湖区南山路15号', '拍西湖全貌赏塔楼倩影', 'https://piao.qunar.com/ticket/detail_2540220911.html', '5', '0.6', '11502', '雷峰塔位于浙江杭州西湖南岸夕照山的雷峰上，是中国首座彩色铜雕宝塔。雷峰塔一名黄妃塔，又称西关砖塔。为吴越国王钱俶因黄妃得子建，旧塔已于1924年倒塌，现已重建。每当夕阳西下，塔影横空，别有一番景色，雷峰夕照为西湖十景之一。雷峰塔也因《白蛇传》中禁锢白娘子的传说而闻名遐迩。”：塔类建筑采用钢材框架作为建筑支撑。。', '[\"https://imgs.qunarzz.com/sight/p0/1412/f2/3ff99d92f10eda6bfc40db2f76e21b56.water.jpg_710x360_d07b151d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104h1200087cwff5F5C2.jpg_710x360_fe892bfd.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101t120008277nmx2655.jpg_710x360_5d6b7d50.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103t1200085k25o2DBF7.jpg_710x360_399073cb.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106y1200087dix5609B8.jpg_710x360_ef8fa660.jpg\", \"https://imgs.qunarzz.com/sight/p0/1409/19/89a2df543d5fba4ddd098f682c1c1845.jpg_710x360_75ccd78e.jpg\"]', '[{\"author\": \"\\u53f6*\\u660e\", \"content\": \"\\u8212\\u5fc3\\u6109\\u60a6\\u7684\\u4e00\\u5929\\uff0c\\u771f\\u7684\\u5f88\\u4e0d\\u9519\\u7684\\u65c5\\u6e38\\u56e2\\uff0c\\u4e0b\\u4e86\\u8ba2\\u5355\\u540e\\uff0c\\u5bfc\\u6e38\\u4f1a\\u5728\\u51fa\\u53d1\\u524d\\u7684\\u4e00\\u5929\\u8054\\u7cfb\\u6211\\u4eec\\uff0c\\u7559\\u597d\\u4fe1\\u606f\\uff0c\\u8ddf\\u56e2\\u8fd9\\u5929\\u7531\\u5bfc\\u6e38\\u5e26\\u9886\\u4f1a\\u987a\\u5229\\u5f88\\u591a\\uff0c\\u8854\\u63a5\\u5b89\\u6392\\u7684\\u6bd4\\u8f83\\u597d\\uff0c\\u4e5f\\u4f1a\\u6709\\u7ed9\\u6211\\u4eec\\u81ea\\u7531\\u6d3b\\u52a8\\u7684\\u65f6\\u95f4\\uff0c\\u897f\\u6e56\\u98ce\\u666f\\u5f88\\u7f8e\\uff0c\\u8d4f\\u5fc3\\u60a6\\u76ee\\uff0c\\u7279\\u522b\\u6ee1\\u610f\\u7684\\u4e00\\u5929\\uff0c\\u8fd9\\u4e2a\\u56e2\\u6ca1\\u6709\\u8d2d\\u7269\\u7684\\uff0c\\u6bd4\\u5fc3\\u3002\", \"date\": \"2023-03-29\", \"score\": 5}, {\"author\": \"\\u66b4*\\u867e\", \"content\": \"\\u7eaf\\u73a9\\uff0c\\u