INSERT INTO `app_travelinfo` VALUES ('767', '雅鲁激流探险漂流', '未评价', '10', '5003', '杭州', '49', '杭州市桐庐县合村乡合村村雅鲁激流探险风景区', '总落差近两百米的峡谷漂流', 'https://piao.qunar.com/ticket/detail_631864041.html', '5', '198', '2078', '大溪峡的魅力究竟何在？一言以蔽之：大溪峡激流闯滩的大风景、大格局、大落差、大水量、大 刺激五“大”特色，给很多游客带来了惊心动魄的水上体验。具体来说，地处第四纪冰川遗址的大溪 峡，全程拥有8公里的惊艳峡谷，198米的垂直落差和5m/秒的水量，能让人感受到惊世骇俗的磅礴气 势。', '[\"https://imgs.qunarzz.com/sight/p0/1506/43/4332a97f7e77fabe.water.jpg_710x360_54e66db2.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102y120008j6utj1D820.jpg_710x360_2367e247.jpg\", \"https://imgs.qunarzz.com/sight/p0/1506/b3/b32a9c5b7b54b8b9.water.jpg_710x360_81411943.jpg\", \"https://imgs.qunarzz.com/sight/p0/1506/13/1354987805ae5f0b.water.jpg_710x360_efd4b917.jpg\", \"https://imgs.qunarzz.com/sight/p0/1506/24/24cca682e539e8d7.water.jpg_710x360_ae2a306e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1506/dd/ddf44943c010786f.water.jpg_710x360_368c981d.jpg\"]', '[{\"author\": \"w*3\", \"content\": \"\\u5f88\\u68d2\\u503c\\u5f97\\u4e00\\u73a9\\u3002\\u5f88\\u523a\\u6fc0\\u590f\\u5929\\u5e26\\u5b69\\u5b50\\u905b\\u5a03\\u597d\\u53bb\\u5904\\uff01\", \"date\": \"2023-08-01\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u679c\\u7136\\u4e0d\\u865a\\u6b64\\u884c\\uff0c\\u867d\\u7136\\u4eba\\u6bd4\\u8f83\\u591a\\uff0c\\u4f46\\u67b6\\u4e0d\\u4f4f\\u7f8e\\u5440\\uff0c\\u5168\\u7a0b\\u60ca\\u559c\\u8fde\\u8fde\\uff0c\\u5168\\u7a0b8\\u516c\\u91cc\\uff0c\\u771f\\u7684\\u5f88\\u723d\\uff0c\\u6709\\u5f88\\u591a\\u7684\\u6025\\u6d41\\u3002\\u6240\\u4ee5\\u4e00\\u5b9a\\u8981\\u6293\\u7d27\\u628a\\u624b\\uff0c\\u5207\\u8bb0\\uff0c\\u8981\\u4e0d\\u6574\\u4e2a\\u4eba\\u5c31\\u4f1a\\u7ffb\\u5230\\u6eaa\\u6d41\\uff0c\\u591f\\u4f60\\u5403\\u4e00\\u58f6\\u7684\\uff0c\\u53ef\\u4e0d\\u662f\\u5f00\\u73a9\\u7b11\\u7684\\uff0c\\u51fa\\u95e8\\u5728\\u5916\\u5b89\\u5168\\u7b2c\\u4e00\\u3002\\u8fd8\\u6709\\u8981\\u53e6\\u5916\\u591a\\u5e26\\u8863\\u670d\\u88e4\\u5b50\\uff0c\\u6f02\\u6d41\\u4e0b\\u6765\\u5c31\