INSERT INTO `app_travelinfo` VALUES ('775', '杭州野生动物世界', '4A景区', '10', '1282', '杭州', '49', '杭州市杭富路九龙大道1号', '寓教于乐亲近小动物', 'https://piao.qunar.com/ticket/detail_3518681031.html', '5', '210', '21879', '野生动物世界是浙江省重点旅游项目——杭州野生动物世界，园区山峦起伏、流水潺潺、植被茂盛，依托原有的山形地貌设有步行区和车行区两大游览区。青山绿水间徜徉着来自世界各地的几百种珍禽异兽和我国一、二级保护动物近万只。如：白狮、白虎、白犀牛、大熊猫、金丝猴等。', '[\"https://imgs.qunarzz.com/sight/p0/1902/49/49859385dce03352a3.img.jpg_710x360_ab5f72f9.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102y120008g2xn1eBE39.jpg_710x360_012c6624.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103e1200087u59efE504.jpg_710x360_bedbcb35.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01048120008ewwqia039A.jpg_710x360_8bdd8c54.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100q120008ex51al440A.jpg_710x360_342557ce.jpg\", \"https://imgs.qunarzz.com/sight/p0/1902/ef/ef70a22af6d40654a3.img.jpg_710x360_7628dc3d.jpg\"]', '[{\"author\": \"i*1\", \"content\": \"\\u573a\\u5730\\u5c0f\\uff0c\\u52a8\\u7269\\u5c11\\uff0c\\u5168\\u662f\\u7334\\u5b50\\u4e4c\\u9f9f\\u548c\\u9e66\\u9e49\\u3002\\u90a3\\u4e48\\u5c0f\\u8fd8\\u5750\\u706b\\u8f66\\uff1f\\u62112\\u5c0f\\u65f6\\u80fd\\u901b\\u4e00\\u4e2a\\u6765\\u56de\\uff01\\u7334\\u5b50\\u591a\\u5230\\u5410\\uff0c\\u5b54\\u96c0\\u3001\\u6bd2\\u86c7\\u3001\\u91ce\\u732a\\u3001\\u9b23\\u72d7\\u3001\\u5e73\\u5934\\u54e5\\u3001\\u7a7f\\u5c71\\u7532\\u3001\\u7f8e\\u6d32\\u72ee\\u3001\\u72d0\\u72f8\\u3001\\u4f01\\u9e45\\u3001\\u5317\\u6781\\u718a\\u90fd\\u6ca1\\u6709\\u770b\\u7740\\uff0c\\u4e0d\\u77e5\\u9053\\u6709\\u6ca1\\u6709\\u3002\\u5efa\\u8bae\\u6b65\\u884c\\u6e38\\u89c8\\uff0c\\u4ec0\\u4e48\\u8f66\\u90fd\\u4e0d\\u9700\\u8981\\u3002210\\u7684\\u95e8\\u7968\\u8d35\\u4e86\\u70b9\\uff0c150\\u5dee\\u4e0d\\u591a\\u3002\\u5176\\u5b83\\u7684\\u90fd\\u633a\\u597d\\u7684\\uff0c\\u5c24\\u5176\\u73af\\u5883\\u536b\\u751f\\u4e0d\\u9519\\uff01\", \"date\": \"2023-06-06\", \"score\": 4}, {\"author\":