INSERT INTO `app_travelinfo` VALUES ('777', '鼓浪屿', '5A景区', '10', '3323', '厦门', '48', '厦门市思明区鼓浪屿', '万国建筑博览', 'https://piao.qunar.com/ticket/detail_1708816601.html', '5', '10', '83053', '鼓浪屿隶属于福建省厦门市，位于厦门岛西南隅，与厦门半岛隔海相望，只隔一条宽600米的鹭江，轮渡4.5分钟可达。面积1.91平方千米，2万多人，现为思明区所辖。岛上气候宜人，四季如春，无车马喧嚣，鸟语花香，素有“海上花园”之誉，岛上收藏众多古典钢琴沁心典雅，更为其赢得“钢琴之岛”的美誉。鼓浪屿原名“圆沙洲”，别名“圆洲仔”，明朝改称“鼓浪屿”。乃因岛西南方有一礁石，每当涨潮水涌，浪击礁石，声似擂鼓，人们称“鼓浪石”，鼓浪屿因此而得名。鼓浪屿街道短小，纵横交错，清洁幽静，空气新鲜，岛上树木苍翠，繁花似锦，特别是小楼红瓦与绿树相映，显得格外漂亮。', '[\"https://imgs.qunarzz.com/sight/p0/1506/f6/f6b727f036fe5d6d.water.jpg_710x360_86c9a116.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106e1200087dg5q9E686.jpg_710x360_51f6993a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101t12000825yd946670.jpg_710x360_3de26dc5.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105u1200087cqv2aD4F0.jpg_710x360_1b24d9e8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01001120008261fvw7FEE.jpg_710x360_d2808bdb.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106r1200087cuwjt4D32.jpg_710x360_a6b567a9.jpg\"]', '[{\"author\": \"l*9\", \"content\": \"\\u5ba2\\u670d\\u70ed\\u60c5\\u8010\\u5fc3\\u5e2e\\u6211\\u4eec\\u63a8\\u8350\\u4e86\\u7ebf\\u8def\\uff0c\\u8fd8\\u7ec6\\u5fc3\\u544a\\u77e5\\u4e86\\u53ef\\u4ee5\\u4e0a\\u95e8\\u63a5\\u9001\\uff0c\\u7136\\u540e\\u6211\\u4eec\\u4e00\\u56e2\\u6b63\\u5f0f\\u5f00\\u542f\\u4e86\\u6109\\u5feb\\u7684\\u571f\\u697c\\u4e4b\\u65c5\\u3002\\u611f\\u53f9\\u5ba2\\u5bb6\\u4eba\\u7684\\u667a\\u6167\\uff0c\\u600e\\u4e48\\u5efa\\u51fa\\u8fd9\\u4e48\\u5b8f\\u4f1f\\u7684\\u571f\\u697c\\u5efa\\u7b51\\uff0c\\u51e0\\u767e\\u5e74\\u4e86\\u8fd8\\u6709\\u4eba\\u80fd\\u6b63\\u5e38\\u5c45\\u4f4f\\u3002\\u884c\\u7a0b\\u5b89\\u6392\\u5408\\u7406\\uff0c\\u8001\\u4eba\\u53c2\\u52a0\\u4e0d\\u7d2f\\u3002\\u5f20\\u5e08\\u5085\\u4f1a\\u63d0\\u524d\\u8be6\\u7ec6\\u5730\\u544a\\u77e5\\u6ce8\\u610f\\u4e8b\\u9879\\uff0c\\u7ed9\\u5927\\u5bb6\\u5531\\u5c71