INSERT INTO `app_travelinfo` VALUES ('783', '厦门日月谷温泉度假村', '4A景区', '7.6', '86', '厦门', '47', '厦门市海沧区孚莲路1888号', '颇具东南亚风情', 'https://piao.qunar.com/ticket/detail_1697896012.html', '5', '175', '7384', '日月谷温泉坐落于福建省厦门市海沧区东孚镇汤岸，交通便捷，闹中取静，日月谷温泉独享优越的地理环境。整体规划面积约26万平方米，包括日月谷温泉主题公园、日月谷温泉酒店以及日月谷温泉私人会所，是集观光度假、休闲疗养、商务会议、运动健身、生态教学与环保宣传等一体的综合性旅游度假村。厦门日月谷温泉度假村为福建省首家大型园林露天温泉，总体规划26万平方米气势磅礴，富有浓郁东南亚异国风情、设有日月谷温泉公园、日月谷温泉酒店以及日月谷温泉私人会所，是集观光度假、休闲疗养、商务会议、运*健身、生态示范、环保宣传等于一体的多*能综合型旅游度假区。日月谷温泉主题公园设有近百个露天温泉泡池，几十种特色温泉、恢弘的接待大厅，异国风味美食坊、古香古色抚荷轩，养生阁、月光亭、泡泡吧、超大型游泳池……每到一处。 日月谷温泉酒店是按照国际五星级标准设计建造的涉外旅游饭店、中餐厅、西餐厅、会议室、会所、KTV等一应俱全，拥有各类设施完善的高级客房及套房115间24小时天然温泉私密泡池。', '[\"https://imgs.qunarzz.com/sight/p0/1912/36/36403a427c245524a3.img.jpg_710x360_4b32d1fc.jpg\", \"https://imgs.qunarzz.com/sight/p0/1912/a0/a0d7c33a687f9159a3.img.jpg_710x360_6141e25c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/48/483bbe9b75a7c30ca3.img.jpg_710x360_01925096.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/2e/2e821855b63a7fc9a3.img.jpg_710x360_1d0f4b94.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/ab/ab559ad943577813a3.img.jpg_710x360_ff153d65.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/da/273e834ab7e13fd5a51751d57d20f828.water.jpg_710x360_6bac9140.jpg\"]', '[{\"author\": \"x*3\", \"content\": \"\\u6e29\\u6cc9\\u633a\\u5e72\\u51c0\\uff0c\\u98ce\\u666f\\u79c0\\u4e3d\\uff0c\\u8212\\u9002\\uff0c\\u552f\\u4e00\\u4e0d\\u8db3\\u7684\\u5c31\\u662f\\u98df\\u7269\\u54c1\\u79cd\\u4e0d\\u591a\\uff0c\\u5473\\u9053\\u4e00\\u822c\", \"date\": \"2023-02-23\", \"score\": 4}, {\"author\": \"u*1\", \"content\": \"\\u73a9\\u7684\\u5f88\\u597d\\uff0c\\u8f66\\u4e0a\\u5f88\\u65b9\\u4fbf\\uff0c\\u770b\\u8d77\\u6765\\u5341\\u5206\\u4e0d\\u9519\", \"date\": \"2023-07-14\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p0/1912/36/36403a427c245524a3.img.jpg_280x200_4b92d997.jpg', '2023-08-03');
