INSERT INTO `app_travelinfo` VALUES ('787', '厦门大学芙蓉湖', '未评价', '5.2', '88', '厦门', '46', '厦门市思明区思明南路422号', '百年柳树下赏黑天鹅', 'https://piao.qunar.com/ticket/detail_521127110.html', '5', '98', '253', '厦大为人熟知的“芙蓉湖”位于厦门大学本部老校区的中央，是整个校园规划的点睛之笔。在厦门大学漳州校区北区也有一湖，名曰“芙蓉湖”，自然是老校区的衍生版本。芙蓉湖的得名和周边建筑芙蓉楼群一样，源自李光前的家乡是南安芙蓉乡，李光前对厦大校园建设作出过重大贡献。厦大衔山含湖，树茂草密，楼宇隐于绿叶之际。厦大有湖，其名芙蓉。芙蓉者，出淤泥而不染。而此芙蓉湖，乃厦大灵秀之源，故以词咏之。', '[\"https://imgs.qunarzz.com/sight/p0/1706/d9/d9dd96b4b2ad988ba3.water.jpg_710x360_5dffe8f8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105i1200089gw2au1626.jpg_710x360_9af42a53.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101e1200089mfv29B73A.jpg_710x360_8a7b3d58.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01042120008aotdbl06EB.jpg_710x360_f9cc3262.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/7a/7a5bf37493ac822ea3.water.jpg_710x360_9b083272.jpg\", \"https://imgs.qunarzz.com/sight/p0/1706/73/73b6dc07931efe81a3.water.jpg_710x360_d2ade49c.jpg\"]', '[{\"author\": \"1*9\", \"content\": \"[cp]\\u901b\\u53a6\\u5927\\u7684\\u65f6\\u5019\\uff0c\\u5728\\u5c55\\u9986\\u7684\\u5496\\u5561\\u5385\\u770b\\u5230\\u4e86\\u4e00\\u4e2a\\u56db\\u5e74\\u7ea7\\u5c0f\\u670b\\u53cb\\u8d34\\u7684\\u4fbf\\u6761\\uff0c\\u4e0a\\u9762\\u5199\\u7740\\u201c\\u957f\\u5927\\u6211\\u8981\\u8003\\u53a6\\u95e8\\u5927\\u5b66\\uff0c\\u7136\\u540e\\u6bcf\\u5929\\u5b66\\u4e60\\u5b8c\\u53bb\\u9f13\\u6d6a\\u5c7f\\u6e38\\u6cf3\\u201d\\u6211\\u5f53\\u65f6\\u770b\\u5b8c\\u5fcd\\u4e0d\\u4f4f\\u7b11\\u4e86\\uff0c\\u4e0d\\u662f\\u5728\\u7b11\\u5c0f\\u670b\\u53cb\\u7684\\u68a6\\u60f3\\u800c\\u662f\\u5728\\u60f3\\u8fd9\\u4e2a\\u5c0f\\u670b\\u53cb\\u7684\\u68a6\\u60f3\\u662f\\u591a\\u4e48\\u5355\\u7eaf\\u554a\\uff0c\\u5c0f\\u65f6\\u5019\\u90fd\\u6709\\u4e2a\\u6e05\\u5317\\u68a6\\uff0c\\u4f46\\u957f\\u5927\\u4e86\\u4e0d\\u5f97\\u4e0d\\u63a5\\u53d7\\u73b0\\u5b9e\\u7684\\u6211\\u5bf9\\u68a6\\u60f3\\u597d\\u50cf\\u6ca1\\u6709\\u5c0f\\u65f6\\u5019\\u7684\\