INSERT INTO `app_travelinfo` VALUES ('788', '金光湖', '3A景区', '3.3', '60', '厦门', '44', '厦门市同安区莲花镇内田村', '闽南的西双版纳', 'https://piao.qunar.com/ticket/detail_3909804658.html', '5', '196', '358', '金光湖位于厦门市同安莲花镇内田村，距同安城区23公里，厦门市区60公里。林区山形如“湖”状，四周六条山岭和两座小山交相环抱，构成“金”字形，旭日初生、叶露晶莹、金光闪闪，故名“金光湖”。 金光湖被誉为“闽南的西双版纳”，林海茫茫，野生动植物品种繁多，不胜枚举，林区总面积7768亩845米。林区古木参天，曲径幽回，身处林海，不仅可以饱览大自然秀丽风光，还可尽情享受“森林氧吧”的无穷乐趣。 区内有国家一级、二级保护植物及其它树林，还有珍稀动物，众多的中草药及自然景观。古木参天，藤萝交缠，灌木丛丛，野草离离，严严实实地覆盖着山地。林区具有很高的科研、旅游价值和丰富的动植物资源。这里有2亿多年前与恐龙同时代的植物“活化石”---刺桫椤，以及稀有的蕨类植物-----观音坐莲，国家一级保护动物-----穿山甲，可供采用的中药-----灵芝草、风鼓草、砂仁、玉桂等，林区还有173科835种较为罕见树种，如国家二级保护植物--，夫妻树一楠，还有千年重阳木、木荷、黄储、桉树、白杨树、九层松、青岗栎、枫、杉等。林中尚有数十种野生经济菌群，组成一个生机盎然的绿色王国。高温季节，金光湖林区内气温低于林外1℃－5℃，低温季节，林内温度高于林外2℃左右，林中含氧量和负离子数高于城市5－7倍，堪称少有的\"天然氧吧\"。游人置身其中，可吸入较多的负离子，享受爽透的森林浴。金光湖是您休闲度假的好去处！', '[\"https://imgs.qunarzz.com/sight/p0/201301/16/da805420caba882393835fbb.jpg_710x360_187652f7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104z120008hq9pzdCF4D.jpg_710x360_3c263441.jpg\", \"https://imgs.qunarzz.com/sight/p0/1604/e5/e53f9b79526cb1c290.water.jpg_710x360_f92ab815.jpg\", \"https://imgs.qunarzz.com/sight/p0/1604/93/933589589c2f168890.water.jpg_710x360_7e07895b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1410/28/cbd2a23ac07503b208d1c892387b32f4.jpg_710x360_08e4c2fa.jpg\", \"https://imgs.qunarzz.com/sight/p0/1410/28/f594306074deaa71a468b870d1d8d461.jpeg_710x360_d95f514c.jpeg\"]', '[{\"author\": \"\\u5de6*\\u80d6\", \"content\": \"\\u975e\\u5e38\\u68d2\\u7684\\u4f53\\u9a8c\\uff0c\\u9884\\u5b9a\\u540e\\u6709\\u5bfc\\u6e38\\u4e3b\\u52a8\\u8054\\u7cfb\\u4e0a\\u8f66\\u5730\\u70b9\\uff0c\\u670d\\u52a1\\u70ed\\u60c5\\uff0c\\u5bfc\\u6e38\\u666f\\u70b9\\u4ecb\\u7ecd\\u8be6\\u7ec6\\uff0c\\u53f8\\u673a\\u4e5f\\u5f88\\u70ed\\u60c5\", \"date\": \"2023-06-03\", \"score\": 5}, {\"author\": \"p*5\", \"content\": \"\\u674e\\u5bfc\\u548c\\u53f8\\u673a\\u4e25\\u5e08\\u5085\\u670d\\u52a1\\u975e\\u5e38\\u5468\\u5230\\uff01\", \"date\": \"2023-06-26\", \"score\": 5}, {\"author\": \"