INSERT INTO `app_travelinfo` VALUES ('789', '厦门五缘湾拓海帆船俱乐部', '未评价', '6.6', '55', '厦门', '49', '厦门市湖里区环岛东路五缘湾游艇港码头', '感受出海的乐趣', 'https://piao.qunar.com/ticket/detail_380287359.html', '5', '39', '328', '拓海帆船俱乐部致力打造＂让水上贵族运动平民化”。跟随专业人士感受大海与天地之间形成的美丽风景线，了解大海航行文化。可自由选择是否打湿衣服，可在船上自由站立拍照。近观翔安，远看金门， 配备专业救生衣、教练全部是全国帆船冠军，获得国际ASA驾驶证书', '[\"https://imgs.qunarzz.com/sight/p0/1605/e7/e7647e4dfcf0a8b390.water.jpg_710x360_da0dbc11.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/22/223be0a5d0e4932e90.water.jpg_710x360_a1f9b2c0.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/f8/f800f1dd856c81da90.water.jpg_710x360_63980654.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/51/518663c2b29cf3f190.water.jpg_710x360_d66f91f0.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/3c/3c7c51a627c949fb90.water.jpg_710x360_e1958d7e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/33/334b8aef2f344ded90.water.jpg_710x360_bbd95e9c.jpg\"]', '[{\"author\": \"\\u5b89*\\u8fdc\", \"content\": \"\\u5bfc\\u6e38\\u670d\\u52a1\\u975e\\u5e38\\u597d\\uff0c\\u5b89\\u6392\\u59a5\\u5f53\\uff0c\\u6293\\u5230\\u597d\\u591a\\u8783\\u87f9\\uff0c\\u5341\\u591a\\u53ea\\uff0c\\u5e06\\u8239\\u770b\\u5230\\u4e86\\u91d1\\u95e8\\u5c9b\", \"date\": \"2023-05-28\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u597d\\u597d\\u770b\\u770b\\u597d\\u597d\\u597d\\u597d\\u597d\\u5427\\uff01\\u8fd9\\u4e48\\u4e9b\\u597d\\u5403\", \"date\": \"2023-07-11\", \"score\": 5}, {\"author\": \"d*1\", \"content\": \"\\u4fbf\\u5b9c\\uff0c\\u8fd8\\u53ef\\u4ee5\\uff01\\u8d76\\u6d77\\u597d\\u73a9\\u5c31\\u662f\\u6709\\u70b9\\u70ed\", \"date\": \"2022-08-08\", \"score\": 5}, {\"author\": \"d*1\", \"content\": \"\\u5bfc\\u6e38\\u4e0d\\u9519\\uff0c\\u4fbf\\u5b9c\\uff0c\\u8d76\\u6d77\\u597d\\u73a9\", \"date\": \"2022-08-08\", \"score\": 5}, {\"author\": \"a*5\", \"content\": \"\\u597d\\u73a9\\uff0c\\u6d77\\u9c9c\\u9910\\u4e5f\\u633a\\u597d\\u5403\\uff0c\\u5c31\\u662f\\u592a\\u70ed\\u4e86\\uff0c\\u5982\\u679c\\u8981\\u4e0a\\u6765\\u5c31\\u66f