INSERT INTO `app_travelinfo` VALUES ('792', '花海乐园', '3A景区', '8.1', '58', '武汉', '49', '武汉市黄陂区双泉村（大余湾景区对面）研梳路木兰花海乐园景区', '以花木兰文化为主题', 'https://piao.qunar.com/ticket/detail_1118446051.html', '4.9', '65', '176', '花海乐园景区坐落于湖北省武汉市黄陂区研子镇木兰景区群，占地面积1500余亩，景区以“花木兰”文化为主题，以一年四季独具特色的梯田生态花卉景观及婚纱摄影为特色，辅以刺激、惊险的互动体验式游乐项目和精彩绝伦的特技表演，组成了一片坡地七彩花田和五大游玩主题区。景区以“花木兰”文化为主题，以一年四季独具特色的梯田生态花卉景观及婚纱摄影为特色，辅以刺激、惊险的互动体验式游乐项目和精彩绝伦的特技表演，组成了一片坡地七彩花田和五大游玩主题区，集特色七彩梯田花海、户外婚纱摄影基地、木兰古战场马战表演、水上嘉年华、农家动物表演、瓜果采摘、大型马戏团表演、大型机械游乐、儿童乐园等众多娱乐项目为一体的综合性生态旅游景区。花海乐园为游客设置了专门的户外烧烤场地，还准备了农家土灶，在夜间伴着篝火晚会，看民间杂耍，看露天电影，大口吃肉，大口喝酒，大声狂笑……体验不一样的休闲生活！', '[\"https://imgs.qunarzz.com/sight/p0/1603/ae/ae9e8cfed1d42dc390.water.jpg_710x360_1534ce4b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010561200088jyvwyAC52.jpg_710x360_ced0003f.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/8d/8d4729c5e4643db690.water.jpg_710x360_3e5f4b00.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/93/930c52b18b602acf90.water.jpg_710x360_42d21d00.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/1b/1b0ff4c1f0a8f96290.water.jpg_710x360_955ab5bd.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/71/7107935bc9216b3390.water.jpg_710x360_766c40ee.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1603/ae/ae9e8cfed1d42dc390.water.jpg_280x200_808aafcc.jpg', '2023-08-03');
