INSERT INTO `app_travelinfo` VALUES ('801', '大连圣亚海洋世界', '4A景区', '8.2', '3650', '大连', '48', '大连市沙河口区中山路608-6-8号（星海广场西500米）', '全景海底通道探索海底世界', 'https://piao.qunar.com/ticket/detail_1375848172.html', '3', '198', '69044', '大连圣亚海洋世界目前在营项目共有海洋世界、极地世界、珊瑚世界、银河星海、厉害塔等五大场馆；并有3大王 牌表演节目：浪漫人鱼童话水秀《海豚湾之恋》、双人双鲸全水下表演《白鲸传奇》、大型人与动物互动表演秀《*夫海象》，2大特色观赏产品包括国家 级南极企鹅种源繁育基地——南极企鹅岛、超IMAX拱形天幕影院《深海乐章》，以及近10000平方米的圣亚奇趣商业街。景区实时跟踪主题公园发展趋势与游客消费体验需求，对景区软硬件不断更新改造，集文创生活、海洋主题餐饮、特色酒店、娱乐休闲体验等为一体的海洋特色“大连的城市客厅”', '[\"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01009120008hmd8e534B5.jpg_710x360_a702c608.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01055120008i60oseE042.jpg_710x360_d908e1d7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105s120008j2orbiD3A4.jpg_710x360_0ffeadfe.jpg\", \"https://imgs.qunarzz.com/sight/p0/1709/76/7691528bc7d7ad3ca3.img.png_710x360_ecfb5e47.png\", \"https://imgs.qunarzz.com/sight/p0/2204/ae/ae5fe20108206ccfa3.water.jpg_710x360_70a4817c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1709/42/426917ba25e5da3ca3.img.png_710x360_78d7a802.png\"]', '[{\"author\": \"a*9\", \"content\": \"\\u53d6\\u7968\\u4f53\\u9a8c\\u7279\\u522b\\u5dee\\uff0c\\u518d\\u79bb\\u666f\\u533a\\u6b65\\u884c\\u5341\\u4e94\\u5206\\u949f\\u7684\\u8ddd\\u79bb\\u3002\\u5927\\u5bb6\\u8d2d\\u4e70\\u7684\\u65f6\\u5019\\u6ce8\\u610f\\u4e00\\u4e0b\\u3002\\u4ef7\\u683c\\u6ca1\\u4f18\\u52bf\\uff0c\\u95e8\\u53e3\\u9ec4\\u725b\\u66f4\\u4fbf\\u5b9c\\u4e9b\\uff0c\\u5efa\\u8bae\\u5230\\u666f\\u533a\\u518d\\u4e70\\u3002\\u666f\\u533a\\u4eba\\u591a\\uff0c\\u7ba1\\u7406\\u4e71\\u3002\\u6309\\u7167\\u5185\\u5bb9\\u548c\\u670d\\u52a1\\u6765\\u8bf4\\uff0c\\u5bf9\\u4e0d\\u8d77\\u7968\\u4ef7\\u3002\\u8fd9\\u4e2a\\u666f\\u70b9\\u513f\\u53ef\\u4ee5\\u53bb\\u73a9\\u4e00\\u6b21\\uff0c\\u800c\\u4e14\\u4ec5\\u9650\\u4e00\\u6b21\\u3002\\u666f\\u533a\\u5185\\u7684\\u505c\\u8f66\\u5382\\u4e0d\\u5bf9\\u5916\\uff0c\\u53ea\\u80fd\\u505c\\u8def\\u8fb9\\u300220\\u5143\\u5c01\\u9876\", \"date\": \"2023-05-25\", \"score\": 3}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u4f53\\u9a8c\\u611f\\u4e0d\\u597d\\uff0c\\u8fdb\\u95e8\\u5c31\\u6392\\u961f\\u4e86\\u56db\\u5341\\u591a\\u5206\\u949f\\uff0c\\u6ca1\\u6709\\u4eba\\u7ef4\\u6301\\u79e9\\u5e8f\\uff0c\\u6709\\u4eba\\u63d2\\u961f\\uff0c\\u800c\\u4e14\\u6ca1\\u6709\\u68da\\u5b50\\u906e\\u6321\\uff0c\\u4e70\\u7684\\u56db\\u9986\\u8054\\u7968\\uff0c\\u9664\\u4e86\\u6d77\\u6d0b\\u4e16\\u754c\\u90a3\\u4e2a\\u5927\\u70b9\\uff0c\\u5176\\u4ed6\\u51e0\\u4e2a\\u9986\\u5f88\\u5c0f\\uff0c\\u6d77\\u6d0b\\u52a8\\u7269\\u8868\\u6f14\\u73af\\u8282\\u8fd8\\u662f\\u5f88\\u4e0d\\u9519\\u7684\\uff0c\\u6bd4\\u8f83\\u9707\\u64bc\", \"date\": \"2023-07-21\", \"score\": 3}]', 'https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01009120008hmd8e534B5.jpg_280x200_c4a53f82.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('802', '东鸡冠山', '4A景区', '5', '28', '大连', '49', '大连市旅顺口区城区的东北部', '保存有日俄战争遗址', 'https://piao.qunar.com/ticket/detail_2416180772.html', '5', '9', '216', '旅顺东鸡冠山有日俄战争时期的是用的，是省级文物保护单位和大连市爱国主义教育基地。此座堡垒是沙俄在1898年3月27日强租旅顺口以后，为了加强陆地防线，由沙俄上校工程师维利奇科于1899年设计，强迫大批中国民工于1900年初开始修建的一座用混凝土鹅卵石浇注而成的永久性堡垒，是沙俄旅顺东部陆上防线的一座重点工事，主要控制北面的开阔地。它主要由堡垒大门、军官室、兵舍、弹药库、电话室、侧防暗堡、外沿坑道、护垒壕、梯形井、暗道、炮阵地、机枪阵地、伙房兼包扎所、暗堡、堡垒后院、木吊桥、散兵壕等构成。西北至西南部、南部为后勤生活设施，东北至东南部为军事作战防御设施。这是一座典型的攻守兼备，以防为主的堡垒。', '[\"https://imgs.qunarzz.com/sight/p0/201402/25/9fdf9e0514427e088cd3cda321882325.jpg_710x360_c8ef4f94.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/d5/d51026143263cce2a3.water.jpg_710x360_ab274247.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/bb/bbe630c410d8de77a3.water.jpg_710x360_f4c844e4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/be/bee3fc3347afe0f1a3.water.jpg_710x360_d6fe49e4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/39/3996411462494e6fa3.water.jpg_710x360_6d59dbf3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/6c/6c631370ddd423b4a3.water.jpg_710x360_401b62f2.jpg\"]', '[{\"author\": \"y*8\", \"content\": \"\\u53f8\\u673a\\u738b\\u5e08\\u5085\\u8bb2\\u89e3\\u8be6\\u7ec6\\uff0c\\u670d\\u52a1\\u5468\\u5230\\uff0c\\u70ed\\u60c5\\u5927\\u65b9\\uff0c\\u975e\\u5e38\\u611f\\u8c22\\u4e00\\u8def\\u4e0a\\u7684\\u7167\\u987e\\u3002\\u5e0c\\u671b\\u516c\\u53f8\\u591a\\u5b89\\u6392\\u738b\\u5e08\\u5085\\u8fd9\\u6837\\u7684\\u53f8\\u673a\\u8ba9\\u6211\\u4eec\\u591a\\u4e86\\u89e3\\u5927\\u8fde\\u3002\", \"date\": \"2022-08-07\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p0/201402/25/9fdf9e0514427e088cd3cda321882325.jpg_280x200_09df1ff5.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('803', '大连现代博物馆', '4A景区', '3', '14', '大连', '50', '大连市沙河口区会展路10号', '解读大连百年城史', 'https://piao.qunar.com/ticket/detail_1646935560.html', '5', '6', '83', '大连现代博物馆坐落于大连星海湾广场西侧，占地21,600平方米，建筑面积30,400平方米，展陈面积15,000平方米，地下一层，设有1,700平方米的停车场、藏品库房、设备间和办公室。地上四层，为大堂和展厅。馆内设有展演厅、学术报告厅、多媒体检索室、咖啡厅和商场等服务设施。四层设有1000平方米的临时展厅。', '[\"https://imgs.qunarzz.com/wugc/p2/201306/06/f7cc123afa4aa8ad93835fbb.jpg_710x360_b0ff8f3e.jpg\", \"https://imgs.qunarzz.com/wugc/p193/201205/31/9e7797db282c523893835fbb.png_710x360_8586f11b.png\", \"https://imgs.qunarzz.com/sight/201312/06/2bd737e46579c72dc8d65eac.jpg_710x360_ed54b762.jpg\", \"https://imgs.qunarzz.com/sight/201312/06/d2c3e5d5e58117fdc8d65eac.jpg_710x360_979c7333.jpg\", \"https://imgs.qunarzz.com/wugc/p76/201306/06/50b308759cad9ccb93835fbb.jpg_710x360_00f48422.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/bba0d95e7b73f337.jpg_710x360_7d8cff2a.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u5bfc\\u6f14\\u8d1f\\u8d23\\uff0c\\u65e0\\u8d2d\\u7269\\uff0c\\u63a8\\u8350\", \"date\": \"2023-05-03\", \"score\": 5}, {\"author\": \"j*6\", \"content\": \"\\u5e26\\u56e2\\u7684\\u5bfc\\u6e38\\u5f88\\u70ed\\u5fc3\\uff0c\\u8d1f\\u8d23\\u662f\\u4e00\\u4e2a\\u975e\\u5e38\\u6709\\u62c5\\u5f53\\uff0c\\u6709\\u8d23\\u4efb\\u5fc3\\u7684\\u5bfc\\u6e38\\u3002\\u8fd9\\u4e2a\\u884c\\u7a0b\\u91cc\\u7684\\u5403\\u4f4f\\u884c\\u90fd\\u5f88\\u6ee1\\u610f\\uff0c\\u671f\\u5f85\\u4e0b\\u4e00\\u6b21\\u8fd8\\u6709\\u673a\\u4f1a\\u62a5\\u8fd9\\u4e2a\\u65c5\\u884c\\u793e\\u7684\\u65c5\\u6e38\\u56e2\", \"date\": \"2023-04-30\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u53f8\\u673a\\u5e08\\u5085\\u5f88\\u597d \\u7eaf\\u73a9 \\u6ca1\\u6709\\u989d\\u5916\\u6d88\\u8d39\", \"date\": \"2023-03-26\", \"score\": 5}, {\"author\": \"s*7\", \"content\": \"\\u65e9\\u4e0a9\\u70b9\\u5230\\u9152\\u5e97\\u6765\\u63a5\\uff0c\\u8dd1\\u4e86\\u597d\\u51e0\\u4e2a\\u666f\\u70b9\\uff0c\\u62cd\\u4e86\\u5