INSERT INTO `app_travelinfo` VALUES ('808', '紫云花汐薰衣草庄园', '4A景区', '7.3', '4', '大连', '49', '大连市金州区相应街道土门子村', '置身数十里香草花海', 'https://piao.qunar.com/ticket/detail_3430675971.html', '4.9', '59', '62', '紫云花汐薰衣草庄园位于大连金州区向应街道土门子村，园区占地面积1100亩，有薰衣草、马鞭草、高雪伦、千日红四大花海，还有欧式的建筑和雕塑，相互搭配，只为全心全意打造超浪漫的气氛。', '[\"https://imgs.qunarzz.com/sight/p0/1804/ad/adbfe0d97d80ba17a3.img.jpg_710x360_65813fcc.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/eb/eb861067cec67934a3.img.png_710x360_8a11b954.png\", \"https://imgs.qunarzz.com/sight/p0/1804/a6/a65996772cf94a05a3.img.jpg_710x360_6a7a24d9.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/6d/6d141e4657a922aa3.img.jpg_710x360_0074189c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/40/40ff1921a4013275a3.img.jpg_710x360_3a5d555f.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1804/ad/adbfe0d97d80ba17a3.img.jpg_280x200_1b60338a.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('809', '冰峪沟', '4A景区', '5', '22', '大连', '40', '大连市庄河市仙人洞镇大连冰峪国家地质公园内', '北方罕见的喀斯特地貌公园', 'https://piao.qunar.com/ticket/detail_4271543841.html', '1', '20', '394', '大连冰峪旅游度假区呈集观光、游览、休闲、度假为一体的山岳型景区。度假区位于辽东半岛南端，大连庄河市北40公里处，全区总面积170多平方公里，中心面积50多平方公里。这里奇特的甲溶岩地貌、天然的青松林风光，国内少有，世界罕见。区内奇嶂林立、沟谷幽深、溪潭澄澈、林茂花繁。有动物200多种、植物上多种，堪称“天然动植物王国”。奇山秀水的有机结合，构成了冰峪独有的自然古朴、山远灵动的风光佳境，有“北方桂林”之美誉。倘佯其中，令人心旷神怡、流连忘返。', '[\"https://imgs.qunarzz.com/wugc/p110/201204/20/a5dc43dd3cc1ae6493835fbb.jpg_710x360_b1bcb9f1.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100m0w000000kgh1p99A9.jpg_710x360_b0fa8c3a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100v0w000000ki4yn11E0.jpg_710x360_756b70b6.jpg\", \"https://imgs.qunarzz.com/sight/201312/04/cebe6ca91a716763c8d65eac.jpg_710x360_90c1c784.jpg\", \"https://imgs.qunarzz.com/sight/201312/04/dbbb7d36da2cfc64c8d65eac.jpg_710x360_fc33d364.jpg\", \"https://imgs.qunarzz.com/wugc/p251/201307/19/0027e446ac116bad93835fbb.jpg_710x360_f5690c96.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u503c\\u4e0d\\u56de\\u7968\\u4ef7\\u7684\\u51b7\\u6e05\\u666f\\u533a\\uff0c\\u666f\\u70b9\\u5f88\\u5c11\\uff0c\\u8bbe\\u65bd\\u8001\\u65e7\\uff0c\\u8def\\u7ebf\\u5f88\\u77ed\\u3002\", \"date\": \"2023-06-25\", \"score\": 2}, {\"author\": \"\\u5b89*5\", \"content\": \"\\u5bfc\\u6e38\\u7528\\u5404\\u79cd\\u624b\\u6bb5\\u548c\\u65b9\\u6cd5\\u8bf1\\u5bfc\\u4f60\\u53bb\\u8d2d\\u4e70\\u56ed\\u533a\\u5185\\u7684\\u6e38\\u73a9\\u5c0f\\u9879\\u76ee\\uff0c\\u800c\\u8fd9\\u4e9b\\u5c0f\\u9879\\u76ee\\uff0c\\u771f\\u7684\\u5c31\\u662f\\u770b\\u5230\\u5c31\\u4f1a\\u540e\\u6094\\uff0c\\u4ed6\\u90fd\\u662f\\u5728\\u51fa\\u53d1\\u65f6\\u7684\\u5927\\u5df4\\u8f66\\u4e0a\\u63a8\\u8350\\u4f60\\u8d2d\\u4e70\\uff0c\\u6240\\u4ee5\\u770b\\u4e0d\\u5230\\u9879\\u76ee\\uff0c\\u88ab\\u4ed6\\u63cf\\u8ff0\\u7684\\u5c0f\\u9879\\u76ee\\u975e\\u5e38\\u7684\\u597d\\uff0c\\u800c\\u6211\\u4eec\\u6e38\\u73a9\\u4e00\\u6b21