INSERT INTO `app_travelinfo` VALUES ('816', '狮子林', '4A景区', '10', '6757', '苏州', '49', '苏州市姑苏区园林路23号', '以假山闻名的苏州名园', 'https://piao.qunar.com/ticket/detail_3534312851.html', '5', '40', '41632', '狮子林为苏州四大名园之一，至今已有650多年的历史。元代至正二年（公元1342年），元末名僧天如禅师维则的弟子\"相率出资，买地结屋，以居其师。因园内“林有竹万固，竹下多怪石，状如狻猊（狮子）者”；又因天如禅师维则得法于浙江天目山狮子岩普应国师中峰，为纪念佛徒衣钵、师承关系，取佛经中狮子座之意，故名“狮子林”。', '[\"https://imgs.qunarzz.com/sight/p0/1802/19/19ea68a3f2b8a9c7a3.img.jpg_710x360_e09b0e36.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102a1200089f1yxcF3D9.jpg_710x360_196fe221.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104y1200087d13qc227D.jpg_710x360_758f6092.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01024120008ak4ebaAA9C.jpg_710x360_692898d6.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01016120008c2pt5fD370.jpg_710x360_8e9f7595.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01004120008c2qfqxEBEB.jpg_710x360_0e4d8d4f.jpg\"]', '[{\"author\": \"\\u5510*\\u660e\", \"content\": \"\\u5237\\u8eab\\u4efd\\u8bc1\\u8fdb\\u53bb\\uff0c\\u633a\\u65b9\\u4fbf\\uff0c\\u8d85\\u7ea7\\u591a\\u4eba\\uff0c\\u7f51\\u7ea2\\u6253\\u5361\\u70b9\\u3002\\u5efa\\u8bae\\u65e9\\u4e0a\\u53bb\\uff0c10\\u70b9\\u524d\\u51fa\\u6765\\u3002\", \"date\": \"2023-07-23\", \"score\": 5}, {\"author\": \"a*0\", \"content\": \"\\u5bfc\\u6e38\\u5927\\u54e5\\u8bb2\\u7684\\u7279\\u522b\\u597d\\uff0c\\u98ce\\u8da3\\u5e7d\\u9ed8\\uff0c\\u5fc5\\u987b\\u63a8\\u8350\\uff0c\\u8046\\u542c\\u7cbe\\u5f69\\u7684\\u8bb2\\u89e3\\uff01\", \"date\": \"2023-06-06\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u4eca\\u5929\\u6765\\u5230\\u5468\\u5e84\\uff0c\\u7531\\u7ae5\\u5bfc\\u505a\\u5bfc\\u6e38\\uff0c\\u6574\\u4e2a\\u65c5\\u7a0b\\u8ba9\\u6211\\u7559\\u4e0b\\u4e86\\u975e\\u5e38\\u6df1\\u523b\\u7684\\u5370\\u8c61\\u3002\\u4e00\\u5f00\\u59cb\\uff0c\\u6211\\u5c31\\u88ab\\u8fd9\\u5ea7\\u53e4\\u9547\\u8ff7\\u4eba\\u7684\\u6