INSERT INTO `app_travelinfo` VALUES ('819', '同里古镇', '5A景区', '8', '3299', '苏州', '48', '苏州市吴江区中川南路1号', '精致柔美的江南水乡', 'https://piao.qunar.com/ticket/detail_3696794349.html', '5', '80', '33010', '同里—— 以“醇正水乡，旧时江南”的特色闻名于海内外。同里古镇风景优美，镇外四面环水，古镇镶嵌于同里、九里、叶泽、南星、庞山五湖之中。镇区被川字形的15条小河分隔成七个小岛，而49座古桥又将小岛串为一个整体。建筑依水而立，以“小桥流水人家”著称，沿岸“屋宇丛密，街巷逶迤”。镇内自成水网，民用水河桥比比皆是，悠悠绿水，水活水清，“家家临水，户户通舟”。形成“水巷小桥多，人家尽枕河”，柳桥通水市，河港入湖田”的独特景观，也是省重点文物保护单位，已列为太湖十三大景区之一。', '[\"https://imgs.qunarzz.com/sight/p0/2005/60/60c020dd5e95629ba3.water.jpg_710x360_1bbe212e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010281200086x1oqt2AF1.jpg_710x360_d169b7c8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010511200086ww9beAED7.jpg_710x360_d06bfb46.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100g1f000001gr0qg177F.jpg_710x360_5ab7b888.jpg\", \"https://imgs.qunarzz.com/sight/p0/2005/c5/c5a670d70bcfd39ba3.water.jpg_710x360_52bebe05.jpg\", \"https://imgs.qunarzz.com/sight/p0/2005/14/1458162e71beae6aa3.water.jpg_710x360_039d0e02.jpg\"]', '[{\"author\": \"\\u5510*\\u660e\", \"content\": \"\\u53d6\\u7968\\u65b9\\u4fbf\\uff0c\\u9760\\u8c31\\uff0c\\u5237\\u8eab\\u4efd\\u8bc1\\u8fdb\\u53bb\\uff0c\\u6e38\\u89c8\\u4e86\\u9000\\u601d\\u56ed\\uff0c\\u4e09\\u4e2a\\u5802\\uff0c\\u4e09\\u5ea7\\u6865\\uff0c\\u8fd8\\u6709\\u4e00\\u4e9b\\u6742\\u4e03\\u6742\\u516b\\u7684\\u666f\\u70b9\\uff0c\\u5929\\u6c14\\u4e0d\\u9519\\uff0c\\u5929\\u5bab\\u4f5c\\u7f8e\\u3002\", \"date\": \"2023-07-24\", \"score\": 5}, {\"author\": \"\\u82b1*3\", \"content\": \"\\u6ca1\\u60f3\\u5230\\u8fd9\\u5bb6\\u9152\\u5e97\\u670d\\u52a1\\u505a\\u5f97\\u8fd9\\u4e48\\u597d\\uff0c\\u5e26\\u8001\\u4eba\\u5b69\\u5b50\\u6765\\u8fd9\\u91cc\\u5ea6\\u5047\\u771f\\u7684\\u4e0d\\u9519\\uff0c\\u5927\\u5802\\u529e\\u7406\\u5165\\u4f4f\\u5f88\\u5feb\\uff0c\\u623f\\u95f4\\u52a0\\u5e8a\\u7684\\u670d\\u52a1\\u5458\\u9648\\u59d0\\u5f88\\u70ed\\u60c5\\uff0c\\u5c0f\\u95ee\\u9898\\u5f88\\u5feb\\u89e3\\u51b3\\uff0c\