INSERT INTO `app_travelinfo` VALUES ('822', '太湖旅游区', '5A景区', '7.9', '20', '苏州', '49', '苏州市吴中区孙武路2013号', '风景秀丽的太湖之滨', 'https://piao.qunar.com/ticket/detail_1610749572.html', '4.9', '7.9', '428', '特色1.吴中区原名吴县，从先秦开始已有2000多年的历史，位于苏州古城南部，濒临太湖。特色2.苏州吴中。特色3.长三角知名滨湖休闲旅游目的地。苏州吴中太湖旅游区位于苏州西南隅的太湖之滨，太湖烟波飘渺的湖景、岛屿上自在悠闲的乡村生活、四季不同的瓜果，吸引了无数苏州当地及周边城市的游客前来放松减压。', '[\"https://imgs.qunarzz.com/sight/p0/1609/cd/cde3b70cbaf8700ca3.water.jpg_710x360_7fdfaadc.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100q1200089gfs08B6F6.jpg_710x360_010fed7c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103l1200086qdo885B78.jpg_710x360_821c6d08.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100m1200089irm2pEA46.jpg_710x360_f4b453f0.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105g12000877vsxyA2A6.jpg_710x360_38a2ef18.jpg\", \"https://imgs.qunarzz.com/sight/p0/1609/a2/a29e007dbf96327aa3.water.jpg_710x360_61311934.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1609/cd/cde3b70cbaf8700ca3.water.jpg_280x200_31851fc9.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('823', '沙家浜', '5A景区', '4.9', '434', '苏州', '47', '苏州市常熟市沙家浜镇芦苇荡路188号', '京剧《沙家浜》发源地', 'https://piao.qunar.com/ticket/detail_3969348563.html', '1', '34.9', '7424', '沙家浜风景区位于秀丽明媚的阳澄湖畔，交通便捷，成为长江三角洲高速公路网中的一个重要节点。全区以“芦花放，稻谷香，岸柳成行”之独特的江南水乡田园风光崛起于旅游市场。交通便捷，东接常昆线至上海八十公里，南达沪宁高速苏州互通40公里。', '[\"https://imgs.qunarzz.com/sight/p0/1802/80/80c0ccee0d2debf9a3.img.jpg_710x360_3c5f863b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01074120008fgt8pj3A0E.jpg_710x360_b7b67a64.jpg\", \"https://imgs.qunarzz.com/sight/p0/1802/55/55398c25d0f72e6ba3.img.jpg_710x360_9f56738a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1802/1e/1ecd0dbe6eee4667a3.img.jpg_710x360_898914e2.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/24/24f8e1542b4c9128a3.water.jpg_710x360_538e25c1.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/38/38aaf88ce741f81da3.water.jpg_710x360_5217f9b6.jpg\"]', '[{\"author\": \"h*1\", \"content\": \"\\u665a\\u4e0a\\u4eba\\u8fd8\\u662f\\u5f88\\u70ed\\u95f9\\u7684\\uff0c\\u8868\\u6f14\\u6709\\u70b9\\u513f\\u5c2c\\uff0c\\u4f46\\u662f\\u62b5\\u4e0d\\u4f4f\\u89c2\\u770b\\u7684\\u70ed\\u60c5\", \"date\": \"2022-08-06\", \"score\": 5}, {\"author\": \"\\u738b*\\u5987\", \"content\": \"\\u5954\\u7740\\u4f60\\u6c99\\u5bb6\\u6d5c\\u7ea2\\u8272\\u9769\\u547d\\u6839\\u636e\\u5730\\u7684\\u81ea\\u7136\\u666f\\u533a\\u53bb\\u7684\\uff0c\\u5927\\u5931\\u6240\\u671b\\uff0c\\u5148\\u8bf4\\u95e8\\u796880+6\\u7535\\u52a8\\u8f66\\u7968\\uff0c\\u6ca1\\u6709\\u770b\\u5230\\u4ec0\\u4e48\\u666f\\u8272\\u5c3180\\u5143\\uff0c\\u91cc\\u9762\\u6240\\u8c13\\u7684\\u6f14\\u51fa\\u90fd\\u4e0d\\u8d85\\u8fc710\\u5206\\u949f.\\u6709\\u4e00\\u4e2a\\u771f\\u6b63\\u7684\\u6f14\\u51fa\\u662f30\\u5206\\u949f\\u7684\\uff0c\\u8fd8\\u9700\\u8981\\u53e6\\u5916\\u4e70\\u796850\\u5143\\uff01\\u91cc\\u9762\\u5c31\\u662f\\u4e00\\u4e2a\\u5927\\u70b9\\u7684\\u6c34\\u6ce1\\u91cc\\u9762\\u957f\\u4e86\\u70b9\\u82a6\\u82c7\\u5b50\\uff01\\u5728\\u8bf4\\u8bf4\\u7535\\u52a8\\u8f66\\