INSERT INTO `app_travelinfo` VALUES ('828', '青岛极地海洋公园', '4A景区', '9.1', '10232', '青岛', '47', '青岛市东海东路60号', '与企鹅等极地动物亲密接触', 'https://piao.qunar.com/ticket/detail_214642947.html', '5', '255.8', '94454', '青岛极地海洋世界是一座现代化极地动物展馆，建筑面积47000平方米。馆内拥有白鲸、企鹅、北极熊、海獭、海象等十余种百余只极地动物，及千余种万余只珍惜的海洋动物，是国际上拥有极地海洋动物品种和数量首屈一指的综合性场馆。馆内共向游客开放了五大展区极地动物展示区、极地童话城堡、神秘的海兽区、精品鱼类区、精彩的海洋动物剧场。', '[\"https://imgs.qunarzz.com/sight/p0/2302/b7/b7f24d5565d2f450a3.water.jpg_710x360_081ef5f5.jpg\", \"https://imgs.qunarzz.com/sight/p0/2302/b7/b7b150d616d305ba3.water.jpg_710x360_aae3681b.jpg\", \"https://imgs.qunarzz.com/sight/p0/2302/89/89551b74e2d53332a3.water.jpg_710x360_f5b8c363.jpg\", \"https://imgs.qunarzz.com/sight/p0/2302/1d/1db5ee4eaf1ee063a3.water.jpg_710x360_219eaa42.jpg\", \"https://imgs.qunarzz.com/sight/p0/2302/c0/c06220a756492fcaa3.water.jpg_710x360_41fa6dc9.jpg\", \"https://imgs.qunarzz.com/sight/p0/2302/b9/b9a341e62017c695a3.water.jpg_710x360_97aa8d72.jpg\"]', '[{\"author\": \"\\u81ea*\\u4e0b\", \"content\": \"\\u516b\\u4e5d\\u5e74\\u524d\\u53bb\\u8fc7\\u4e00\\u6b21\\uff0c\\u5f53\\u65f6\\u53ea\\u53bb\\u7684\\u6781\\u5730\\u6d77\\u6d0b\\u9986\\uff0c\\u8fd9\\u6b21\\u4e94\\u4e2a\\u9986\\u4e00\\u4e2a\\u6ca1\\u843d\\uff0c\\u5c24\\u5176\\u662f\\u7403\\u5e55\\u9986\\u3001\\u6d77\\u5e73\\u7ebf\\u5267\\u573a\\u3001\\u51b0\\u96ea\\u4e50\\u56ed\\u5f88\\u60ca\\u8273\\uff0c\\u5b69\\u5b50\\u5f88\\u559c\\u6b22\\u770b\\u5f88\\u559c\\u6b22\\u73a9\\u3002\\u867d\\u7136\\u573a\\u9986\\u6e38\\u73a9\\u4e09\\u4e2a\\u5c0f\\u65f6\\uff0c\\u518d\\u52a0\\u4e0a\\u4e2d\\u5348\\u5403\\u996d\\u4e00\\u5c0f\\u65f6\\uff0c\\u4e00\\u5171\\u56db\\u4e2a\\u5c0f\\u65f6\\uff0c\\u65f6\\u95f4\\u4e0d\\u957f\\u4f46\\u73a9\\u7684\\u5f88\\u55e8\\u5f88\\u5c3d\\u5174\\u3002\\u552f\\u4e00\\u5410\\u69fd\\u7684\\u5c31\\u662f\\u505c\\u8f66\\u573a\\u6536\\u8d39\\uff0c\\u4e00\\u4e2a\\u5c0f\\u65f6\\u5341\\u5757\\u94b1\\uff0c\\u5149\\u505c\\u8f66\\u8d3945\\u5143\\uff0c\\u5728\\u91cc\\u9762\\u6d88\\u8d39\\u4e86\\u4e0a\\u5343\\u5757\\u94b1\\uff0c\\u53