INSERT INTO `app_travelinfo` VALUES ('831', '青岛海滨', '4A景区', '10', '87', '青岛', '49', '青岛市京山路11号', '纵享漫长海岸风光', 'https://piao.qunar.com/ticket/detail_1091334227.html', '4.9', '138', '26', '青岛海滨风景区位于青岛市区南部沿海一线，东西长约25公里，南北宽约3公里。陆地包括青岛山、信号山、观象山、太平山、八关山、小鱼山及南侧区域。海域则有团岛湾、青岛湾、汇泉湾、太平湾、浮山湾等海域及所含岛、礁、海滩等。主要风景游览区有八大关景区、八大峡景区、太平山景区和太平角景区。主要风景点有栈桥、鲁迅公园、小青岛、小鱼山、百花苑、汇泉广场、五四广场、音乐广场、燕儿岛公园、二、三、六海水浴场及海滨观光大道――东海路、香港路等。', '[\"https://imgs.qunarzz.com/sight/p0/1409/16/63aafa83df9fb5f5115422e114bcb6f1.jpg_710x360_c0c895d0.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103z120008c4fxaoCFF4.jpg_710x360_8e207754.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100h1200086ynfcn2986.jpg_710x360_9a9479cc.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103p120008c4bxak8F56.jpg_710x360_83d32105.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01016120008c4bp21F36A.jpg_710x360_e267b0c5.jpg\", \"https://imgs.qunarzz.com/sight/p0/1409/16/e203606d796c85c01fcd87f29b7c7125.jpg_710x360_05915e5e.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1409/16/63aafa83df9fb5f5115422e114bcb6f1.jpg_280x200_20c60982.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('832', '青岛方特梦幻王国', '4A景区', '10', '2799', '青岛', '49', '青岛市城阳区红岛街道岙东南路（胶州湾大桥红岛出口即达）', '玩转东方文化主题乐园', 'https://piao.qunar.com/ticket/detail_3738285627.html', '5', '280', '23197', '青岛方特梦幻王国位于美丽的青岛市城阳区红岛海滨，占地约70万平方米，由深圳华强集团投资20亿元兴建，以高科技互动体验营造梦幻的感受。方特梦幻王国采用国际一流的理念和技术精心打造，被誉为“东方梦幻乐园”。青岛方特梦幻王国由飞越极限、生命之光、崂山道士、水漫金山、宇宙博览会、秦陵历险、猴王、逃出恐龙岛、唐古拉雪山、火流星、嘟比脱口秀、小鸡不好惹、欢乐天地、童梦天地等10多个主题项目区组成，包含主题项目、游乐项目、休闲及景观项目200余项。', '[\"https://imgs.qunarzz.com/sight/p0/201301/16/712d14a7f8c9868d93835fbb.png_710x360_6577e136.png\", \"https://imgs.qunarzz.com/sight/p0/1412/2f/595a3c7ee5250ab06a70dbf419053086.water.jpg_710x360_8907da90.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/23/17d2bc9f54533a6a2dbd9e9fb3647f14.water.jpg_710x360_227b5b86.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/ce/5e68341da04753eea2f64dc90c2aefb3.water.jpg_710x360_63018ce4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/25/d8d3ded84a08922dc92fc1414dccc823.water.jpg_710x360_a97c2c69.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/aa/e0964d5de0d39e7eafa826a4c423ea71.water.jpg_710x360_96e5c3ef.jpg\"]', '[{\"author\": \"\\u4e39*\\u4e39\", \"content\": \"\\u98ce\\u666f\\u5f88\\u7f8e\\uff0c\\u5c71\\u6d77\\u76f8\\u8fde\\uff0c\\u5c71\\u5149\\u6d77\\u8272\\uff0c\\u6b63\\u662f\\u5d02\\u5c71\\u98ce\\u666f\\u7684\\u7279\\u8272\\u3002\\u5728\\u5168\\u56fd\\u7684\\u540d\\u5c71\\u4e2d\\uff0c\\u552f\\u6709\\u5d02\\u5c71\\u662f\\u5728\\u6d77\\u8fb9\\u62d4\\u5730\\u5d1b\\u8d77\\u7684\\u3002 \\u5f53\\u4f60\\u6f2b\\u6b65\\u5728\\u5d02\\u5c71\\u7684\\u9752\\u77f3\\u677f\\u5c0f\\u8def\\u4e0a\\uff0c\\u4e00\\u8fb9\\u662f\\u78a7\\u6d77\\u8fde\\u5929\\uff0c\\u60ca\\u6d9b\\u62cd\\u5cb8\\uff1b\\u53e6\\u4e00\\u8fb9\\u662f\\u9752\\u677e\\u602a\\u77f3\\uff0c\\u90c1\\u90c1\\u8471\\u8471\\uff0c\\u4f60\\u4f1a\\u611f\\u5230\\u5fc3\\u80f8\\u5f00\\u9614\\uff0c\\u6c14\\u8212\\u795e\\u723d\\u3002\", \"date\": \"2022-08-12\", \"score\": 5}, {\"author\": \"\\u5b81*\\u5c81\", \"content\": \"\\u4eca\\u5929\\u90a2\\u5bfc\\u5e26\\u96