INSERT INTO `app_travelinfo` VALUES ('833', '青岛滨海学院世界动物标本艺术馆', '4A景区', '8.6', '12', '青岛', '47', '青岛市黄岛区嘉陵江西路425号', '滨海学院内的综合性展馆', 'https://piao.qunar.com/ticket/detail_73527023.html', '4.7', '69', '169', '青岛滨海学院世界动物标本艺术馆是“国家AAAA级旅游景区”，“山东省四星级科普教育基地”，“青岛市生态科普教育基地””。艺术馆位于滨海学院校园内，一年四季如春，内设宇宙演化展区、动物标本展区、古生物化石展区和油画展区等。动物标本展区拥有非洲象、非洲狮、长颈鹿、北极熊等1000余件珍稀动物及鸟类标本。该展区依据选材和布局的搭配，营造出形态各异的动物生存、成长环境。极地冰川、温带季风、热带雨林、稀树草原等应有尽有，“兽有山野森林、鸟有湿地绿荫”，是您休闲、娱乐、与大自然亲密接触的好去处。宇宙演化展区充分利用声、光、电等技术探索宇宙的奥秘，追寻人类起源，精心打造成集知识性、趣味性、参与性于一体的自然生态景观。此馆建设的目的是普及自然科学知识，珍视野生动物资源，推动生态环境保护，加强环境道德教育。', '[\"https://imgs.qunarzz.com/sight/p0/201407/23/c828b65bb0c357e6e8b242a49efcdf6b.jpg_710x360_76b0eb1f.jpg\", \"https://imgs.qunarzz.com/sight/p0/201407/23/1471148f3ddf6ea591d42756fc7b1fec.jpg_710x360_4e3c2caf.jpg\", \"https://imgs.qunarzz.com/sight/p0/201407/23/7d97fd15419852aa42c6f3a52fe03b4a.jpg_710x360_310b20c2.jpg\", \"https://imgs.qunarzz.com/sight/p0/201407/23/66e31c8c7fd1f9e90668f3bfe4499211.jpg_710x360_38fc2df8.jpg\", \"https://imgs.qunarzz.com/sight/p0/201407/23/b620294f98bbb6340eadec5f7be7ae7a.jpg_710x360_5e573b94.jpg\", \"https://imgs.qunarzz.com/sight/p0/201407/23/b5157114c9c50f6dc1d6f2243d0935db.jpg_710x360_16b3fa02.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/201407/23/c828b65bb0c357e6e8b242a49efcdf6b.jpg_280x200_808e580d.jpg', '2023-08-03');
