INSERT INTO `app_travelinfo` VALUES ('841', '蓟洲彩虹谷', '3A景区', '4.7', '28', '天津', '50', '天津市蓟县罗庄子镇洪水庄村西', '开启一场浪漫之旅', 'https://piao.qunar.com/ticket/detail_696294395.html', '5.0', '38', '235', '蓟洲国际彩虹谷景区坐落于蓟县城北七公里（蓟洲国际滑雪场内），四面环山，果树成荫，景色宜人，天然氧吧；它是集吃、住、游、购、娱为一体的综合性四季度假景区，冬季可戏雪、滑雪，夏季可避暑、观虹；此地主要分为三大主题园，分别是：爱情园、恐龙园和彩虹谷。', '[\"https://imgs.qunarzz.com/sight/p0/1611/72/72d90a4948f9186fa3.water.jpg_710x360_f28a8847.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/e5/e568c05c6a10508da3.water.jpg_710x360_38630e4d.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/eb/ebc9eee3e1f5812ba3.water.jpg_710x360_50dc7eb7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/97/979b120755a188fea3.water.jpg_710x360_a58f44f4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/91/91a03bb585a121a3.water.jpg_710x360_8236161a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1611/d6/d63e8cfb9b57fc3ba3.water.jpg_710x360_61721532.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1611/72/72d90a4948f9186fa3.water.jpg_280x200_f4300c55.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('842', '天津方特欢乐世界', '4A景区', '10', '2178', '天津', '49', '天津市滨海新区中生大道4888号', '互动式体验游乐项目', 'https://piao.qunar.com/ticket/detail_728251717.html', '5', '280', '18922', '天津方特欢乐世界坐落于天津滨海新区中新天津生态城生态岛内，是京津冀地区首家以“科幻”为主题的高科技公园。区别于国内其他以机械游乐设施为主的传统主题公园概念，融合3D、4D技术，以大型高科技展示项目为主要表现形式，将推出20余个大型主题项目区，包含科技动漫、未来幻想、科普教育、神话传说、主题表演等多个方面。《欢乐天地》是由多款经典机械类项目组成的游乐区，包括双层转马、旋转小蜜蜂、儿童爬山车、果虫滑车、转转杯等……是童年的乐园，缤彩纷呈，让大小朋友尽享欢乐情怀与缤纷乐趣！方特城堡是大型儿童参与式体验项目。在这儿，您不仅能现场观看生动有趣的手偶剧表演，还能够亲身感受虚拟仿真、多媒体互动等妙趣横生的游艺技术，淘气堡、小滑梯、充气屋、卡通秀等玩乐设施装点成的童梦天地，更深得小朋友和家长们的喜爱。熊出没历险是动感射击类游戏项目。乘坐载有发射装置的卡通游览车，行进在多角度、多方位的缤纷场景和奇异世界里，随乖巧可爱的熊大、熊二，与光头强和各种憨态可掬的小怪物们战斗。波浪翻滚是模拟船只在大海航行、遭遇风暴时的情景设计的体验项目。船体被猛地抛向高空，继而不断翻滚、摇摆、颠簸，带给您乘风破浪的奇妙感觉。', '[\"https://imgs.qunarzz.com/sight/p0/2001/da/da683895d3a6c6aea3.img.jpg_710x360_d2da4413.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01034120008jc7sfi34CE.jpg_710x360_02994d4d.jpg\", \"https://imgs.qunarzz.com/sight/p0/1711/74/746c8484c3dfe4dca3.water.jpg_710x360_f4e1844c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1711/4b/4b802bff6ee9df69a3.water.jpg_710x360_0b8f615a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1711/15/15567406f6274cf7a3.water.jpg_710x360_5c2f3c47.jpg\", \"https://imgs.qunarzz.com/sight/p0/1711/10/10dbe56e6fda9d06a3.water.jpg_710x360_7bf4beaa.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u8fd9\\u4e2a\\u65cb\\u8f6c\\u6728\\u9a6c\\u6709\\u7740\\u591a\\u5f69\\u7684\\u706f\\u5149\\u548c\\u8ff7\\u4eba\\u7684\\u97f3\\u4e50\\uff0c\\u8ba9\\u4f60\\u4eff\\u4f5b\\u7f6e\\u8eab\\u4e8e\\u68a6\\u5e7b\\u822c\\u7684\\u4ed9\\u5883\\u3002\\u800c\\u4e14\\uff0c\\u5b83\\u7684\\u901f\\u5ea6\\u548c\\u65cb\\u8f6c\\u89d2\\u5ea6\\u90fd\\u975e\\u5e38\\u9002\\u4e2d\\uff0c\\u65e2\\u5145\\u6ee1\\u4e86\\u523a\\u6fc0\\u611f\\uff0c\\u53c8\\u4e0d\\u4f1a\\u8ba9\\u4eba\\u611f\\u5230\\u6655\\u7729\\u3002\\ud83c\\udf08\\u2728\\u65e0\\u8bba\\u662f\\u4e0e\\u670b\\u53cb\\u3001\\u5bb6\\u4eba\\u4e00\\u8d77\\u73a9\\uff0c\\u8fd8\\u662f\\u72ec\\u81ea\\u4e00\\u4eba\\