INSERT INTO `app_travelinfo` VALUES ('843', '天津盘山风景名胜区', '5A景区', '9.5', '350', '天津', '48', '天津市蓟县官庄镇莲花岭村', '山势秀美被乾隆皇帝所喜爱', 'https://piao.qunar.com/ticket/detail_2140799835.html', '5', '74.5', '8788', '盘山风景区，位于天津蓟县城区西北，该景区史记于汉，盛建于唐，极于清，是自然山水与名胜古迹并著，佛家寺院与皇家园林共称的旅游胜地，历史上众多的帝王将相，文人墨客竞游于此，清乾隆皇帝，先后来到盘山32次，留下了歌咏盘山的诗作1366首，并发出了“早知有盘山，何必下江南”的感叹。据记载，盘山名胜计有十峰、八岭、三盘、五台、八峪、九岩、十一洞、二十六名石、一淀、八泉、三井、五桥、四沟、二潭、五地、一塘、百余座塔、四亭、二轩、七十二寺、庙、庵等数百处景点。有些人文古迹虽经自然风化、侵华日军“三光政策”的毁损和“文化大革命”的破坏，现在只留下残迹遗址，但不少古迹仍保存完好。天成寺、古佛舍利塔、定光佛舍利塔、进士墓、石塔林、千佛像、契真洞石窟、摩崖石刻等等，实物俱在。近几年由于天津市政府的重视，又重新修复了一些古迹。自然景观中，如奇峰怪石、崖洞、古树、瀑布、泉池等等，更是历历在目。按照自然景物分布的特色，古人把盘山分为上、中、下、三盘。自来峰一带为上盘，劲松苍翠，蟠曲遮天，是为松胜；古中盘一带为中盘，怪石嵯峨，是为石胜；晾甲石一带为下盘，万泉响涧，是为水胜。以上三胜统称为三盘胜境。', '[\"https://imgs.qunarzz.com/sight/p0/2005/b6/b6f4427ab902210a3.water.jpg_710x360_322cc59e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106d1200087crt4a5E5E.jpg_710x360_25f44d7c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106u1200087dn8bpA247.jpg_710x360_2c44f457.jpg\", \"https://imgs.qunarzz.com/sight/p0/1511/a8/a8f4adb1d885100f90.water.jpg_710x360_39c76070.jpg\", \"https://imgs.qunarzz.com/sight/p0/1509/6a/6a40f43c4fb934bc.img.jpg_710x360_9f9108c1.jpg\", \"https://imgs.qunarzz.com/sight/p0/1509/42/4242002387903882.img.jpg_710x360_0aae7763.jpg\"]', '[{\"author\": \"k*p\", \"content\": \"\\u5bfc\\u6e38\\u670d\\u52a1\\u597d\\uff0c\\u8bb2\\u89e3\\u6e05\\u6670\\u3002\\u5927\\u5df4\\u8f66\\u8212\\u9002\\uff0c\\u6e38\\u73a9\\u65f6\\u95f4\\u5b89\\u6392\\u5408\\u7406\\uff0c\\u4e0d\\u9519\\uff01\", \"date\": \"2023-06-03\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u6765\\u76d8\\u5c71\\u4e00\\u65e5\\u6e38\\uff0c\\u73a9\\u7684\\u5f88\\u5f00\\u5fc3\\uff0c\\u722c\\u7684\\u5f88\\u5c3d\\u5174\\uff0c\\u738b\\u59d0\\u7684\\u884c\\u7a0b\\u89c4\\u5212\\u7684\\u5408\\u7406\\uff0c\\u4e3a\\u6211\\u4eec\\u8282\\u7701\\u4e86\\u4e0d\\u5c11\\u65f6\\u95f4\\uff0c\\u5e0c\\u671b\\u4e0b\\u6b21\\u6765\\u51fa\\u6e38\\u80fd\\u518d\\u6b2