INSERT INTO `app_travelinfo` VALUES ('848', '睦南公园', '未评价', '10', '85', '天津', '50', '天津市和平区睦南道', '绿植众多、花繁叶茂', 'https://piao.qunar.com/ticket/detail_1675183869.html', '5.0', '30', '1', '睦南公园是座落在和平区的一处街心公园。每到五月，满园的月季花和其他各种鲜花盛开；多种大小树木繁茂；除了行道、广场，大多是绿油油的草地，还有一座喷泉、一亭、一廊。再加上周围各式“天津小洋楼”的陪衬，风景非常优美。', '[\"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102s120008ix0yuh4FEC.jpg_710x360_5872cb39.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01013120008je0udu3CD3.jpg_710x360_378c7e9e.jpg\"]', '[]', 'https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102s120008ix0yuh4FEC.jpg_280x200_8dabb27b.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('849', '平津战役纪念馆', '4A景区', '5', '24', '天津', '50', '天津市红桥区平津道8号', '了解平津战役历史的好地方', 'https://piao.qunar.com/ticket/detail_3234880200.html', '5.0', '9', '22', '由多维演示馆、纪念广场、胜利花园、序厅、战役决策厅、战役实施厅、人民支前厅、伟大胜利厅、英烈业绩厅等组成。', '[\"https://imgs.qunarzz.com/sight/p0/201312/04/f4614a9271cabfc7614c1708e095964d.jpg_710x360_8d5682e0.jpg\", \"https://imgs.qunarzz.com/sight/p0/201312/04/12067dfa056e977d0572d3aad80bc920.jpg_710x360_447a4a29.jpg\", \"https://imgs.qunarzz.com/sight/p0/201312/04/972a57a26b87960cedf423ba0ae895cb.jpg_710x360_ce043e79.jpg\", \"https://imgs.qunarzz.com/sight/p0/201312/04/3d40563a3bbec7b486d8c0d4efaba043.jpg_710x360_b14f6739.jpg\", \"https://imgs.qunarzz.com/sight/p0/201312/04/8c13ffe1c949f01614506ffa987cd846.jpg_710x360_180935f8.jpg\", \"https://imgs.qunarzz.com/sight/p0/201312/04/8d4ed75229eae36b45980e69ae4861cc.jpg_710x360_2748609e.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/201312/04/f4614a9271cabfc7614c1708e095964d.jpg_280x200_0121811c.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('850', '牛首山文化旅游区', '4A景区', '8.9', '19256', '南京', '50', '南京市江宁区宁丹大道18号', '朝拜礼佛的千年道场', 'https://piao.qunar.com/ticket/detail_1706838119.html', '5', '88', '56449', '牛首山又名天阙山，是金陵四大名胜之一，因山顶东西双峰形似牛头双角而得名。一座牛首山，半部南京史。牛首山风光秀美，素有“春牛首”之美誉，古有牛首烟岚、献花清兴、祖堂振锡等金陵美景，还遗存了很多历史古迹，如岳飞抗金故垒、摩崖石刻、弘觉寺塔、郑和文化园等，吸引无数帝王将相、文人墨客在此修身养性、品茗作赋，留下诗词400多首。著名景点 佛顶宫、佛顶塔、佛顶寺、牛头禅文化园（弘觉寺塔、明代摩崖石刻）、郑和文化园、岳飞抗金故垒、隐龙湖、禅林路景观区。', '[\"https://imgs.qunarzz.com/sight/p0/1601/d4/d4f1a62140b9d93590.img.jpg_710x360_50c630ce.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102g1200084mo8ipC7BB.jpg_710x360_07ed2fe2.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010421200084dna8z2806.jpg_710x360_66814d2f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102x1200084c7fhl547E.jpg_710x360_7a6657f1.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105n1200085c5fb2EFD9.jpg_710x360_6971c10e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1601/90/90b6af37f85ee47590.img.jpg_710x360_a97e57b3.jpg\"]', '[{\"author\": \"\\u7ea2*\\u8713\", \"content\": \"\\u4e00\\u5ea7\\u725b\\u9996\\u5c71\\uff0c\\u534a\\u90e8\\u91d1\\u9675\\u53f2\\u3002\\u975e\\u5e38\\u503c\\u5f97\\u4e00\\u53bb\\u3002\\u6211\\u4eec\\u662f\\u6253\\u8f66\\u5230\\u897f\\u95e8\\uff0c\\u5e02\\u533a\\u6253\\u8f66\\u53bb\\u5f88\\u65b9\\u4fbf\\uff0c\\u53ea\\u898117\\u5143\\u3002\\u65e9\\u4e0a\\u53bb\\u6700\\u597d\\uff0c\\u4e0b\\u5348\\u4eba\\u5f88\\u591a\\u3002\\u897f\\u95e8\\u662f\\u5728\\u5c71\\u8170\\uff0c\\u4e0b\\u8f66\\u540e\\uff0c\\u6b65\\u884c\\u5927\\u7ea6\\u5341\\u5206\\u949f\\u5c31\\u5230\\u4f5b\\u9876\\u5bab\\u3002\\u5982\\u679c\\u4ece\\u4e1c\\u95e8\\u5165\\u53ef\\u4ee5\\u5750\\u5927\\u5df4\\u76f4\\u63a5\\u5230\\u4e00\\u9876\\u5bab\\uff0c\\u5927\\u5df410\\u5143\\u3002\\u5b8f\\u4f1f\\u58ee\\u89c2\\uff0c\\u91cc\\u9762\\u8bbe\\u65bd\\u5f88\\u597d\\uff0c\\u4e0d\\u7528\\u722c\\u697c\\u68af\\uff0c\\u5168\\u7a0b\\u624b\\u6276\\u7535\\u68af\\u4e0a