INSERT INTO `app_travelinfo` VALUES ('853', '红山森林动物园', '4A景区', '10', '5508', '南京', '49', '江苏省南京市玄武区和燕路168号', '看镇园之宝澳洲考拉', 'https://piao.qunar.com/ticket/detail_404704522.html', '5', '40', '27689', '红山森林动物园由南京玄武湖动物园迁移到此而建。现已建成小红山鸟类区、大红山猛兽区、放牛山食草动物和灵长动物三大展区,39个设计新颖的动物场馆依山营建，错落山间。狮虎区，猩猩、狒狒展区,熊山,大象馆,温顺可爱的大、小熊猫、松鼠猴、火烈鸟热带雨林馆、科普馆、两栖馆、水禽馆等聚集了大自然的万种风情。大型动物表演场、儿童乐园等游乐设施,使游人可在游览之余尽情嬉戏。红山森林动物园以独特的森林和动物特色，展示了无穷魅力，已成为南京的又一处旅游热点。', '[\"https://imgs.qunarzz.com/sight/201312/03/38da27f8a2ad319ec8d65eac.jpg_710x360_b632f6ad.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01048120008g5dcwa807F.jpg_710x360_2c8fc65c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105t120008fe3n6c0DBF.jpg_710x360_72e63315.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100z120008g5d9e9DAFB.jpg_710x360_7aeeb13e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101n120008exh66u6E7C.jpg_710x360_c8d04ee7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100i120008eys7ug0728.jpg_710x360_c6641120.jpg\"]', '[{\"author\": \"g*5\", \"content\": \"\\u548c\\u95fa\\u871c\\u4ece\\u5f00\\u56ed\\u73a9\\u5230\\u95ed\\u56ed\\uff0c\\u6bcf\\u4e2a\\u53ef\\u7231\\u7684\\u5c0f\\u52a8\\u7269\\u90fd\\u53bb\\u770b\\u5566\\uff0c\\u6700\\u60f3\\u8bf4\\u7684\\u5c31\\u662f\\u611f\\u8c22\\u7ea2\\u5c71\\u68ee\\u6797\\u52a8\\u7269\\u56ed\\u56ed\\u957f\\uff0c\\u771f\\u7684\\u662f\\u5584\\u5f85\\u6bcf\\u4e00\\u4e2a\\u5c0f\\u751f\\u547d\\uff0c\\u8ba9\\u5b83\\u4eec\\u771f\\u6b63\\u610f\\u4e49\\u4e0a\\u5b9e\\u73b0\\u81ea\\u7531\\uff0c\\u800c\\u4e14\\u6ca1\\u6709\\u50cf\\u5176\\u4ed6\\u52a8\\u7269\\u56ed\\u6bcf\\u6bcf\\u4e00\\u4e2a\\u9986\\u90fd\\u4f1a\\u552e\\u5356\\u6295\\u5582\\u7684\\u98df\\u7269\\uff0c\\u5728\\u8fd9\\u91cc\\u5b8c\\u5168\\u4e0d\\u5b58\\u5728\\uff0c\\u4e5f\\u662f\\u771f\\u6b63\\u7684\\u4fdd\\u62a4\\u4e86\\u5b83\\u4eec\\u2026\\u2026\\u5f88\\u5f00\\u5fc3\\uff0c\\u4e00\\u5b9a\\u8fd8\\u4f1a\\u518d\\u53bb\\u6