INSERT INTO `app_travelinfo` VALUES ('854', '钟山风景区', '5A景区', '5', '4924', '南京', '49', '南京市玄武区石象路7号', '南京地标景区', 'https://piao.qunar.com/ticket/detail_3793771320.html', '5', '0.5', '22367', '钟山风景名胜区蜿蜒起伏，宛如游龙，包括紫金山、明代城垣、玄武湖，山、湖的连接地带以及环湖的富贵山、覆舟山、鸡笼山等若干低丘和城垣、城堡。自钟山远眺，玄武湖五洲烟柳，十里春风，燕雀湖波光墙影，紫霞湖林海明珠，尽收眼底。使人心旷神怡。富贵山、覆舟山、鸡笼山秀峰塔影，林木葱郁。自然生成的山林野趣，加上匠心独运的精美建筑，使得钟山风景区成为自然美与人文美紧密结合的上乘佳作。', '[\"https://imgs.qunarzz.com/sight/p0/1603/4e/4ec01cc307848a5890.water.jpg_710x360_30389b38.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100b1f000001gso2vEBE4.jpg_710x360_4c895126.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010381200087eer63C010.jpg_710x360_3b1a1fd7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1502/d3/d3f607a0fe500d07.water.png_710x360_cfec4ac4.png\", \"https://imgs.qunarzz.com/sight/p0/1412/be/7bdbf26b7e95f0b63b72a48d5fb58dc1.water.jpg_710x360_6331c05e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1502/3f/3fc32196f0a90785.water.jpg_710x360_55e0ae2c.jpg\"]', '[{\"author\": \"l*1\", \"content\": \"\\u4e70\\u7684\\u56db\\u4e2a\\u666f\\u533a\\u5957\\u7968\\uff0c\\u53ea\\u53bb\\u4e86\\u4e09\\u4e2a\\u3002\\u660e\\u5b5d\\u9675\\u7684\\u795e\\u9053\\u4e00\\u76f4\\u5411\\u5f80\\uff0c\\u6ca1\\u6709\\u8ba9\\u6211\\u5931\\u671b\\u3002\\u7f8e\\u9f84\\u5bab\\u548c\\u7075\\u8c37\\u666f\\u533a\\u8ddd\\u79bb\\u8fdc\\uff0c\\u62fc\\u8f6610\\u5143\\u4e00\\u4eba\\uff0c\\u56db\\u4eba\\u4e00\\u8f66\\u6bd4\\u8f83\\u5212\\u7b97\\u3002\\u666f\\u533a\\u7684\\u8f6620\\u4e00\\u4eba\\uff0c\\u8fd8\\u5f97\\u6362\\u4e58\\u3002\\u53bb\\u7684\\u592a\\u665a\\uff0c\\u5230\\u7075\\u8c37\\u666f\\u533a\\u5df2\\u7ecf\\u4e94\\u70b9\\u949f\\u3002\\u542c\\u8bf4\\u7075\\u8c37\\u5854\\u4e94\\u70b9\\u534a\\u5c31\\u4e0d\\u80fd\\u8fdb\\u4e86\\uff0c\\u8d76\\u7d27\\u5f80\\u4e0a\\u722c\\uff0c\\u8d76\\u4e0a\\u4e86\\u65e5\\u843d\\uff0c\\u5f88\\u7f8e\\u3002\\u7ed3\\u679c\\u4e0b\\u6765\\u770b\\u65e0\\u6881\\u6bbf\\uff0c\\u6dde\\u6caa\\u4f1a\\u6218\\u70c8\\u58eb\\u7eaa\\u5ff5\\u7891\\u7