INSERT INTO `app_travelinfo` VALUES ('871', '银子岩景区', '4A景区', '8.3', '4777', '桂林', '46', '桂林市荔浦县马岭镇马岭镇沿321国道向北10公里(十里画廊景区)银子岩景区', '惟妙惟肖的钟乳石群', 'https://piao.qunar.com/ticket/detail_9482346.html', '5', '54', '21990', '银子岩旅游景区位于桂林市荔浦县马岭镇，距桂林市中心85公里。银子岩景区四周群山环抱，俊秀的小青山、朝寨山拔地而起，耸立于碧波荡漾的青湖之滨。景区内花香、果香、稻香四处溢香，湖光山色交相辉映，牛悠马闲，鸡犬相闻，呈现一派迷人的田园风光。山青、水秀、洞奇、石美，享有“桂林山水代表”之美誉。', '[\"https://imgs.qunarzz.com/sight/p0/201403/07/a47369acdec386e923b3713c2c8b9b21.jpg_710x360_b3176e8a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100610000000pby57668D.jpg_710x360_7464c74e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100810000000pbissC27F.jpg_710x360_e18d26d8.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/24/0809aa01c1d530e5161ef65146c5d4b9.jpg_710x360_2e330587.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/24/07659aabdef8a58632aa24852bd5bc66.jpg_710x360_bfb122df.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/24/1cb42f718142003da40eb9069feb17f4.jpg_710x360_99945dc9.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u5728\\u8be5\\u5e73\\u53f0\\u4e0a\\u8d2d\\u4e70\\u7684\\u5957\\u7968\\uff0c\\u975e\\u5e38\\u4f18\\u60e0\\uff0c\\u4e0b\\u5355\\u540e\\u5de5\\u4f5c\\u4eba\\u5458\\u4f1a\\u7535\\u8bdd\\u8054\\u7cfb\\u770b\\u6f14\\u51fa\\u7684\\u65f6\\u95f4\\uff0c\\u6211\\u4eec\\u9009\\u4e86\\u7b2c\\u4e00\\u573a\\uff0c\\u5370\\u8c61\\u5218\\u4e09\\u59d0\\u7684\\u5ea7\\u4f4d\\u53f7\\u5728\\u6700\\u524d\\u9762\\u51e0\\u6392\\uff0c\\u4f4d\\u7f6e\\u5f88\\u597d\\uff0c\\u89c6\\u91ce\\u6e05\\u6670\\uff0c\\u503c\\u5f97\\u63a8\\u8350\\u3002\\u5de5\\u4f5c\\u4eba\\u5458\\u6709\\u8010\\u5fc3\\uff0c\\u8fd8\\u89e3\\u7b54\\u4e86\\u53bb\\u94f6\\u5b50\\u5ca9\\u7684\\u5750\\u8f66\\u65b9\\u5f0f\\uff0c\\u670d\\u52a1\\u5f88\\u68d2\\uff0c\\u4e0b\\u6b21\\u8fd8\\u6765\\u3002\", \"date\": \"2023-07-30\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u5f20\\u8fd0\\u5bfc\\u6e38\\u670d\\u52a1\\u975e\\u5e38\\u5230\\u4f4d\\u6001\\u5ea6\\u97