INSERT INTO `app_travelinfo` VALUES ('878', '红溪景区', '4A景区', '8.3', '58', '桂林', '49', '桂林市临桂区中庸镇怡乐村', '千藤红溪世界一绝，避暑度假的理想去处', 'https://piao.qunar.com/ticket/detail_4087495055.html', '4.9', '49.9', '174', '红溪金瀑景区位于桂林市西郊临桂县中庸乡。景区因多紫砂石，水中阳光下鲜红而得名红溪，更因瀑布岩石为断层蜡黄岩石阳光折射下呈金黄色而得名金瀑！这是世界一绝的千藤红溪。景区中的溪石大多为红色紫砂岩，因而得名。溪红、池秀、瀑美、石奇、树怪、藤古合为红溪六绝。', '[\"https://imgs.qunarzz.com/sight/p0/201301/10/8571fe7b1601571293835fbb.jpg_710x360_5ba671dc.jpg\", \"https://imgs.qunarzz.com/sight/p0/1811/83/83a82e3c14d94492a3.img.jpg_710x360_08936fcb.jpg\", \"https://imgs.qunarzz.com/sight/p0/1811/90/90627a809226265ea3.img.jpg_710x360_893491f2.jpg\", \"https://imgs.qunarzz.com/sight/p0/1811/39/39e2f0e678bb2921a3.img.jpg_710x360_8043f16e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1811/80/807805a595a123b8a3.img.jpg_710x360_e707154c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1811/71/71746599fdfcbdf5a3.img.jpg_710x360_bbe51715.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/201301/10/8571fe7b1601571293835fbb.jpg_280x200_e19d5755.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('879', '桂林在水一汸景区', '4A景区', '10', '33', '桂林', '48', '桂林市临桂区宛田乡宛田村委东宅江村', '四周山环水绕，天然林保存较完整', 'https://piao.qunar.com/ticket/detail_3083422935.html', '0', '20', '41', '桂林在水一汸景区位于桂林市临桂区宛田乡东宅江村大瑶古寨，四周山环水绕，原始植被明显，天然林保存较完整；植被种类繁多，尤其以奇花异草、竹木藤厥为多。植被覆盖率高达90%。距离桂林市五通高铁站18公里、桂林站44公里，距离桂林市政府38公里，距321国道、夏蓉高速公路出口仅3.8公里，离贵广高铁桂林西站46公里，贵广高速铁路（旅游专线）、夏蓉高速公路（经济专线），三个高速公路出口和一个火车站。', '[\"https://imgs.qunarzz.com/sight/p0/1904/f5/f536849cd0b45fffa3.img.png_710x360_f2eb8ebb.png\", \"https://imgs.qunarzz.com/sight/p0/1904/c8/c8f5bb657d2dd111a3.img.png_710x360_8f97a7f2.png\", \"https://imgs.qunarzz.com/sight/p0/1904/3b/3bea6d71cc48874ca3.img.png_710x360_80ab828f.png\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/017065fb87381c02.jpg_710x360_348f0eac.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/eeda934bc08b8c02.jpg_710x360_ecdad4c5.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/fdb32ded44801c02.jpg_710x360_71928a61.jpg\"]', '[{\"author\": \"t*9\", \"content\": \"\\u633a\\u4e0d\\u9519\\u7684 \\u6211\\u662f\\u7b2c\\u4e8c\\u6b21\\u53bb\\u4e86 \\u5916\\u976234\\u5ea6\\u7684\\u5929 \\u91cc\\u9762\\u5f88\\u51c9\\u723d \\u6c81\\u4eba\\u5fc3\\u813e\\u53ef\\u4ee5\\u8fd9\\u6837\\u8bf4 \\u5750\\u89c2\\u5149\\u8f66\\u4e0a\\u53bb\\u5230\\u56ed\\u533a\\u95e8\\u53e3 \\u524d\\u9762\\u8fd8\\u6709\\u4e00\\u6bb5\\u662f600\\u5e74\\u7684\\u53e4\\u5be8 \\u767b\\u9876\\u7528\\u4e86\\u4e24\\u4e2a\\u534a\\u5c0f\\u65f6  \\u56e0\\u4e3a\\u662f\\u8d70\\u8d70\\u505c\\u505c  \\u8ddd\\u79bb\\u6765\\u56de\\u5927\\u6982\\u57286\\u516c\\u91cc\\u5de6\\u53f3 \\u6709\\u4e9b\\u5730\\u65b9\\u6bd4\\u8f83\\u9661 \\u9700\\u8981\\u624b\\u811a\\u5e76\\u7528\\u624d\\u884c\\u3002\\u7011\\u5e03\\u6ca1\\u6709\\u4ec0\\u4e48\\u5927\\u7011\\u5e03 \\u4e0d\\u5982\\u7ea2\\u6eaa\\u5927  \\u4f46\\u662f\\u6bd4\\u8f83\\u5bc6\\u96c6 \\u603b\\u7684\\u6765\\u8bf4\\u8fd8\\u884c \\u53ef\\u4ee5\\u53bb\\u73a9\", \"date\": \"2020-07-23\", \"score\": 5