INSERT INTO `app_travelinfo` VALUES ('903', '九乡风景名胜区', '4A景区', '10', '2757', '昆明', '45', '昆明市宜良县九乡彝族回族乡境内', '电影《神话》拍摄地', 'https://piao.qunar.com/ticket/detail_2825510206.html', '5', '60', '11029', '九乡风景名胜区是以溶洞景观为主体，洞外自然风光、人文景观、民族风情融为一体的综合性风景名胜区，拥有上百座大、小溶洞。九乡风景名胜区共有六大景区，即：现已建成开放的叠虹桥景区和拟将建设的三脚洞景区、大沙坝景区、明月湖景区（即柴石滩水库）、阿路龙景区、马蹄河景区。现已开发的九乡风景区有十大景域：峡谷旅游观光电梯、荫翠峡、惊魂峡、古河穿洞、雄狮厅、仙人洞、雌雄双瀑、林荫寨、蝙蝠洞和旅游索道。雄狮大厅为世界独一无二的地下厅堂，整个大厅面积达15000平方米。雌雄瀑气势恢弘，如黄河倒悬，神田奇伟壮丽，充满田园风味，为世界罕见的一大奇观。难怪许多游客感慨地说：“不游九乡，枉来云南。”', '[\"https://imgs.qunarzz.com/sight/p0/201405/22/9fe3286bde7dd013112e133f09f71967.jpg_710x360_177144aa.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010061200089wzrm506BD.jpg_710x360_6a3ebcab.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105u1200089x2of405EB.jpg_710x360_27aa273f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100l11000000qx8rwA61D.jpg_710x360_bef9eaac.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103o1200089x2igiFF70.jpg_710x360_4166c167.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105r1200089wztxy245E.jpg_710x360_4e9689a8.jpg\"]', '[{\"author\": \"b*7\", \"content\": \"\\u6ca1\\u60f3\\u5230\\u7684\\u662f\\u5c45\\u7136\\u771f\\u7684\\u662f\\u7eaf\\u73a9\\u56e2\\uff0c\\u4e00\\u5f00\\u59cb\\u4e0d\\u60f3\\u62a5\\u56e2\\uff0c\\u6015\\u88ab\\u5751\\uff0c\\u54a8\\u8be2\\u8fc7\\u5ba2\\u670d\\u540e\\u5c31\\u62b1\\u7740\\u8bd5\\u4e00\\u8bd5\\u7684\\u6001\\u5ea6\\u62a5\\u4e86\\u77f3\\u6797\\u4e5d\\u4e61\\u4e00\\u65e5\\u6e38\\uff0c\\u4e3b\\u8981\\u60f3\\u56fe\\u4e2a\\u65b9\\u4fbf\\uff0c\\u4ec0\\u4e48\\u90fd\\u6709\\u4eba\\u7ed9\\u4f60\\u5b89\\u6392\\u597d\\uff0c\\u5c31\\u9002\\u5408\\u6211\\u8fd9\\u79cd\\u53c8\\u60f3\\u51fa\\u53bb\\u73a9\\u53c8\\u61d2\\u5f97\\u8ba1\\u5212\\u7684\\u4eba\\uff0c\\u62a5\\u56e2\\u7b80\\u76f4\\u5c31\\u662f\\u61d2\\u4eba\\u798f\\u97f3\\u554a\\uff0c\\u4ec0\\u4e48\\u90fd\\u7ed9\\u4f60\\u5b89\\u6392\\u59a5\\u5f53\\uff0c\\u8ba9\