INSERT INTO `app_travelinfo` VALUES ('915', '官渡古镇', '4A景区', '10', '2', '昆明', '43', '昆明市东南郊官渡区', '观赏古老寺庙和人文景观', 'https://piao.qunar.com/ticket/detail_1801618475.html', '4.3', '40', '361', '历史悠久的官渡镇位于昆明东南郊，是昆明地区著名的历史文化古镇之一。也是昆明历史文化保护的重要组成部分，镇内分布着较多的古建筑、佛寺、阁楼、庙宇、俗称“六寺、七阁、八庙”，现有国家、省、市、区级重点文物保护单位10余处。', '[\"https://imgs.qunarzz.com/sight/p37/201211/02/f659c849209c65c793835fbb.jpg_710x360_90a079e5.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p37/201211/02/f659c849209c65c793835fbb.jpg_280x200_8a680a56.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('916', '东莞观音山旅游风景区', '4A景区', '10', '478', '东莞', '49', '东莞市樟木头镇笔架山大道观音山国家森林公园', '南天圣地百粤秘境', 'https://piao.qunar.com/ticket/detail_423322352.html', '4', '19.9', '10118', '观音山旅游风景区位于樟木头笔架山1号，是集生态观光、休闲度假和佛道文化为一体的中国首家民营国家森林公园和国家4A级旅游景区。景区曾先后荣获联合国环境规划署、国家有关部委及广东省授予的“国际生态安全旅游示范基地”、“中国十佳休闲旅游景区”、“中国十大爱情主题景区”等荣誉称号。素有“南天圣地，百粤秘境”和“天然氧吧”之美誉。以其独特的自然景观和深厚的人文内涵，已成为人们崇尚绿色生态，追求精神生活的旅游目的地之一。', '[\"https://imgs.qunarzz.com/sight/p0/1905/af/af4f95f579ee43b1a3.water.jpg_710x360_29dcac56.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010471200085vzq1h34F2.jpg_710x360_47d5cd4a.jpg\", \"https://imgs.qunarzz.com/sight/p0/2003/c1/c10de40b7bc5e3f7a3.img.jpg_710x360_0079e9df.jpg\", \"https://imgs.qunarzz.com/sight/p0/2003/2b/2b7b6f5023b884c7a3.img.jpg_710x360_3df2af8a.jpg\", \"https://imgs.qunarzz.com/sight/p0/2003/44/44b186e5e429dfd3a3.img.jpg_710x360_363bee17.jpg\", \"https://imgs.qunarzz.com/sight/p0/2003/ba/ba322d51e35eca48a3.img.jpg_710x360_01a31023.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u518d\\u597d\\u7684\\u6587\\u6848\\uff0c\\u518d\\u9ad8\\u7ea7\\u7684\\u76f8\\u673a\\uff0c\\u90fd\\u4e0d\\u53ca\\u4f60\\u8eab\\u4e34\\u5176\\u5883\\u6765\", \"date\": \"2023-06-23\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u548c\\u670b\\u53cb\\u53bb\\u7684\\uff01\\uff01\\uff01\\u611f\\u89c9\\u662f\\u6691\\u5047\\u6e38\\u73a9\\u7684\\u5fc5\\u53bb\\u9879\\u76ee\\u4e86\\u54c8\\u54c8\\u54c8\\u54c8\\u54c8\\u54c8\\u771f\\u7684\\u521b\\u9020\\u4e86\\u5f88\\u591a\\u7f8e\\u597d\\u7684\\u56de\\u5fc6\\uff01\\uff01\\uff01\\u98ce\\u666f\\u771f\\u7684\\u5f88\\u7f8e\\u5f88\\u7f8e\\uff01\\uff01\\u5dcd\\u5ce8\\u5165\\u4e91\\u7684\\u5c71\\u8109\\u548c\\u989c\\u8272\\u8273\\u4e3d\\u7684\\u68af\\u4e00\\u6574\\u4e2a\\u5927\\u9707\\u64bc\\u4f4f\\uff01\\uff01\\u670b\\u53cb\\u7b2c\\u4e00\\u6b21\\u6765\\u4e1c\\u839e\\u627e\\u6211\\u73a9\\u6211\\u5e26\\u5979\\u53bb\\u7684\\u89c2\\u97f3\\u5c71\\u5b8c\\u5168\\u6ca1\\u95ee\\u9