INSERT INTO `app_travelinfo` VALUES ('940', '金色海岸游艇', '未评价', '10', '4915', '深圳', '49', '深圳市盐田区海鲜食街1号金色海岸码头', '遥望香港诸岛', 'https://piao.qunar.com/ticket/detail_2513432011.html', '5', '80', '2979', '深圳市金色海岸实业有限公司成立于2002年9月，公司以海洋旅游资源为依托，以“海上看盐田”的旅游观光专案为基础。公司将系列地发展海洋旅游及休闲渔业、海洋交通、海洋饮食文化、海洋传媒研究等产业。目前公司主要筹备金色海岸“海上观光游览巴士”和“海上商务会所”项目，前期拟投资人民币五千万元建造各种游艇、游船20余艘。公司基地设在盐田食街水产大厦，在深圳各区设立了对外联络窗口。', '[\"https://imgs.qunarzz.com/sight/p0/1504/25/2567e9dc989e2eb6.water.jpg_710x360_67f814ec.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01016120008hr4j6z5EF3.jpg_710x360_b6099092.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01015120008hr53d5DEEC.jpg_710x360_21a3a997.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0101r120008hr4v4j759E.jpg_710x360_f01fd304.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100y120008i5vmlg8126.jpg_710x360_dc1d2b90.jpg\", \"https://imgs.qunarzz.com/sight/p0/1504/e9/e99a3c1d8ab4f7c5.water.jpg_710x360_ed33427d.jpg\"]', '[{\"author\": \"j*g\", \"content\": \"\\u5f88\\u597d\\u73a9\\u513f\\uff0c\\u5bfc\\u6e38\\u5f88\\u70ed\\u60c5\\uff0c\\u597d\\u8bc4\\uff01\", \"date\": \"2023-06-16\", \"score\": 5}, {\"author\": \"c*8\", \"content\": \"\\u5f88\\u597d\\u7684\\u4f53\\u9a8c\\uff01\\u5bfc\\u6e38\\u5f88\\u8d1f\\u8d23\\uff0c\\u6001\\u5ea6\\u4e5f\\u5f88\\u597d\\uff01\", \"date\": \"2023-07-05\", \"score\": 5}, {\"author\": \"p*7\", \"content\": \"\\u4eca\\u5929\\u771f\\u7684\\u592a\\u5e78\\u8fd0\\u4e86\\uff0c\\u9047\\u5230\\u4e86\\u8fd9\\u4e48\\u597d\\u7684\\u56e2\\u961f\\u3002\\u884c\\u7a0b\\u4e2d\\u5927\\u6885\\u6c99\\u3001\\u4e2d\\u82f1\\u8857\\u3001\\u4e16\\u754c\\u4e4b\\u7a97\\u7b49\\u5404\\u5386\\u53f2\\u8bb2\\u89e3\\u751f\\u52a8\\u3001\\u5185\\u5bb9\\u4e30\\u5bcc \\u4ece\\u53e4\\u81f3\\u4eca\\u7684\\u6f14\\u8bf4\\u8ba9\\u4eba\\u53d7\\u76ca\\u532a\\u6d45\\u3002\\u6bcf\\u4e2a\\u666f\\u70b9\\u90fd\\u6709\\u7ec6\\u81f4\\u7684\\u8bb2\\u89e3\\uff0c\\u8ba9\\u6e38\\u5ba2\\