INSERT INTO `app_travelinfo` VALUES ('949', '云门山水上乐园', '未评价', '8.7', '99', '韶关', '48', '韶关市乳源县云门山风景区（云门寺往前2公里）', '老少皆宜的加勒比风格乐园', 'https://piao.qunar.com/ticket/detail_1265896983.html', '3', '70', '537', '门山水上乐园，占地25000平方米，强烈的加勒比风格使得整个水上乐园充满了浓郁的热带风情，这里远离了夏日的燥热，驱逐了日常的城市喧嚣，有大喇叭、海啸池、彩虹滑梯、造浪池、冲天回旋、大型水寨、儿童戏水池、垂直滑梯等趣味水上项目，集合了惊险刺激、畅快悠闲等多重感官体验，是老少皆宜的新型消暑度假乐园。来云门山水上乐园！', '[\"https://imgs.qunarzz.com/sight/p0/1505/a4/a4b1bc5a3cc3c106.water.jpg_710x360_7fd102fd.jpg\", \"https://imgs.qunarzz.com/sight/p0/1505/16/16611768add92a10.water.jpg_710x360_c4243bfd.jpg\", \"https://imgs.qunarzz.com/sight/p0/1505/4c/4c8ab7527d4bf68d.water.jpg_710x360_997e34bb.jpg\", \"https://imgs.qunarzz.com/sight/p0/1505/75/75494cc8e081b230.water.jpg_710x360_12d85509.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/1637ffc4c600fd02.jpg_710x360_a70375f8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/f21e4fe1660bda02.jpg_710x360_fb888ad0.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u6c34\\u4e0a\\u4e50\\u56ed\\u4e0e\\u6d77\\u6d0b\\u516c\\u56ed\\u5957\\u7968\\uff0c\\u5047\\u7684\\u3002\\u53ea\\u662f\\u4e91\\u95e8\\u5c71\\u95e8\\u7968\\u4e0e\\u6c34\\u4e0a\\u4e50\\u56ed\\u3002\", \"date\": \"2023-07-28\", \"score\": 3}]', 'https://imgs.qunarzz.com/sight/p0/1505/a4/a4b1bc5a3cc3c106.water.jpg_280x200_5ee73f40.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('950', '云门寺', '4A景区', '5', '12', '韶关', '47', '韶关市乳源县县城东北6公里慈雪峰下', '佛教之地，前有青山后有潭', 'https://piao.qunar.com/ticket/detail_632469767.html', '4.7', '9', '128', '云门山大觉禅寺，简称云门寺，位于乳源瑶族自治县县城北6公里的慈云峰下，是我国佛教禅宗五大支派之一云门宗的发祥地。云门寺创建于五代后唐同光元年（公元923年），由云门宗始祖六祖慧能九传弟子文偃禅师所建。公元928年，南汉王刘龚赐额“光泰禅院”，后又改为“证真禅寺”。至宋乾德元年（公元963年），南汉王刘长敕封文偃禅师为“大慈云匡圣弘明大师”，并赐“文铢衣”一件，同时将“证真禅寺”敕升为“大觉禅寺”。因该寺座落在云门山，故称“云门山大觉禅寺”。', '[\"https://imgs.qunarzz.com/sight/p64/201211/08/500d8d0132dae62b93835fbb.jpg_710x360_40a81157.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106g120009cu95y04EB9.jpg_710x360_55077f76.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/35c2d0db739a18.jpg_710x360_1a6ea97d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/b2d71eac829884e03a8ee9f2aa8a688e.jpg_710x360_abfc0601.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/352f0449463c0e4ba7b40981cbb5b107.jpg_710x360_dcf8a27e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/66498ea3c89c1571e94e7a15a3aee358.jpg_710x360_47d60f5d.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p64/201211/08/500d8d0132dae62b93835fbb.jpg_280x200_3349bb8d.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('951', '乐奇欢乐水上世界', '未评价', '9.2', '2', '韶关', '50', '韶关市浈江区大学路坳下农场', '粤北大型的水上游乐园', 'https://piao.qunar.com/ticket/detail_197721576.html', '5.0', '62.9', '65', '韶关乐奇欢乐水世界位于韶关市浈江区大学路坳下农场/大吉庄园，是由香港龙德集团与韶关市大吉庄园共同打造的一家集生态文化旅游、欢乐刺激、温馨好玩的各种游乐项目、商住餐饮一体的粤北大型的水上游乐园。该项目投资1亿人民币，总占地面积100多亩，设计日至多接待量1万人次，引进国际国内先进的游乐设备。', '[\"https://imgs.qunarzz.com/sight/p0/1906/b0/b0aa8eab35a9814fa3.img.jpg_710x360_255892b2.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1906/b0/b0aa8eab35a9814fa3.img.jpg_280x200_99f15462.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('952', '南雄珠玑古巷', '4A景区', '10', '94', '韶关', '48', '韶关市南雄市珠玑镇珠玑村', '古朴风貌犹存的驿道', 'https://piao.qunar.com/ticket/detail_2158093230.html', '3', '38', '1213', '闻名遐迩的珠玑巷，原名敬宗苍巷。唐敬宗时，张昌一家人七代同堂，朝庭闻其孝义，生气勃勃赐与珠玑绦环，为避敬宗庙谥，就将敬宗巷改名为“珠玑巷”。珠玑巷长1500米，宽5米，用鹅卵石铺砌而成，两旁民宅祠堂，店铺栉毗相联，曲直有致，弯凸自然，巷内有古榕、古楼、古塔、古桥、古建筑遗址等文物古迹及观光景点。', '[\"https://imgs.qunarzz.com/sight/p42/201211/05/3d755c260caee9e793835fbb.jpg_710x360_8979754a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/63/637e9711101da6c090.water.jpg_710x360_e1afcb7f.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/81/81fad0c1badf225290.water.jpg_710x360_8b20eab8.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/10/101accd593bd6a7890.water.jpg_710x360_a8af453a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/ea/eaa5843202ed953d90.water.jpg_710x360_5f274bfb.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/33/3393a62e95f929b890.water.jpg_710x360_e3231343.jpg\"]', '[{\"author\": \"a*4\", \"content\": \"\\u5357\\u96c4\\u73e0\\u7391\\u53e4\\u5df7 \\u767e\\u5bb6\\u59d3\\u7960\\u5802 \\u7834\\u65e7\\u7684\\u8001\\u5c4b\\u548c\\u65b0\\u5065\\u7684\\u5b97\\u7960\\u5e76\\u5b58\\u7740 \\u5546\\u4e1a\\u5473\\u6d53 \\u95e8\\u796840/\\u4eba \\u5c31\\u50cf\\u522b\\u4eba\\u8bf4\\u7684\\u4ece\\u6ca1\\u53bb\\u8fc7\\u7684\\u8d70\\u8d70\\u901b\\u901b\\u8fd8\\u662f\\u4e0d\\u9519\\u7684 \\u4f46\\u4e0d\\u4f1a\\u518d\\u53bb\\u7b2c\\u4e8c\\u6b21\\u4e86\", \"date\": \"2021-11-08\", \"score\": 3}]', 'https://imgs.qunarzz.com/sight/p42/201211/05/3d755c260caee9e793835fbb.jpg_280x200_68d9f5c5.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('953', '帽子峰旅游景区', '4A景区', '5', '82', '韶关', '49', '韶关市南雄市', '有“粤北九寨沟”之美誉', 'https://piao.qunar.com/ticket/detail_3639825912.html', '4.9', '7', '1447', '南雄帽子峰森林公园 岭南银杏王国', '[\"https://imgs.qunarzz.com/sight/p0/1809/2c/2ce00cda968fb17a3.img.jpg_710x360_c0dc9a25.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102m120008j2xmdc2386.jpg_710x360_93cd4168.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01009120008jr28o0784D.jpg_710x360_7b31f586.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/b5/b51bc210606d69e3a3.img.jpg_710x360_d94c7264.jpg\", \"https://imgs.qunarzz.com/sight/p0/1709/ed/ed9bc7bb385bfeb0a3.img.jpg_710x360_2415a941.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/19/191dc369487c0185a3.img.jpg_710x360_53088d7a.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1809/2c/2ce00cda968fb17a3.img.jpg_280x200_1553b53c.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('954', '云天海温泉', '4A景区', '4', '4', '韶关', '48', '韶关市新丰县梅坑镇利坑角', '温泉池中品味四季不同景致', 'https://piao.qunar.com/ticket/detail_867705830.html', '4.8', '68', '380', '新丰云天海温泉地处原始森林峡谷中，两山怀抱，地势狭长，占地100多亩，拥有各式温泉泡池，可同时容纳近1000人。优质天然氡温泉无色无嗅，含有丰富的多种矿物质、微量元素，据专家测试，对各种关节炎、皮肤病、痛风、牛皮癣、慢性湿疹、神经性皮炎、过敏性皮炎等有一定疗效。除露天温泉外，游客还可以在温泉区享受沐足和各式按摩服务。新丰云天海现拥有各式客房242间，房间宽敞明亮，可饱览绝美园景和山景。装修设计按照国际五星级标准，豪华大气，尽显尊贵风范。温泉水直入客房，配备中央空调。另有无线上网、卫星电视、国际电话、迷您吧、安全系统等等，让您在度假之余充分感受到舒适、便捷、完善的五星级服务。', '[\"https://imgs.qunarzz.com/sight/p0/1602/30/3069afdb1897583a90.water.jpg_710x360_91cbe7d3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/89/8966f5319c09762490.img.jpg_710x360_156aed61.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/7e/7e33a967193a2f5990.img.jpg_710x360_d72103b2.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/45/45f5c9f247c29e090.img.jpg_710x360_21e38594.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/d564bc031eae2b1c67e41081e6f7f3a5.jpg_710x360_9d2caf13.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/0f4b0e66dcd38364ed2618f52c94ff29.jpg_710x360_8a1e9cd0.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1602/30/3069afdb1897583a90.water.jpg_280x200_57751c5e.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('955', '梅关古道', '4A景区', '9.5', '40', '韶关', '48', '韶关市南雄市珠玑镇梅岭村', '梅花树遍布的古驿道', 'https://piao.qunar.com/ticket/detail_3835372378.html', '4.8', '38', '428', '梅关位于距南雄市约30公里梅岭顶部。梅岭为五岭（大庚岭、骑田岭、萌渚岭、都庞岭、越城岭）之一大庚岭东段，大庚岭横亘广东、江西两省之间，全长200多公里。梅岭海拨只有400多米，但它处于大庚岭之咽喉，地势险要。在秦朝以前，五岭之南属南蛮之地，被古王朝列为疆域之外，那时的岭南车马不通，人烟稀少。', '[\"https://imgs.qunarzz.com/sight/p0/1603/4b/4b9c5fda7b0efd5190.img.jpg_710x360_fb19285d.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104y120006dom4ng8DB6.jpg_710x360_e07d97ce.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104v120006doa2rw089E.jpg_710x360_7dc5e161.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105p120006doch50FC1B.jpg_710x360_50b2d636.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/2c/2cc6613813ab371c90.img.jpg_710x360_4ccaa344.jpg\", \"https://imgs.qunarzz.com/sight/p0/1603/21/213d2af37b06671790.img.jpg_710x360_ea83d251.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1603/4b/4b9c5fda7b0efd5190.img.jpg_280x200_dfb6590c.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('956', '经律论文化旅游小镇', '4A景区', '6.5', '32', '韶关', '49', '韶关市曲江区小坑国家森林公园内', '享受禅意的佛化风光', 'https://piao.qunar.com/ticket/detail_815905885.html', '4.9', '90', '373', '“经律论文化旅游小镇，森林温泉世界”以禅修养生、休闲度假、旅游服务为主营业务，单体设计主要采用东南亚地区建筑风格。景区内集温泉、度假、禅修、养生为一体，充分利用已有的优质地热温泉资源、优美清凉山景，完整诠释自然之美，突出以“禅文化”为特色的核心理念。整个项目分为三期工程进行，一期工程项目投资十七个亿，主要由自在楼、温泉馆、温泉中心、度假酒店、禅宗文化国际交流中心、中青旅国际青年公寓等七大单体建筑，以及祈福广场、云水禅境等组成。', '[\"https://imgs.qunarzz.com/sight/p0/1908/1a/1adae78f2835b35da3.img.jpg_710x360_2444c5a5.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01049120008s7ifslF18E.jpg_710x360_ec47497a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102h120008s7j0l0183C.jpg_710x360_c9ffb791.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106x120008s7irbz7626.jpg_710x360_462a7d81.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103c120008s7j0ktC854.jpg_710x360_49740291.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102z120008s7j2rpB533.jpg_710x360_99ddfc28.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1908/1a/1adae78f2835b35da3.img.jpg_280x200_c6169b30.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('957', '乳源广东大峡谷', '4A景区', '8.4', '134', '韶关', '48', '广东省韶关市乳源瑶族自治县大布镇', '华南少数的地陷峡谷，美誉为“峡谷之母”', 'https://piao.qunar.com/ticket/detail_2821454197.html', '5', '55', '3643', '大峡谷景区一带为平均海拔620米的山间盆地，夏季气候清凉怡人，平均常温22℃，房间不需要空调，是华南地区少有的避暑胜地。据地质学家考证，大峡谷属石英砂岩地貌，形成于距今一亿三千万年前的喜马拉雅造山运动。由于地壳曾几度升沉，地层断裂并经流水冲刷、风化侵蚀等作用，使大峡谷一带呈现出丰富的岩石层理，构成了现在大峡谷雄、奇、险、秀独特的自然景观。', '[\"https://imgs.qunarzz.com/sight/p0/1605/7b/7b270a0b1dad062790.img.jpg_710x360_c87522be.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/81/81d936690ea9928590.water.jpg_710x360_6db2e1a8.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/e3/e37651877dd40d6790.water.jpg_710x360_6ac6fa5e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/7a/7a1984cb89fed0c490.img.jpg_710x360_dee2a5fe.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/d3/d344d0a79b5e8ccd90.img.jpg_710x360_b0ca4f99.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/7a/7a1984cb89fed0c490.img.jpg_710x360_dee2a5fe.jpg\"]', '[{\"author\": \"e*5\", \"content\": \"\\u8fd8\\u4e0d\\u9519\\u5427\\uff0c\\u6765\\u5230\\u6ca1\\u591a\\u4e45\\u5c31\\u4e0b\\u4e86\\u4e00\\u573a\\u534a\\u5c0f\\u65f6\\u7684\\u5927\\u96e8\\uff0c\\u96e8\\u540e\\u7a7a\\u6c14\\u633a\\u6e05\\u65b0\\uff0c\\u8fd9\\u91cc\\u5f88\\u9ad8\\uff0c\\u697c\\u68af\\u50cf\\u662f\\u76f4\\u7684\\uff0c\\u6709\\u70b9\\u6050\\u9ad8\\uff0c\\u53ef\\u4ee5\\u9009\\u62e9\\u8d70\\u5e73\\u8def\\uff0c\\u6574\\u4f53\\u8fd8\\u662f\\u4e0d\\u9519\\u7684\\u3002\", \"date\": \"2023-06-24\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p0/1605/7b/7b270a0b1dad062790.img.jpg_280x200_17cc9eae.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('958', '古佛岩', '4A景区', '5', '46', '韶关', '45', '韶关市乐昌市市南郊5公里处', '因洞内石群形似仙佛而得名', 'https://piao.qunar.com/ticket/detail_1977533435.html', '4.5', '9', '95', '古佛洞天,因洞内的石笋和钟乳石，形状如仙似佛，前人在洞内安放过佛像，在洞口又建有佛寺而得名。它位于乐昌市城西南5公里处。', '[\"https://imgs.qunarzz.com/sight/p25/201211/06/a1b56815d5a178c493835fbb.jpg_710x360_3f94146c.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/fa2fc897c3059cd6731fbc012bfc7094.jpg_710x360_0a9e16fa.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/a16f561e5194366dc0a7c8fe86065085.jpg_710x360_24c8a14f.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/a97e2b2bf21409aeb90a0e88b39b08aa.jpg_710x360_36647af1.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/b70d5251a1f79baa2fe23ea88e01de2b.jpg_710x360_117c2a04.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/509465658ccb6e9f19aa3773bd6b119f.jpg_710x360_ffa0fabe.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p25/201211/06/a1b56815d5a178c493835fbb.jpg_280x200_b8cecaf6.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('959', '丽宫云瑶谷温泉', '4A景区', '5.2', '74', '韶关', '46', '韶关市乳源县候公渡乳城镇青岗', '环境清秀有多种特色温泉池', 'https://piao.qunar.com/ticket/detail_2994647798.html', '4.6', '88', '561', '丽宫云瑶谷温泉是集温泉养生、动感游乐、商务会议、休闲旅游为一体的大型综合性度假胜地。与其他温泉不同的是，这里拥有疗养价值极高的天然冷热两大矿泉，是国内罕见的弱碱性“低矿物化氯化物硫酸盐钙镁型高热偏硅酸锶泉”；云瑶谷温泉区主要分为SPA室内养神区、禅境养心岛、五道养生谷、森林养性坡；并建设有华南地区独一无二的冷热水大型冲浪沙滩项目———山谷夏威夷海滩———疯狂山泉水世界。这里除了温泉、凤凰山森林度假别墅、卧龙岭生态旅游观光区、开心农场、产权别墅等设施项目也在陆续建造。拥有500多间及高雅的元首别墅和多款温泉别墅数十幢。会议中心配备外语同声传译系统,拥有可同时容纳600人会议的大型会议厅及中、小型专用会议室12间；宴会中心有可同时容纳近1000人用餐的宴会大厅及各式风味厅6个、VIP包间15间和烛光湖畔风味宵夜廊；集沐足按摩、麻雀棋牌、健身美容、SPA水疗、豪华夜总会于一体的丽宫国际休闲俱乐部；另设有网吧游戏、乒乓球室、桌球室、网球场、影视厅、展览及书报馆、儿童乐园、自助式农耕乐区等综合配套服务。', '[\"https://imgs.qunarzz.com/sight/p0/201212/22/278e09da7a74a15c93835fbb.png_710x360_07c8612c.png\", \"https://imgs.qunarzz.com/sight/p0/1605/12/12f6963c84e861f390.img.jpg_710x360_805ce7b1.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/5d/5d3d38f4bdb15b0190.img.jpg_710x360_d35160fc.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/82/826689acadbed55190.water.jpg_710x360_ccfc377b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/1b/1bcfbfc2fb5d42b890.water.jpg_710x360_fee388d7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1605/46/466a3abe286344b690.img.jpg_710x360_d0341e59.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/201212/22/278e09da7a74a15c93835fbb.png_280x200_adb8725d.png', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('960', '曹溪温泉假日度假村', '4A景区', '9.3', '6', '韶关', '44', '韶关市曲江区转溪桥（近华南寺）', '泡汤时尽享悦耳铃音', 'https://piao.qunar.com/ticket/detail_3463928373.html', '4.4', '68', '187', '曹溪温泉假日度假村景区，曹溪温泉假日度假村位于韶关市曲江马坝，由京珠高速公路沙溪出口下106国道，经南华寺向北1.5公里即可到达，距广州仅2个半小时的车程。度假村占地50万平方米， 有103个富含氡、偏硅酸的养生温泉池，130多栋豪华别墅，1300多间纯别墅享受的高级客房，7间大型会议室（700人）；毗邻南华寺、马坝遗址、丹霞山等风景名胜， 是度假旅游的圣地。2003年11月底，度假村荣获“广东十佳优质温矿泉””等称号，共有1000多个餐位，2003年下半年全面开业后，接待国外、港澳台人士达到5万人次。度假村的泉水源来自清澈见底的曹溪水，让你可以完全放松心情。传说六祖慧能曾在曹溪沐浴净身，一夜之间顿悟佛理，曹溪水因此而成为千年圣水。园中的禅茶净心沐独具特色，融南华茶艺、佛学智慧、修身养性之道于一体，让您体验另类的旅游方式和度假心情。闻百铃、思慧智、修身心，微风轻佛，发出清脆悦耳的铃声，风铃之声不但能点缀环境，还能安慰心灵，催生雅趣，对人生产生平和的心态。', '[\"https://imgs.qunarzz.com/sight/p0/1603/b4/b47ef95b9815e35b90.water.jpg_710x360_3d8a7e67.jpg\", \"https://imgs.qunarzz.com/sight/p0/201212/20/ef8e2a6f33a9d7da93835fbb.png_710x360_d9d30421.png\", \"https://imgs.qunarzz.com/sight/p0/201212/20/47c39f6fda9328c793835fbb.png_710x360_757b124c.png\", \"https://imgs.qunarzz.com/sight/p0/201212/20/f698e7af0001593193835fbb.png_710x360_b3bc3829.png\", \"https://imgs.qunarzz.com/sight/p0/201212/20/2a5c76b538c270c093835fbb.png_710x360_37d8bca8.png\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1603/b4/b47ef95b9815e35b90.water.jpg_280x200_77374dd5.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('961', '长隆野生动物世界', '未评价', '10', '40176', '广州', '49', '广州市番禺区大石镇105国道大石段593号', '《爸爸去哪儿》拍摄地', 'https://piao.qunar.com/ticket/detail_4281924223.html', '5', '168', '216844', '野生动物世界考拉园：“欢欢”和“乐乐”，还有“四代同堂”的考拉大家族，将给游客带来更多欢乐！长隆大熊猫中心：长隆野生动物世界的心脏位置。在2013年7月31日出生的，广东首只大熊猫宝宝以及2014年7月29，都在这里生活。金蛇秘境：全世界知名的20多个蛇精英种族，全都聚会于此，“金蛇秘境”结合原生态蛇生活环境，为游客创造与蛇近距离接触的条件，体验蛇界的缤纷斑斓和灵气，发现它们亲和、美丽、祥瑞的一面。', '[\"https://imgs.qunarzz.com/sight/p0/2307/63/631afc19a69fe96ca3.water.jpg_710x360_3ffdd728.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01054120008ez0j6z2FBA.jpg_710x360_b96e4678.jpg\", \"https://imgs.qunarzz.com/sight/p0/2307/49/49ce1717bf9551c5a3.water.jpg_710x360_43c9e698.jpg\", \"https://imgs.qunarzz.com/sight/p0/2307/79/79152745bfbe44b9a3.water.jpg_710x360_cd3754e1.jpg\", \"https://imgs.qunarzz.com/sight/p0/2307/58/58204d38d59b3ebba3.water.jpg_710x360_ca2b6718.jpg\", \"https://imgs.qunarzz.com/sight/p0/2307/81/817a61f2a4a312b2a3.water.jpg_710x360_932183ca.jpg\"]', '[{\"author\": \"\\u9646*\\u62c6\", \"content\": \"\\u53bb\\u52a8\\u7269\\u56ed\\u7684\\u670b\\u53cb\\u4e00\\u5b9a\\u8981\\u6ce8\\u610f\\uff01\\uff01\\u5317\\u95e8\\u81ea\\u9a7e\\u8f66\\u6e38\\u89c8\\u7684\\u533a\\u57df\\u548c\\u5c0f\\u706b\\u8f66\\u6e38\\u89c8\\u7684\\u533a\\u57df\\u662f\\u4e00\\u6837\\u7684\\uff01\\u5728\\u65fa\\u5b63\\u65f6\\u6700\\u597d\\u9009\\u62e9\\u79df\\u8f66\\u6216\\u8005\\u81ea\\u9a7e\\u53bb\\u89c2\\u8d4f\\uff0c\\u80fd\\u81ea\\u7531\\u505c\\u9760\\u627e\\u673a\\u4f4d\\uff0c\\u8fd0\\u6c14\\u597d\\u8fd8\\u80fd\\u5f00\\u7a97rua\\u957f\\u9888\\u9e7f\\uff0c\\u66f4\\u91cd\\u8981\\u7684\\u662f\\u81ea\\u9a7e\\u8f66\\u7684\\u9053\\u8def\\u662f\\u9760\\u5185\\u5708\\u7684\\uff01\\uff01\\u8fd9\\u5c31\\u610f\\u5473\\u7740\\u5927\\u90e8\\u5206\\u8def\\u6bb5\\u81ea\\u9a7e\\u8f66\\u90fd\\u79bb\\u52a8\\u7269\\u66f4\\u8fd1\\uff0c\\u5750\\u5c0f\\u706b\\u8f66\\u9996\\u5148\\u662f\\u6f2b\\u957f\\u7684\\u6392\\u961f\\uff0c\\u4e0a\\u706b\\u8f66\\u540e\\u65f6\\u5e3