INSERT INTO `app_travelinfo` VALUES ('976', '古龙峡', '4A景区', '5.1', '3311', '清远', '50', '清远市清新区三坑滩(近旧107国道)', '悬崖对峙、奇峰耸立', 'https://piao.qunar.com/ticket/detail_460675690.html', '5', '35', '2057', '清远古龙峡森林探险王国位于清远市清新区滨江峡谷，距离广州市区68公里，地处广清高速与清连高速（许广高速）及汕湛高速的交汇点。在高差千米的万丈崖群峰的夹峙下，古龙大峡谷集瀑布、深潭、奇石、丛林于一体，悬崖对峙、奇峰耸立、银瀑飞溅，滩多水急，原始雨林遮天蔽日，一年四季清爽宜人。景物特色可用“奇、隐、俏、巧、幽、旷、惊、险、全、古”十个字来概括。峡谷内保存着3000万平方米亚热带丛林，森林覆盖率达到99.1%。负氧离子浓度高达16万/cm³，是清心洗肺的大氧吧。清代诗人袁枚曾以“万山如浪涌，泉鸣溪涌流。浮云脚下踩, 松涛景色殊。”形容清远古龙峡的壮美幽深。', '[\"https://imgs.qunarzz.com/sight/p0/2206/39/39d5a0b7c304e2afa3.water.jpg_710x360_2144788d.jpg\", \"https://imgs.qunarzz.com/sight/p0/2206/a9/a9db64661db7b63ea3.water.jpg_710x360_1d9f5104.jpg\", \"https://imgs.qunarzz.com/sight/p0/2206/9f/9f919a0f06978816a3.water.jpg_710x360_c3c023f8.jpg\", \"https://imgs.qunarzz.com/sight/p0/2206/28/28e0343a21bf47e3a3.water.jpg_710x360_0aeb5c62.jpg\", \"https://imgs.qunarzz.com/sight/p0/2206/b8/b88a4863224811faa3.water.jpg_710x360_a2848097.jpg\"]', '[{\"author\": \"l*2\", \"content\": \"\\u9648\\u5bfc\\u975e\\u5e38\\u98ce\\u8da3\\u5e7d\\u9ed8\\uff0c\\u7ed9\\u6211\\u4eec\\u8be6\\u7ec6\\u8bb2\\u89e3\\u4e86\\u6f02\\u6d41\\u65f6\\u7684\\u4e00\\u4e9b\\u6ce8\\u610f\\u4e8b\\u9879\\u5566\\uff0c\\u8fd8\\u4ecb\\u7ecd\\u4e86\\u6e05\\u8fdc\\u7684\\u4ee3\\u8868\\u7f8e\\u98df\\u6e05\\u8fdc\\u9e21\\uff01\\u6574\\u4e2a\\u884c\\u7a0b\\u4e0b\\u6765\\u771f\\u7684\\u633a\\u8212\\u670d\\u7684~ \\u590f\\u5929\\u6f02\\u6d41\\u771f\\u7684\\u5f88\\u723d\\uff01\\u5c31\\u662f\\u6709\\u70b9\\u8d39\\u624b\\uff0c\\u4e00\\u76f4\\u7528\\u529b\\u6293\\u7740\\u8239\\u8247\\u7684\\u628a\\u624b\\u771f\\u7684\\u5f88\\u8d39\\u529b\\uff0c\\u54c8\\u54c8\\u54c8\\u54c8\\uff0c\\u4f46\\u5f88\\u6709\\u610f\\u601d\\uff01\\u5927\\u5bb6\\u53bb\\u73a9\\u4e00\\u5b9a\\u8981\\u6293\\u4f4f\\u4e86\\uff01\\u53e4\\u9f99\\u5ce1\\u7684\\u7011\\u5e03\\u4e5f\\u8d85\\u7ea7\\u597d\\u770b\\uff01\", \"date\": \"2023-05-29\", \"score\": 5}, {\"author\": \"z*9\", \"content\": \"\\u98ce\\u666f\\u5f88\\u597d\\uff0c\\u8def\\u4e5f\