INSERT INTO `app_travelinfo` VALUES ('978', '黄腾峡生态旅游区', '4A景区', '4.3', '1910', '清远', '50', '清远市清城区黄腾峡生态旅游区', '大型综合生态旅游区', 'https://piao.qunar.com/ticket/detail_3469119854.html', '5.0', '86', '3123', '黄腾峡生态旅游区是国家AAAA级旅游景区，漂流行业龙头企业，位于清远市清城区，距离市中心仅3.8公里。凭借惊险刺激的漂流体验和优越的生态环境，黄腾峡漂流在业内享有“漂流之王”和“小九寨”的美誉，水质一流、风景一流、空气一流，得到各旅游同行及广大游客的一致好评。', '[\"https://imgs.qunarzz.com/sight/p0/1904/34/34475fa1c439776aa3.img.jpg_710x360_e219929c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/ef/ef27fd6c52ed6e62a3.water.jpg_710x360_7c29aa3b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/56/56a66d71fab816f5a3.water.jpg_710x360_1a9a088b.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/3d/3d6e60ba87c0bbd1a3.water.jpg_710x360_dd2c5e5c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/7e/7e1458dfa9239dd1a3.img.jpg_710x360_fa71999e.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/1b/1b59f0969cbc8cbea3.img.jpg_710x360_5c04b2e1.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1904/34/34475fa1c439776aa3.img.jpg_280x200_8fb42272.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('979', '洞天仙境', '4A景区', '8.4', '1190', '清远', '49', '清远市英德市九龙镇', '探秘天坑溶洞奇景', 'https://piao.qunar.com/ticket/detail_274697750.html', '4', '72', '13595', '洞天仙境（又名穿天岩），是一个溪流穿山而过的岩洞，乘船游览，流程约200米，洞内洞厅广阔，钟乳石形态多样，洞顶有2个穿孔通天，光线从中透入洞里。洞内有一片陆地，生长着茂盛的阴生植物，密密麻麻，枝叶婆婆，呈原始状态，像这样的洞内小森林确是一个罕风奇观。看着这里有光、有水、有地、有林，感觉像走入另一个世界，“别有洞天”可能就是这样一意境吧。', '[\"https://imgs.qunarzz.com/sight/p0/1708/f4/f49ac51a028a4d8da3.water.jpg_710x360_9559ecc7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0104m120007ze0yq52084.jpg_710x360_26784276.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/69/69075e8246a6fce2a3.water.jpg_710x360_7ef42ca6.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/f0/f085f6fc56a08da2a3.water.jpg_710x360_413ee267.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/18/18ad0e6dc3a200da3.water.jpg_710x360_d4616719.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/3f/3f96eec0fc3524fca3.water.jpg_710x360_ac1454b1.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u505c\\u8f66\\u6bd4\\u8f83\\u65b9\\u4fbf\\uff0c\\u91cc\\u9762\\u5f88\\u51c9\\u5feb\\uff0c\\u98ce\\u666f\\u4e0d\\u9519\\uff0c\\u5e26\\u5a03\\u4e5f\\u53ef\\u4ee5\\u73a9\\uff0c\\u53f0\\u9636\\u4e0d\\u591a\", \"date\": \"2023-05-03\", \"score\": 4}, {\"author\": \"V*7\", \"content\": \"\\u5751\\u554a \\uff0c\\u4e94\\u767e\\u5757\\u94b1\\u7684\\u623f\\u95f4 \\uff0c\\u5c31\\u8fd9?\\u5509 \\uff0c\\u552f\\u4e00\\u7684\\u4f18\\u70b9\\u5c31\\u662f\\u666f\\u8272\\u4e0d\\u9519\\u3002\\u4e94\\u4e00\\u672c\\u6765\\u5c31\\u5835\\u8f66\\u5f00\\u8f66\\u51cc\\u6668\\u5230\\u7684\\uff0c\\u623f\\u5b50\\u5c0f\\u5f97\\u53ef\\u601c\\u4e0d\\u8bf4\\uff0c\\u536b\\u751f\\u60c5\\u51b5\\u5c31\\u8ddf\\u5c0f\\u65c5\\u5e97\\u4f3c\\u7684\\uff0c\\u4e00\\u8fdb\\u95e8\\u4e00\\u53ea\\u62c7\\u6307\\u5927\\u7684\\u866b\\u5b50\\u8db4\\u5728\\u6795\\u5934\\u4e0a\\uff0c\\u8fd9\\u770b\\u4e86\\u8c01\\u4e0d\\u7cdf\\u5fc3\\u554a\\u3002\\u8fd9\\u9152\\u5e97\\u82b1\\u4e86\\u5927\\u4ef7\\u94b1\\u505a\\u5e7f\\u544a\\uf