INSERT INTO `app_travelinfo` VALUES ('980', '峰林晓镇', '4A景区', '9.8', '738', '清远', '50', '清远市英德市九龙镇九沙路九龙小镇国际生态农业度假区', '线型排列的石灰岩质山峰', 'https://piao.qunar.com/ticket/detail_1559465190.html', '1', '60', '6898', '九龙小镇占地7000多亩，地处于九龙镇英西峰林核心段，内有千多座呈线型排列的石灰岩质山峰，山势雄伟,景色奇特,大小溪涧、河流穿绕其间，千姿百态的山峰、神秘莫测的岩洞、嶙峋怪异的岩石、变化无穷的云海、雾景、浓郁乡土气息的田园风光，千亩花海烂漫盛开，置身其间，风光绮丽、景致醉人，大有蓝天、青山、碧水回归大自然的神韵，一个集:农业公园、乡村酒店、特色民宿、花卉观赏、休闲观光、养生度假、科普教育、户外拓展等为一体的大型综合性生态旅游度假区。', '[\"https://imgs.qunarzz.com/sight/p0/1810/dc/dc267904d9c7bd72a3.img.jpg_710x360_12c752bf.jpg\", \"https://imgs.qunarzz.com/sight/p0/1902/bb/bb1d14b6fbd2f83a3.water.jpg_710x360_6f9a95b6.jpg\", \"https://imgs.qunarzz.com/sight/p0/1902/53/53d1662e9f15a2bda3.water.jpg_710x360_ce701916.jpg\", \"https://imgs.qunarzz.com/sight/p0/1902/46/463721309b0d31a7a3.water.jpg_710x360_0b6e6078.jpg\", \"https://imgs.qunarzz.com/sight/p0/1902/55/55006254ef734c83a3.water.jpg_710x360_75e1cbff.jpg\", \"https://imgs.qunarzz.com/sight/p0/1810/84/842a9275db48cc1ba3.img.jpg_710x360_007c1255.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u88ab4a\\u666f\\u533a\\u9a97\\u4e86\\uff0c57\\u5143\\u4e00\\u4e2a\\u7684\\u95e8\\u7968\\u3002\\u6240\\u8c13\\u7684\\u5c0f\\u6842\\u6797\\uff0c\\u5c31\\u51e0\\u4e2a\\u5c71\\u5cf0\\uff0c\\u91cc\\u9762\\u8fd8\\u6709\\u5f88\\u591a\\u6536\\u8d39\\u9879\\u76ee\\uff0c4\\u8f6e\\u89c2\\u5149\\u8f66100\\u5143\\uff0c\\u5c0f\\u706b\\u8f66\\u6536\\u8d39\\uff0c100\\u7c73\\u957f\\u7684\\u7d22\\u9053\\u6536\\u8d39\\uff0c\\u8377\\u82b1\\u6c60\\u5858\\u5c0f\\u8239\\u6536\\u8d39\\uff0c\\u70ed\\u6c14\\u7403\\u548c\\u6ed1\\u7fd4\\u4f1e\\u6536\\u8d39\\u3002\\u4e0d\\u5efa\\u8bae\\u53bb\\uff0c\\u5c31\\u8ddf\\u57ce\\u5e02\\u91cc\\u7684\\u516c\\u56ed\\u5dee\\u4e0d\\u591a\\uff0c\\u6beb\\u65e0\\u7279\\u8272\\u3002\", \"date\": \"2023-07-17\", \"score\": 1}, {\"author\": \"s*6\", \"content\": \"\\u4e0d\\u9519\\u7684\\u9009\\u62e9\\uff0c\\u5927\\u5bb6\\u73a9\\u5f97\\u5f88\\u5f00\\u5fc3\\u54e6\\uff01\", \"date\": \"2022-10-08\", \"score\": 5}, {\"author\":