INSERT INTO `app_travelinfo` VALUES ('981', '连州龙潭', '4A景区', '8.6', '12', '清远', '48', '清远市连州市九陂镇龙潭管理区连江河段', '不与山水争锋，一切都顺应自然，归于自然', 'https://piao.qunar.com/ticket/detail_795487867.html', '4.8', '43', '230', '以观光为主，同时具有休闲、度假、娱乐、生态旅游、文化传播、自然保护等多种*能，并设立夜游湟川三峡和瑶族篝火晚会项目，甚至是简单而广博的连州博物馆，星级酒店等集多种休闲活动，为游客提供更多休闲娱乐选择的一个旅游度假区。成为粤北乃至广东的一个具有知名品牌的生态旅游区，全面完善基础设施和旅游设施，创造文明、清新、优异、舒适的旅游环境，把生态和旅游融入人们的生活中。使得龙潭度假区与阳朔西街、丽江古城、凤凰古城既有相同的元素又有大不同的元素，形成自己独特的风格。度假区的用地布局、道路形式、建筑群关系等都体现了“隐”的概念，不与山水争锋，一切都顺应自然，归于自然。以突出一个以“生态”为主，以“水”为主的旅游度假区。', '[\"https://imgs.qunarzz.com/sight/p0/1708/e6/e631e5b861bca6e5a3.water.jpg_710x360_57c66b9c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/1c/1c0ab0c7413ad7baa3.water.jpg_710x360_45693e9d.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/3d/3d1f55d12d405ecda3.water.jpg_710x360_443c13cd.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/c9/c91d55bd5977c9aba3.water.jpg_710x360_2d92adf7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/c5/c55d99218aaa4e75a3.water.jpg_710x360_6f0ad814.jpg\", \"https://imgs.qunarzz.com/sight/p0/1708/6c/6c3d503d442bba3fa3.water.jpg_710x360_f513b341.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1708/e6/e631e5b861bca6e5a3.water.jpg_280x200_2c117e08.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('982', '千年瑶寨', '4A景区', '8.3', '514', '清远', '48', '清远市连南县三排镇南岗村', '规模宏大的瑶族古寨', 'https://piao.qunar.com/ticket/detail_1184826927.html', '5', '98', '5998', '千年瑶寨位于广东省清远市连南瑶族自治县境内，瑶族文化历史悠久，薪传不绝，内涵丰富，风情浓郁，瑶族同胞能歌善舞。瑶族“耍歌堂”、“长鼓舞”、“瑶族婚俗”接连被列入国家级非物质文化遗产，独特的瑶族婚俗、排瑶民歌、瑶族刺绣均被列入“广东省非物质文化遗产”名录，千年瑶寨先后被评为“中国历史文化名村”、中国民间艺术之乡“、中国少数民族特色村寨”，同时被列入国务院旅游发展重点保护区域，并于2014年12月被评为国家AAAA级旅游景区。创建国家AAAAA级景区也即将启动。', '[\"https://imgs.qunarzz.com/sight/p0/1901/b9/b98c96818a52fe42a3.img.jpg_710x360_9e67c86f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100510000000pdeq67D6C.jpg_710x360_db84cd81.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100510000000pdeqe22E2.jpg_710x360_90fd2256.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100u10000000pavhf8660.jpg_710x360_c1843da7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100510000000pdepm520C.jpg_710x360_04f15516.jpg\", \"https://imgs.qunarzz.com/sight/p0/1901/f2/f294889d871fdf47a3.img.jpg_710x360_876d0bc9.jpg\"]', '[{\"author\": \"\\u695a*\\u695a\", \"content\": \"\\u7f51\\u8d2d\\u95e8\\u7968103\\uff0c\\u73b0\\u573a\\u4e70\\u7968118\\uff0c\\u540d\\u5bbf\\u6f2b\\u5929\\u8981\\u4ef7\\u30021600\\uff0c2100\\u4e00\\u665a\\u4e0a\\uff0c\\u5176\\u5b9e\\u5c31\\u662f\\u5f88\\u7834\\u65e7\\u5f97\\u5c45\\u6c11\\u623f\\uff0c\\u6302\\u4e86\\u4e9b\\u706f\\u5e26\\uff0c\\uff0c\\u4e2a\\u4eba\\u89c9\\u5f97\\u95e8\\u7968\\u90fd\\u4e0d\\u503c\\u5f97\\uff0c\\u5230\\u5904\\u662f\\u9a6c\\u7caa\\u7a7a\\u6c14\\uff0c\\u8868\\u6f14\\u5f88\\u6577\\u884d\\uff0c\\u4e3b\\u8981\\u5546\\u4e1a\\u5316\\u5356\\u5b57\\u753b\\u3002\", \"date\": \"2023-01-28\", \"score\": 1}, {\"author\": \"\\u695a*\\u695a\", \"content\": \"\\u7f51\\u8d2d\\u95e8\\u7968103\\uff0c\\u73b0\\u573a\\u4e70\\u7968118\\uff0c\\u540d\\u5bbf\\u6f2b\\u5929\\u8981\\u4ef7\\u30021600\\uff0c2100\\u4e00\\u665a\\u4e0a\\uff0c\\u5176\\u5b9e\\u5c31\\u662f\\u5f88\\u7834\\u65e7\\u5f97\\u5c45\\u6c1