INSERT INTO `app_travelinfo` VALUES ('1030', '农科奇观', '4A景区', '5', '2', '珠海', '47', '广东省珠海市香洲区前山梅溪双龙山旅游路2068号（珠海农科奇观中心）', '见证奇形怪状的植物如何诞生', 'https://piao.qunar.com/ticket/detail_1418545125.html', '4', '12', '1475', '农科奇观拥有三十多座现代农业展示温室大棚，引进、繁育和全年种植世界各地名优瓜果、蔬菜、花卉三大类500多个品种结合农业高科技实验室、组织培养室、生物工厂等形成奇特的高科技生态农业景观。园内现已建设好并常年开放的景观有珍奇蔬菜瓜果园、无土栽培蔬菜园、巨型南瓜园、兰花苑、兰花培育中心、沙漠植物园、树型瓜果种植园、水培红薯树区、盆景园、溪流嬉鱼、小动物园等，配套有大型餐厅、学生旅馆、农产品商场等旅游要素设施。园内集科研、生产、旅游、科普教育和商贸于五位一体，“吃、住、行、游、购娱”一条龙服务，成为广大游客和学生享受新奇乐趣、增长见识的绿色“共乐园”。近两年，农科奇观利用自身的科技和旅游方面的优势陆续举办了“南瓜艺术节”， “熊猫盼盼来珠海”、“蔬菜文化节”和“千色花之节”、“首届七彩辣椒节”等大型主题文化活动，成为农科奇观展现高科技含量旅游观光的知名品牌，得到社会各界的高度评价。', '[\"https://imgs.qunarzz.com/sight/p0/1505/8c/8ce27e7e44c9b62d.water.jpg_710x360_72b1c553.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/5f1d565c9268639eff1fcb413c89ec59.jpg_710x360_29fc8df6.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/02711aeb07fe40f0d4157035b7544519.jpg_710x360_3300213e.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/e9d743b7f08c042799c85f85d0c353c9.jpg_710x360_e915e643.jpg\", \"https://imgs.qunarzz.com/sight/p0/201305/29/135534c6cb6466e8c8d65eac.jpg_710x360_f6462b05.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/3c35aef70a6768be2316392b02da178b.jpg_710x360_faf854bc.jpg\"]', '[{\"author\": \"p*9\", \"content\": \"\\u6d77\\u6d0b\\u6e29\\u6cc9\\u6c60\\u603b\\u7c7b\\u5f88\\u591a \\u5c0f\\u670b\\u53cb\\u73a9\\u7684\\u5f88\\u5f00\\u5fc3\\ud83d\\ude03 \\u800c\\u4e14\\u4e5f\\u5f88\\u8212\\u9002 \\u5de5\\u4f5c\\u4eba\\u5458f020 \\u6001\\u5ea6\\u597d\", \"date\": \"2023-05-20\", \"score\": 5}, {\"author\": \"p*6\", \"content\": \"\\u7f51\\u4e0a\\u4e0d\\u652f\\u6301\\uff0c\\u8d2d\\u4e70\\u5b66\\u751f\\u7968\\u5440\\uff0c\\u5e0c\\u671b\\u6539\\u8fdb\\uff01\\u4e0d\\u8fc7\\u519c\\u79d1\\u56ed\\u7684\\u666f\\u8272\\u4e0d\\u9519\\uff0c\\u91cc\\u9762\\u6709\\u8bb8\\u591a\\u70ed\\u5e26\\u690d\\u7269\\uff0c\\u6211\\u5728\\u8fd9\\u91cc\\u7b2c\\u4e00\\u6b21\\u770b\\u5230\\u957f\\u5728\\u6811\\u4e0a\\u7684\\u83e0\\u841d\\u871c\\u548c\\u9999\\u854